INSERT INTO gita VALUES (1, 1.001, 'Arjunavisada-Yoga', 1, 'dhritarastra uvach; धृतराष्ट्र उवाच ', 'dharma-kshetre kuru-kshetre', 'samveta yuyutsavah', 'maamka: pandavaschaiva', 'kim akurvata sanjaya.', NULL, 'dhritarastra uvach (says); dharma-kshetre (in a spiritual place) kuru-kshetre (in the land of Kurus); samvetah (assembled) yuyutsavah (with a desire to wage war); maamka (my sons); pandavahaschaiva (sons of Pandu); kim (what) akurvata (do they do); sanjaya.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (2, 1.002, 'Arjunavisada-Yoga', 2, 'sanjaya uvach; संजय उवाच', 'dristva tu pandavanikang', 'vyudhna duryodhansa-tada', 'acharyam-upasangamya', 'raja vachanam abraveet', NULL, 'sanjaya uvach (says); dristva (on viewing) tu (but) pandavanikang (Pandava warriors) vyudhna (arranged in a military setting) duryodhansa-tada (Duryodhana at that moment) acharyam-upasangamya (got together with the teacher) raja (king) vachanam (words) abravit (spoke).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (3, 1.003, 'Arjunavisada-Yoga', 3, 'pasyaitang pandu-putranam-acharya', 'mahating chamum', 'vyudhang drupada-putrena', 'tava shishyena dheemata', NULL, NULL, 'pasya (behold) etam (this) pandu-putranam (sons of Pandu) acharya (O teacher) mahatim (great) camum (military force) vyudham (arranged) drupada-putrena (by the son of Drupada) tava (your) sisyena (disciple) dheemata (very intelligent).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (4, 1.004, 'Arjunavisada-Yoga', 4, 'atra sura mahesvasa', 'bhimarjuna-sama yudhi', 'yuyudhano viratascha', 'drupadascha maharathah:', NULL, NULL, 'atra (here) surah (heroes) maha-isu-asah (mighty bowmen) bhima-arjuna (Bhima and Arjuna) samah (equal) yudhi (in the fight) yuyudhanah (Yuyudhana) viratah (Virata) ch (also) drupadah (Drupada) ch (also) maha-rathah (great fighter).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (5, 1.005, 'Arjunavisada-Yoga', 5, 'dhristaketus-che-kitanah', 'kasirajas-cha veeryavan', 'purujit-kuntibhojas-cha', 'saibyas-ch nara-pungavah', NULL, NULL, 'dhristaketuh, chekitanah kasirajah (king of Kasi) ch (also) virya-van (very powerful) purujit, kuntibhoj ch (and) saibya ch (and) nara-pungavah (human heroes).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (6, 1.006, 'Arjunavisada-Yoga', 6, 'yudhamanyus-cha vikranta', 'uttamaujas-cha viryavan', 'saubhadro draupadeyas-cha', 'sarva eva maha-rathah', NULL, NULL, 'yudhamanyuh (Yudhamanyu) ch (and) vikrantah (mighty) uttamaujah (Uttamauja) ch (and) virya-van (very powerful) saubhadrah (the son of Subhadra) draupadeyah (the sons of Draupadi) ch (and) sarve (all) eva (certainly) maha-rathah (great chariot fighters).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (7, 1.007, 'Arjunavisada-Yoga', 7, 'asmakam tu visista ye', 'tan-nibodha dvijottama', 'nayaka mum sainyasya', 'samjnartha tan-bravimi te', NULL, NULL, 'asmakam (our) tu (but) visistah (especially powerful) ye (those) tan (them) nibodha (just take note, be informed) dvija-uttama (the best of the brahmanas) nayakah (captains) mum (my) sainyasya (of the soldiers) samjna-artham (for information) tan (them) bravimi (I am speaking) te (your).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (8, 1.008, 'Arjunavisada-Yoga', 8, 'bhavan-bhisma-sch karna-sch', 'kripa-sch samitim-jayah', 'asvatthama vikarna-sch', 'saumadattis-tathaiva ch', NULL, NULL, 'bhavan (your self) bhismah (Grandfather Bhisma) ch (also) karnah (Karna) ch (and) krpah (Krpa) ch (and) samitim-jayah (always victorious in battle) asvatthama (Asvatthama) vikarnah (Vikarna) ch (as well as) saumadattih (the son of Somadatta) tatha (and as) eva (certainly) ch (and).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (9, 1.009, 'Arjunavisada-Yoga', 9, 'anye ch bahavah sura', 'mad-arthe tyakta-jivitah', 'nana-sastra-praharanah', 'sarve yuddha-visaradah', NULL, NULL, 'anye (many others) ch (also) bahavah (in great numbers) surah (heroes) mad-arthe (for my sake) tyakta-jivitah (prepared to risk life) nana (many) sastra (weapons) praharanah (equipped with) sarve (all of them) yuddha (battle) visaradah (experienced in military science).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (10, 1.010, 'Arjunavisada-Yoga', 10, 'aparyaptam tad asmakam', 'balam bhismabhiraksitam', 'paryaptam tv idam etesam', 'balam bhimabhiraksitam', NULL, NULL, 'aparyaptam (immeasurable) tat (that) asmakam (of ours) balam (strength) bhisma (by Grandfather Bhisma) abhiraksitam (perfectly protected) paryaptam (limited) tu (but) idam (all these) etesam (of the Pandavas) balam (strength) bhima (by Bhima) abhiraksitam (carefully protected).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (11, 1.011, 'Arjunavisada-Yoga', 11, 'ayanesu ch sarvesu', 'yatha-bhagam-avasthitah', 'bhismam-evabhiraksantu', 'bhavantah sarva eva hi', NULL, NULL, 'ayanesu (in the strategic points) ch (also) sarvesu (everywhere) yatha-bhagam (as they are differently arranged) avasthitah (situated) bhismam (unto Grandfather Bhisma) eva (certainly) abhiraksantu (support may be given) bhavantah (all of you) sarve (respectively) eva (certainly) hi (and exactly).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (12, 1.012, 'Arjunavisada-Yoga', 12, 'tasya sanjanayan harsam', 'kuru-vrddhah pitamahah', 'simha-nadam vinadyoccaih', 'sankham dadhmau pratapavan', NULL, NULL, 'tasya (his) sanjanayan (increasing) harsam (cheerfulness) kuru-vrddhah (the grandsire of the Kuru dynasty (Bhisma); pitamahah (the grandfather) simha-nadam (roaring sound, like a lion) vinadya (vibrating) uccaih (very loudly) sankham (conchshell) dadhmau (blew) pratapa-van (the valiant).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (13, 1.013, 'Arjunavisada-Yoga', 13, 'tatah sankhas-ch bheryas-ch', 'panavanaka-gomukhah', 'sahasaivabhyahanyanta sa', 'sabdas tumulo bhavat', NULL, NULL, 'tatah (thereafter) sankhah (conchshells) ch (also) bheryah (bugles) ch (and) panava-anaka (trumpets and drums) go-mukhah (horns) sahasa (all of a sudden) eva (certainly) abhyahanyanta (being simultaneously sounded) sah (that) sabdah (combined sound) tumulah (tumultuous) abhavat (became).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (14, 1.014, 'Arjunavisada-Yoga', 14, 'tatah: svetair hayair yukte', 'mahati syandane sthitau', 'madhavah pandavas-chaiva', 'divyau sankhau pradadhmatuh; थिव्यौ शङ्खौ परथध्मतुः', NULL, NULL, 'tatah (thereafter) svetaih (by white) hayaih (horses) yukte (being yoked) mahati (in a great) syandane (chariot) sthitau (so situated) madhavah (Krishna (the husband of the goddess of fortune); pandavah (Arjuna (the son of Pandu); ch (also) eva (certainly) divyau (transcendental) sankhau (conchshells) pradadhmatuh (sounded).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (15, 1.015, 'Arjunavisada-Yoga', 15, 'panchajanyam hrasikesho', 'devadattam dhananjayah', 'paundram dadhmau maha-sankham', 'bhima-karma vrkodarah', NULL, NULL, 'pancajanyam (the conchshell named Pancajanya) hrsika-isah (Hrsikesa (Krishna, the Lord who directs the senses of the devotees); devadattam (the conchshell named Devadatta) dhanam-jayah (Dhananjaya (Arjuna, the winner of wealth); paundram (the conch named Paundra) dadhmau (blew) maha-sankham (the terrific conchshell) bhima-karma (one who performs herculean tasks) vrka-udarah (the voracious eater (Bhima)).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (16, 1.016, 'Arjunavisada-Yoga', 16, 'anantavijayam raja', 'kunti-putro yudhisthirah', 'nakulah sahadevas-ch', 'sughosa-manipuspakau', NULL, NULL, 'ananta-vijayam (the conch named Ananta-vijaya) raja (the king) kunti-putrah (the son of Kunti) yudhisthirah (Yudhisthira) nakulah (Nakula) sahadevah (Sahadeva) ch (and) sughosa-manipuspakau (the conches named Sughosa and Manipuspaka)', NULL, NULL, 'admin');
INSERT INTO gita VALUES (17, 1.017, 'Arjunavisada-Yoga', 17, 'kasyas-ch paramesv-asah', 'sikhandi ch maha-rathah', 'dhrstadyumno viratas-ch', 'satyakischa-parajitah', NULL, NULL, 'kasyah (the King of Kasi (Varanasi); ch (and) parama-isu-asah (the great archer) sikhandi (Sikhandi) ch (also) maha-rathah (one who can fight alone against thousands) dhrstadyumnah (Dhrstadyumna (the son of King Drupada); viratah (Virata (the prince who gave shelter to the Pandavas while they were in disguise); ch (also) satyakih (Satyaki (the same as Yuyudhana, the charioteer of Lord Krishna); ch (and) aparajitah (who were never vanquished before)', NULL, NULL, 'admin');
INSERT INTO gita VALUES (18, 1.018, 'Arjunavisada-Yoga', 18, 'drupado draupadeyasch', 'sarvasah prthivi-pate', 'saubhadras-ch mahaa-bahuh', 'sankhan dadhmuh prthak-prthak', NULL, NULL, 'drupadah (Drupada, the King of Pancala) draupadeyah (the sons of Draupadi) ch (also) sarvasah (all) prthivi-pate (O King) saubhadrah (the son of Subhadra (Abhimanyu); ch (also) maha-bahuh (mighty-armed) sankhan (conchshells) dadhmuh (blew) prthak prthak (each separately).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (19, 1.019, 'Arjunavisada-Yoga', 19, 'sa ghoso dhartarastranam', 'hrdayani vyadarayat', 'nabhascha prthivim chaiva', 'tumulo bhyanunadayan', NULL, NULL, 'sah (that) ghosah (vibration) dhartarastranam (of the sons of Dhrtarastra) hrdayani (hearts) vyadarayat (shattered) nabhah (the sky) ch (also) prthivim (the surface of the earth) ch (also) eva (certainly) tumulah (uproarious) abhyanunadayan (by resounding).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (20, 1.020, 'Arjunavisada-Yoga', 20, 'atha vyavasthitan drstva', 'dhartarastran-kapi-dhvajah', 'pravrtte sastra-sampate', 'dhanur udyamya pandavah', ' ', ' ', 'atha (thereupon) vyavasthitan (situated) drstva (looking upon) dhartarastran (the sons of Dhrtarastra) kapi-dhvajah (one whose flag is marked with Hanuman) pravrtte (while about to engage) sastra-sampate (the arrows released) dhanuh (bow) udyamya (after taking up) pandavah (the son of Pandu - Arjuna); ', NULL, NULL, 'admin');
INSERT INTO gita VALUES (21, 1.021, 'Arjunavisada-Yoga', 21, 'hrsikesam tada', 'vakyam idam aha mahi-pate', 'arjuna uvach; अर्जुन उवाच ', 'senayor-ubhayor-madhye ratham', 'sthapaya mey chyuta', NULL, 'hrsikesam (unto Lord Krishna) tada (at that time) vakyam (words) idam (these) aha (says) mahi-pate (O King). Arjunah (Arjuna) uvach (says) senayoh (of the armies) ubhayoh (of both the parties) madhye (in between them) ratham (the chariot) sthapaya (please keep) me (my) acyuta (O infallible one). ', NULL, NULL, 'admin');
INSERT INTO gita VALUES (22, 1.022, 'Arjunavisada-Yoga', 22, 'yavad etan nirikse ham', 'yoddhu-kaman-avasthitan', 'kair maya saha', 'yoddhavyam-asmin-rana-samudyamey', NULL, NULL, 'yavat (as long as) etan (all these) nirikse (may look) aham (I) yoddhu-kaman (desiring to fight) avasthitan (arrayed on the battlefield). kaih (with whom) maya (by me) saha (with) yoddhavyam (to fight with) asmin (in this) rana (strife) samudyame (in the attempt).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (23, 1.023, 'Arjunavisada-Yoga', 23, 'yotsyamanan avekse ham', 'ya ete tra samagatah', 'dhartarastrasya durbuddher-yuddhe', 'priya-chikeersavah', NULL, NULL, 'yotsyamanan (those who will be fighting) avekse (let me see) aham (I) ye (who) ete (those) atra (here) samagatah (assembled) dhartarastrasya (the son of Dhrtarastra) durbuddheh (evil-minded) yuddhe (in the fight) priya (well) cikirsavah (wishing).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (24, 1.024, 'Arjunavisada-Yoga', 24, 'sanjaya uvach संजय उवाच', 'evam ukto hrsikeso', 'gudakesena bharata', 'senayor-ubhayor-madhye', 'sthapayitva rathottamam', NULL, 'sanjayah (Sanjaya) uvach (says) evam (thus) uktah (addressed) hrsikesah (Lord Krishna) gudakesena (by Arjuna) bharata (O descendant of Bharata) senayoh (of armies) ubhayoh (of both) madhye (in the midst of) sthapayitva (by placing) ratha-uttamam (the finest chariot).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (25, 1.025, 'Arjunavisada-Yoga', 25, 'bhisma-drona-pramukhatah', 'sarvesam ch mahi-ksitam', 'uvach partha', 'pasyaitan-samavetan-kurun-iti', NULL, NULL, 'bhisma (Grandfather Bhisma) drona (the teacher Drona) pramukhatah (in front of) sarvesam (all) ch (also) mahi-ksitam (chiefs of the world) uvach (says) partha (O Partha (son of Prtha); pasya (just behold) etan (all of them) samavetan (assembled) kurun (all the members of the Kuru dynasty) iti (thus).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (26, 1.026, 'Arjunavisada-Yoga', 26, 'tatrapasyat-sthitan-parthah', 'pitrnatha pitamahan', 'acharyan matulan bhratan putran pautran sakhais tatha', '', '', '', 'tatra (there) apasyat (he could see) sthitan (standing) parthah (Arjuna) pitrn (fathers) atha (also) pitamahan (grandfathers) acaryan (teachers) matulan (maternal uncles) bhratrn (brothers) putran (sons) pautran (grandsons) sakhin (friends) tatha (too).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (27, 1.027, 'Arjunavisada-Yoga', 27, 'svasuran-suhardas-chaiva', 'senayor ubhayor api', 'tan samiksya sa kaunteyah', 'sarvan-bandhun-avasthitan', 'krpaya parayavisto', 'visidann idam abravit', 'svasuran (fathers-in-law) suhrdah (well-wishers) ch (also) eva (certainly) senayoh (of the armies) ubhayoh (of both parties) api (including) tan (all of them) samiksya (after seeing) sah (he) kaunteyah (the son of Kunti) sarvan (all kinds of) bandhun (relatives) avasthitan (situated) krpaya (by compassion) paraya (of a high grade) avistah (overwhelmed by) visidan (while lamenting) idam (thus) abravit (spoke).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (28, 1.028, 'Arjunavisada-Yoga', 28, 'arjuna uvach; अर्जुन उवाच ', 'drstvemam-sva-janam-Krishna', 'yuyutsum samvasthitam', 'sidanti mama gatrani', 'mukham ch parisusyati', NULL, 'arjunah (Arjuna) uvach (says) drstva (after seeing) imam (all these) sva-janam (kinsmen) Krishna (O Krishna) yuyutsum (all in fighting spirit) samupasthitam (all present) sidanti (quivering) mama (my) gatrani (limbs of the body) mukham (mouth) ch (also) parisusyati (drying up).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (29, 1.029, 'Arjunavisada-Yoga', 29, 'vepathuscha sareere me', 'rom-harsascha jayate', 'gandivam sramsate', 'hastat-tvak chaiva paridahyate', NULL, NULL, 'vepathuh (trembling of the body) ch (also) sarire (on the body) me (my) roma-harsah (standing of the hair on end) ch (also) jayate (is taking place) gandivam (the bow of Arjuna) sramsate (is slipping) hastat (from the hands) tvak (skin) ch (also) eva (certainly) paridahyate (burning).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (30, 1.030, 'Arjunavisada-Yoga', 30, 'na ch saknomy avasthatum', 'bhramativa ch me manah', 'nimittani ch pasyami', 'viparitani kesava', NULL, NULL, 'na (nor) ch (also) saknomi (am I able) avasthatum (to stay) bhramati (forgetting) iva (as) ch (and) me (my) manah (mind) nimittani (causes) ch (also) pasyami (I foresee) viparitani (just the opposite) kesava (O killer of the demon Kesi (Krishna)).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (31, 1.031, 'Arjunavisada-Yoga', 31, 'na ch sreyo nupasyami', 'hatva sva-janam-ahave', 'na kankse vijayam Krishna ', 'na ch rajyam sukhani ch', NULL, NULL, 'na (nor) ch (also) sreyah (good) anupasyami (do I foresee) hatva (by killing) sva-janam (own kinsmen) ahave (in the fight) na (nor) kankse (do I desire) vijayam (victory) Krishna (O Krishna) na (nor) ch (also) rajyam (kingdom) sukhani (happiness thereof) ch (also).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (32, 1.032, 'Arjunavisada-Yoga', 32, 'kim no rajyena govinda ', 'kim bhogair jivitena va ', 'yesam arthe kanksitam no ', 'rajyam bhogah sukhani ch ', NULL, NULL, 'kim (what use) nah (to us) rajyena (is the kingdom) govinda (O Krishna) kim (what) bhogaih (enjoyment) jivitena (by living) va (either) yesam (for whom) arthe (for the matter of) kanksitam (desired) nah (our) rajyam (kingdom) bhogah (material enjoyment) sukhani (all happiness) ch (also)', NULL, NULL, 'admin');
INSERT INTO gita VALUES (33, 1.033, 'Arjunavisada-Yoga', 33, 'ta ime vasthita yuddhe ', 'pranams tyaktva dhanani ch ', 'acharyah pitarah putras ', 'tathaiva ch pitamahah', NULL, NULL, 'te (all of them) ime (these) avasthitah (situated) yuddhe (in this battlefield) pranan (lives) tyaktva (giving up) dhanani (riches) ch (also) acaryah (teachers) pitarah (fathers) putrah (sons) tatha (as well as) eva (certainly) ch (also) pitamahah (grandfathers) ', NULL, NULL, 'admin');
INSERT INTO gita VALUES (34, 1.034, 'Arjunavisada-Yoga', 34, 'matulah svasurah pautrah ', 'syalah sambandhinas tatha ', 'etan na hantum icchami ', 'ghnato ''pi madhusudana', NULL, NULL, 'matulah (maternal uncles) svasurah (fathers-in-law) pautrah (grandsons) syalah (brothers-in-law) sambandhinah (relatives) tatha (as well as) etan (all these) na (never) hantum (for killing) icchami (do I wish) ghnatah (being killed) api (even) madhusudana (O killer of the demon Madhu (Krishna); ', NULL, NULL, 'admin');
INSERT INTO gita VALUES (35, 1.035, 'Arjunavisada-Yoga', 35, 'api trailokya-rajyasya ', 'hetoh kim nu mahi-krte ', 'nihatya dhartarastran nah ', 'ka pritih syaj janardana ', NULL, NULL, 'api (even if) trai-lokya (of the three worlds) rajyasya (of the kingdoms) hetoh (in exchange) kim (what to speak of) nu (only) mahi-krte (for the sake of the earth) nihatya (by killing) dhartarastran (the sons of Dhrtarastra) nah (our) ka (what) pritih (pleasure) syat (will there be) janardana (O maintainer of all living entities).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (36, 1.036, 'Arjunavisada-Yoga', 36, 'papam evasrayed asman ', 'hatvaitan atatayinah ', 'tasman narha vayam hantum ', 'dhartarastran sa-bandhavan ', 'sva-janam hi katham hatva ', 'sukhinah syama madhava', 'papam (vices) eva (certainly) asrayet (must take upon) asman (us) hatva (by killing) etan (all these) atatayinah (aggressors) tasmat (therefore) na (never) arhah (deserving) vayam (we) hantum (to kill) dhartarastran (the sons of Dhrtarastra) sa-bandhavan (along with friends) sva-janam (kinsmen) hi (certainly) katham (how) hatva (by killing) sukhinah (happy) syama (become) madhava (O Krishna, spouse of goddess of fortune).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (37, 1.037, 'Arjunavisada-Yoga', 37, 'yady apy ete na pasyanti ', 'lobhopahata-cetasah ', 'kula-ksaya-krtam dosam ', 'mitra-drohe ch patakam', NULL, NULL, 'yadi (if) api (certainly) ete (they) na (do not) pasyanti (see) lobha (greed) upahata (overpowered) cetasah (the hearts) kula-ksaya (in killing the family) krtam (done) dosam (fault) mitra-drohe (quarreling with friends) ch (also) patakam (sinful reactions', NULL, NULL, 'admin');
INSERT INTO gita VALUES (38, 1.038, 'Arjunavisada-Yoga', 38, 'katham na jneyam asmabhih ', 'papad asman nivartitum ', 'kula-ksaya-krtam dosam ', 'prapasyadbhir janardana', NULL, NULL, 'katham (why) na (shall not) jneyam (know this) asmabhih (by us) papat (from sins) asmat (ourselves) nivartitum (to cease) kula-ksaya (the destruction of a dynasty) krtam (by so doing) dosam (crime) prapasyadbhih (by those who can see) janardana (O Krishna).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (39, 1.039, 'Arjunavisada-Yoga', 39, 'kula-ksaye pranasyanti ', 'kula-dharmah sanatanah ', 'dharme naste kulam krtsnam ', 'adharmo ''bhibhavaty uta', NULL, NULL, 'kula-ksaye (in destroying the family) pranasyanti (becomes vanquished) kula-dharmah (the family traditions) sanatanah (eternal) dharme (in religion) naste (being destroyed) kulam (family) krtsnam (wholesale) adharmah (irreligious) abhibhavati (transforms) uta (it is says).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (40, 1.040, 'Arjunavisada-Yoga', 40, 'adharmabhibhavat Krishna ', 'pradusyanti kula-striyah ', 'strisu dustasu varsneya  ', 'jayate varna-sankarah ', NULL, NULL, 'adharma (irreligion) abhibhavat (having become predominant) Krishna (O Krishna) pradusyanti (become polluted) kula-striyah (family ladies) strisu (of the womanhood) dustasu (being so polluted) varsneya (O descendant of Vrsni) jayate (it so becomes) varna-sankarah (unwanted progeny).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (41, 1.041, 'Arjunavisada-Yoga', 41, 'sankaro narakayaiva ', 'kula-ghnanam kulasya ch ', 'patanti pitaro hy esam ', 'lupta-pindodaka-kriyah ', NULL, NULL, 'sankarah (such unwanted children) narakaya (for hellish life) eva (certainly) kula-ghnanam (of those who are killers of the family) kulasya (of the family) ch (also) patanti (fall down) pitarah (forefathers) hi (certainly) esam (of them) lupta (stopped) pinda (offerings) udaka (water) kriyah (performances).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (42, 1.042, 'Arjunavisada-Yoga', 42, 'dosair etaih kula-ghnanam ', 'varna-sankara-karakaih ', 'utsadyante jati-dharmah', 'kula-dharmas ch sasvatah', NULL, NULL, 'dosaih (by such faults) etaih (all these) kula-ghnanam (of the destroyer of the family) varna-sankara (unwanted children) karakaih (by the doers) utsadyante (causes devastation) jati-dharmah (community projects) kula-dharmah (family traditions) ch (also) sasvatah (eternal).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (43, 1.043, 'Arjunavisada-Yoga', 43, 'utsanna-kula-dharmanam ', 'manusyanam janardana ', 'narake niyatam vaso ', 'bhavatity anususruma', NULL, NULL, 'utsanna (spoiled) kula-dharmanam (of those who have the family traditions) manusyanam (of such men) janardana (O Krishna) narake (in hell) niyatam (always) vasah (residence) bhavati (it so becomes) iti (thus) anususruma (I have heard by disciplic succession).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (44, 1.044, 'Arjunavisada-Yoga', 44, 'aho bata mahat papam ', 'kartum vyavasita vayam ', 'yad rajya-sukha-lobhena ', 'hantum sva-janam udyatah', NULL, NULL, 'aho (alas) bata (how strange it is) mahat (great) papam (sins) kartum (to perform) vyavasitah (decided) vayam (we) yat (so that) rajya (kingdom) sukha-lobhena (driven by greed for royal happiness) hantum (to kill) sva-janam (kinsmen) udyatah (trying for).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (45, 1.045, 'Arjunavisada-Yoga', 45, 'yadi mam apratikaram ', 'asastram sastra-panayah ', 'dhartarastra rane hanyus ', 'tan me ksemataram bhavet', NULL, NULL, 'yadi (even if) mam (unto me) apratikaram (without being resistant) asastram (without being fully equipped) sastra-panayah (those with weapons in hand) dhartarastrah (the sons of Dhrtarastra) rane (in the battlefield) hanyuh (may kill) tat (that) me (mine) ksema-taram (better) bhavet (become).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (46, 1.046, 'Arjunavisada-Yoga', 46, 'sanjaya uvach संजय उवाच', 'evam uktvarjunah sankhye ', 'rathopastha upavisat ', 'visrjya sa-saram capam ', 'soka-samvigna-manasah', NULL, 'sanjayah (Sanjaya) uvach (says) evam (thus) uktva (saying) arjunah (Arjuna) sankhye (in the battlefield) ratha (chariot) upasthe (situated on) upavisat (sat down again) visrjya (keeping aside) sa-saram (along with arrows) capam (the bow) soka (by lamentation) samvigna (distressed) manasah (within the mind).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (47, 2.001, 'Sankhya-Yoga', 47, 'sanjaya uvach संजय उवाच', 'tam tatha kripayavistam', 'asru-purnakuleksanam', 'visidantam idam vakyam', 'uvach madhusudanah', NULL, 'sanjayah uvach (Sanjaya says) tam (unto Arjuna) tatha (thus) krpaya (by compassion) avistam (overwhelmed) asru-purna (full of tears) akula (depressed) iksanam (eyes) visidantam (lamenting) idam (this) vakyam (words) uvach (says) madhu-sudanah (the killer of Madhu).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (48, 2.002, 'Sankhya-Yoga', 48, 'sri-bhagavan uvach; श्रीभगवानुवाच', 'kutas tva kasmalam idam', 'visame samupasthitam', 'anarya-justam asvargyam', 'akirti-karam arjuna', NULL, 'sri-bhagavan uvach (The Lord says) kutah (wherefrom) tva (unto you) kasmalam (dirtiness) idam (this lamentation) visame (in this hour of crisis) samupasthitam (arrived) anarya (persons who do not know the value of life) justam (practiced by) asvargyam (that which does not lead to higher planets) akirti (infamy) karam (the cause of) arjuna (O Arjuna).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (49, 2.003, 'Sankhya-Yoga', 49, 'klaibyam ma sma gamah partha', 'naitat tvayy upapadyate', 'ksudram hridaya-daurbalyam', 'tyaktvottistha parantapa', '', '', 'klaibyam (impotence) ma (do not) sma (take it) gamah (go in) partha (O son of Prtha) na (never) etat (like this) tvayi (unto you) upadyate (is befitting) ksudram (very little) hrdaya (heart) daurbalyam (weakness) tyaktva (giving up) uttistha (get up) param-tapa (O chastiser of the enemies).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (50, 2.004, 'Sankhya-Yoga', 50, 'arjuna uvach; अर्जुन उवाच', 'katham bhismam aham sankhye', 'dronam ch madhusudana', 'isubhih pratiyotsyami', 'pujarhav ari-sudana', '', 'arjunah uvach (Arjuna says) katham (how) bhismam (unto Bhisma) aham (I) sankhye (in the fight) dronam (unto Drona) ch (also) madhu-sudana (O killer of Madhu) isubhih (with arrows) pratiyotsyami (shall counterattack) puja-arhau (those who are worshipable) ari-sudana (O killer of the enemies).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (51, 2.005, 'Sankhya-Yoga', 51, 'gurun ahatva hi mahanubhavan', 'sreyo bhoktum bhaiksyam apiha loke', 'hatvartha-kamams tu gurun ihaiva', 'bhunjiya bhogan rudhira-pradigdhan', '', '', 'gurun (the superiors) ahatva (not killing) hi (certainly) maha-anubhavan (great souls) sreyah (it is better) bhoktum (to enjoy life) bhaiksyam (begging) api (even) iha (in this life) loke (in this world) hatva (killing) artha (gain) kaman (so desiring) tu (but) gurun (superiors) iha (in this world) eva (certainly) bhunjiya (has to enjoy) bhogan (enjoyable things) rudhira (blood) pradigdhan (tainted with).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (52, 2.006, 'Sankhya-Yoga', 52, 'na caitad vidmah kataran no gariyo', 'yad va jayema yadi va no jayeyuh', 'yan eva hatva na jijivisamas', 'te ’vasthitah pramukhe dhartarastrah', '', '', 'na (nor) ch (also) etat (this) vidmah (do know) katarat (which) nah (us) gariyah (better) yat (what) va (either) jayema (conquer us) yadi (if) va (or) nah (us) jayeyuh (conquer) yan (those) eva (certainly) hatva (by killing) na (never) jijivisamah (want to live) te (all of them) avasthitah (are situated) pramukhe (in the front) dhartarastrah (the sons of Dhrtarastra).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (53, 2.007, 'Sankhya-Yoga', 53, 'karpanya-dosopahata-svabhavah', 'prcchami tvam dharma-sammudha-cetah', 'yac chreyah syan niscitam bruhi tan me', 'shishyas te ’ham sadhi mam tvam prapannam', '', '', 'karpanya (miserly) dosa (weakness) upahata (being inflicted by) sva-bhavah (characteristics) prcchami (I am asking) tvam (unto You) dharma (religion) sammudha (bewildered) cetah (in heart) yat (what) sreyah (all-good) syat (may be) niscitam (confidently) bruhi (tell) tat (that) me (unto me) sisyah (disciple) te (Your) aham (I am) sadhi (just instruct) mam (me) tvam (unto You) prapannam (surrendered).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (54, 2.008, 'Sankhya-Yoga', 54, 'na hi prapasyami mamapanudyad', 'yac chokam ucchosanam indriyanam', 'avapya bhumav asapatnam rddham', 'rajyam suranam api cadhipatyam', '', '', 'na (do not) hi (certainly) prapasyami (I see) mama (my) apanudyat (can drive away) yat (that) sokam (lamentation) ucchosanam (drying up) indriyanam (of the senses) avapya (achieving) bhumau (on the earth) asapatnam (without rival) rddham (prosperous) rajyam (kingdom) suranam (of the demigods) api (even) ch (also) adhipatyam (supremacy).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (55, 2.009, 'Sankhya-Yoga', 55, 'sanjaya uvach) संजय उवाच', 'evam uktva hrishikesham', 'gudakeshah parantapah', 'na yotsya iti govindam', 'uktva tusnim babhuva ha', '', 'sanjayah uvach (Sanjaya says) evam (thus) uktva (speaking) hrsikesam (unto Krishna, the master of the senses) gudakesah (Arjuna, the master at curbing ignorance) parantapah (the chastiser of the enemies) na yotsye (I shall not fight) iti (thus) govindam (unto Krishna, the giver of pleasure) uktva (saying) tusnim (silent) babhuva (became) ha (certainly).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (56, 2.010, 'Sankhya-Yoga', 56, 'tam uvach hrishikeshah', 'prahasann iva bharata', 'senayor ubhayor madhye', 'visidantam idam vacah', '', '', 'tam (unto him) uvach (says) hrsikesah (the master of the senses, Krishna) prahasan (smiling) iva (like that) bharata (O Dhrtarastra, descendant of Bharata) senayoh (of the armies) ubhayoh (of both parties) madhye (between) visidantam (unto the lamenting one) idam (the following) vacah (words).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (57, 2.011, 'Sankhya-Yoga', 57, 'sri-bhagavan uvach; श्रीभगवानुवाच', 'asocyan anvasocas tvam', 'prajna-vadams ch bhasase', 'gatasun agatasums ch', 'nanusocanti panditah', '', 'sri-bhagavan uvach (The Lord says) asocyan (that which is not worthy of lamentation) anvasocah (you are lamenting) tvam (you) prajna-vadan (learned talks) ch (also) bhasase (speaking) gata (lost) asun (life) agata (not past) asun (life) ch (also) na (never) anusocanti (lament) panditah (the learned).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (58, 2.012, 'Sankhya-Yoga', 58, 'na tv evaham jatu nasam', 'na tvam neme janadhipah', 'na caiva na bhavisyamah', 'sarve vayam atah param', '', '', 'na (never) tu (but) eva (certainly) aham (I) jatu (become) na (never) asam (existed) na (it is not so) tvam (yourself) na (not) ime (all these) jana-adhipah (kings) na (never) ch (also) eva (certainly) na (not like that) bhavisyamah (shall exist) sarve (all of us) vayam (we) atah param (hereafter).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (59, 2.013, 'Sankhya-Yoga', 59, 'dehino ’smin yatha dehe', 'kaumaram yauvanam jara', 'tatha dehantara-praptir', 'dhiras tatra na muhyati', '', '', 'dehinah (of the embodied) asmin (in this) yatha (as) dehe (in the body) kaumaram (boyhood) yauvanam (youth) jara (old age) tatha (similarly) deha-antara (transference of the body) praptih (achievement) dhirah (the sober) tatra (thereupon) na (never) muhyati (deluded).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (60, 2.014, 'Sankhya-Yoga', 60, 'matra-sparshas tu kaunteya', 'sitosna-sukha-duhkha-dah', 'agamapayino ’nityas', 'tams titiksasva bharata', '', '', 'matra (sensuous) sparsah (perception) tu (only) kaunteya (O son of Kunti) sita (winter) usna (summer) sukha (happiness) duhkha-dah (giving pain) agama (appearing) apayinah (disappearing) anityah (nonpermanent) tan (all of them) titiksasva (just try to tolerate) bharata (O descendant of the Bharata dynasty).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (61, 2.015, 'Sankhya-Yoga', 61, 'yam hi na vyathayanty ete', 'purusham purusharsabha', 'sama-duhkha-sukham dhiram', 'so ’mrtatvaya kalpate', '', '', 'yam (one whom) hi (certainly) na (never) vyathayanti (are distressing) ete (all these) purusam (to a person) purusa-rsabha (O best among men) sama (unaltered) duhkha (distressed) sukham (happiness) dhiram (patient) sah (he) amrtatvaya (for liberation) kalpate (is considered eligible).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (62, 2.016, 'Sankhya-Yoga', 62, 'nasato vidyate bhavo', 'nabhavo vidyate satah', 'ubhayor api drsto ’ntas', 'tv anayos tattva-darshibhih', '', '', 'na (never) asatah (of the nonexistent) vidyate (there is) bhavah (endurance) na (never) abhavah (changing quality) vidyate (there is) satah (of the eternal) ubhayoh (of the two) api (verily) drstah (observed) antah (conclusion) tu (but) anayoh (of them) tattva (truth) darsibhih (by the seers).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (63, 2.017, 'Sankhya-Yoga', 63, 'avinasi tu tad viddhi', 'yena sarvam idam tatam', 'vinasam avyayasyasya', 'na kascit kartum arhati', '', '', 'avinasi (imperishable) tu (but) tat (that) viddhi (know it) yena (by whom) sarvam (all of the body) idam (this) tatam (pervaded) vinasam (destruction) avyayasya (of the imperishable) asya (of it) na kascit (no one) kartum (to do) arhati (is able).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (64, 2.018, 'Sankhya-Yoga', 64, 'antavanta ime deha', 'nityasyoktah saririnah', 'anasino ’prameyasya', 'tasmad yudhyasva bharata', '', '', 'anta-vantah (perishable) ime (all these) dehah (material bodies) nityasya (eternal in existence) uktah (it is so says) saririnah (the embodied soul) anasinah (never to be destroyed) aprameyasya (immeasurable) tasmat (therefore) yudhyasva (fight) bharata (O descendant of Bharata).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (65, 2.019, 'Sankhya-Yoga', 65, 'ya enam vetti hantaram', 'yas cainam manyate hatam', 'ubhau tau na vijanito', 'nayam hanti na hanyate', '', '', 'yah (anyone) enam (this) vetti (knows) hantaram (the killer) yah (anyone) ch (also) enam (this) manyate (thinks) hatam (killed) ubhau (both of them) tau (they) na (never) vijanitah (in knowledge) na (never) ayam (this) hanti (kills) na (nor) hanyate (be killed).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (66, 2.020, 'Sankhya-Yoga', 66, 'na jayate mriyate va kadacin', 'nayam bhutva bhavita va na bhuyah', 'ajo nityah sasvato ’yam purano', 'na hanyate hanyamane sarire ', '', '', 'na (never) jayate (takes birth) mriyate (never dies) va (either) kadacit (at any time (past, present or future); na (never) ayam (this) bhutva (came into being) bhavita (will come to be) va (or) na (not) bhuyah (or has coming to be) ajah (unborn) nityah (eternal) sasvatah (permanent) ayam (this) puranah (the oldest) na (never) hanyate (is killed) hanyamane (being killed) sarire (by the body).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (67, 2.021, 'Sankhya-Yoga', 67, 'vedavinasinam nityam', 'ya enam ajam avyayam', 'katham sa purushah partha', 'kam ghatayati hanti kam ', '', '', 'veda (in knowledge) avinasinam (indestructible) nityam (always) yah (one who) enam (this (soul); ajam (unborn) avyayam (immutable) katham (how) sah (he) purusah (person) partha (O Partha (Arjuna); kam (whom) ghatayati (hurts) hanti (kills) kam (whom).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (68, 2.022, 'Sankhya-Yoga', 68, 'vasamsi jirnani yatha vihaya', 'navani grhnati naro ’parani', 'tatha sarirani vihaya jirnany', 'anyani samyati navani dehi', '', '', 'vasamsi (garments) jirnani (old and worn out) yatha (as it is) vihaya (giving up) navani (new garments) grhnati (does accept) narah (a man) aparani (other) tatha (in the same way) sarirani (bodies) vihaya (giving up) jirnani (old and useless) anyani (different) samyati (verily accepts) navani (new sets) dehi (the embodied).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (69, 2.023, 'Sankhya-Yoga', 69, 'nainam chindanti shastrani', 'nainam dahati pavakah', 'na cainam kledayanty apo', 'na sosayati marutah', '', '', 'na (never) enam (this soul) chindanti (can cut to pieces) sastrani (all weapons) na (never) enam (unto this soul) dahati (burns) pavakah (fire) na (never) ch (also) enam (unto this soul) kledayanti (moistens) apah (water) na (never) sosayati (dries) marutah (wind).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (70, 2.024, 'Sankhya-Yoga', 70, 'acchedyo ’yam adahyo ’yam', 'akledyo ’sosya eva ch', 'nityah sarva-gatah sthanur', 'acalo ’yam sanatanah', '', '', 'acchedyah (unbreakable) ayam (this soul) adahyah (cannot be burned) ayam (this soul) akledyah (insoluble) asosyah (cannot be dried) eva (certainly) ch (and) nityah (everlasting) sarva-gatah (all-pervading) sthanuh (unchangeable) acalah (immovable) ayam (this soul) sanatanah (eternally the same).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (71, 2.025, 'Sankhya-Yoga', 71, 'avyakto ’yam acintyo ’yam', 'avikaryo ’yam ucyate', 'tasmad evam viditvainam', 'nanusocitum arhasi', '', '', 'avyaktah (invisible) ayam (this soul) acintyah (inconceivable) ayam (this soul) avikaryah (unchangeable) ayam (this soul) ucyate (is says) tasmat (therefore) evam (like this) viditva (knowing it well) enam (this soul) na (do not) anusocitum (may lament over) arhasi (you deserve).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (72, 2.026, 'Sankhya-Yoga', 72, 'atha cainam nitya-jatam', 'nityam va manyase mrtam', 'tathapi tvam maha-baho', 'nainam socitum arhasi', '', '', 'atha (if, however) ch (also) enam (this soul) nitya-jatam (always born) nityam (forever) va (either) manyase (so think) mrtam (dead) tatha api (still) tvam (you) maha-baho (O mighty-armed one) na (never) enam (about the soul) socitum (to lament) arhasi (deserve).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (73, 2.027, 'Sankhya-Yoga', 73, 'jatasya hi dhruvo mrityur', 'dhruvam janma mrtasya ch', 'tasmad apariharye ’rthe', 'na tvam socitum arhasi', '', '', 'jatasya (one who has taken his birth) hi (certainly) dhruvah (a fact) mrtyuh (death) dhruvam (it is also a fact) janma (birth) mrtasya (of the dead) ch (also) tasmat (therefore) apariharye (for that which is unavoidable) arthe (in the matter of) na (do not) tvam (you) socitum (to lament) arhasi (deserve).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (74, 2.028, 'Sankhya-Yoga', 74, 'avyaktadini bhutani', 'vyakta-madhyani bharata', 'avyakta-nidhanany eva', 'tatra ka paridevana', '', '', 'avyakta-adini (in the beginning unmanifested) bhutani (all that are created) vyakta (manifested) madhyani (in the middle) bharata (O descendant of Bharata) avyakta (nonmanifested) nidhanani (all that are vanquished) eva (it is all like that) tatra (therefore) ka (what) paridevana (lamentation).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (75, 2.029, 'Sankhya-Yoga', 75, 'ascharya-vat pasyati kascid enam', 'ascarya-vad vadati tathaiva canyah', 'ascarya-vac cainam anyah srnoti', 'srutvapy enam veda na caiva kascit', '', '', 'ascharya-vat (amazing) pasyati (see) kascit (some) enam (this soul) ascharya-vat (amazing) vadati (speak) tatha (there) eva (certainly) ch (also) anyah (another) ascarya-vat (similarly amazing) ch (also) enam (this soul) anyah (others) srnoti (hear) srutva (having heard) api (even) enam (this soul) veda (do know) na (never) ch (and) eva (certainly) kascit (anyone).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (76, 2.030, 'Sankhya-Yoga', 76, 'dehi nityam avadhyo ’yam', 'dehe sarvasya bharata', 'tasmat sarvani bhutani', 'na tvam socitum arhasi', '', '', 'dehi (the owner of the material body) nityam (eternally) avadhyah (cannot be killed) ayam (this soul) dehe (in the body) sarvasya (of everyone) bharata (O descendant of Bharata) tasmat (therefore) sarvani (all) bhutani (living entities (that are born); na (never) tvam (yourself) socitum (to lament) arhasi (deserve).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (77, 2.031, 'Sankhya-Yoga', 77, 'sva-dharmam api caveksya', 'na vikampitum arhasi', 'dharmyad dhi yuddhac chreyo ’nyat', 'kshatriyasya na vidyate', '', '', 'sva-dharmam (ones own religious principles) api (also) ch (indeed) aveksya (considering) na (never) vikampitum (to hesitate) arhasi (you deserve) dharmyat (from religious principles) hi (indeed) yuddhat (than fighting) sreyah (better engagements) anyat (anything else) ksatriyasya (of the ksatriya) na (does not) vidyate (exist).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (78, 2.032, 'Sankhya-Yoga', 78, 'yadrcchaya copapannam', 'svarga-dvaram apavrtam', 'sukhinah kshatriyah partha', 'labhante yuddham idrsam', '', '', 'yadrcchaya (by its own accord) ch (also) upapannam (arrived at) svarga (heavenly planet) dvaram (door) apavrtam (wide open) sukhinah (very happy) ksatriyah (the members of the royal order) partha (O son of Prtha) labhante (do achieve) yuddham (war) idrsam (like this).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (79, 2.033, 'Sankhya-Yoga', 79, 'atha cet tvam imam dharmyam', 'sangramam na karishyasi', 'tatah sva-dharmam kirtim ch', 'hitva papam avapsyasi', '', '', 'atha (therefore) cet (if) tvam (you) imam (this) dharmyam (religious duty) sangramam (fighting) na (do not) karisyasi (perform) tatah (then) sva-dharmam (your religious duty) kirtim (reputation) ch (also) hitva (losing) papam (sinful reaction) avapsyasi (do gain).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (80, 2.034, 'Sankhya-Yoga', 80, 'akirtim capi bhutani', 'kathayisyanti te ’vyayam', 'sambhavitasya cakirtir', 'maranad atiricyate', '', '', 'akirtim (infamy) ch (also) api (over and above) bhutani (all people) kathayisyanti (will speak) te (of you) avyayam (forever) sambhavitasya (for a respectable man) ch (also) akirtih (ill fame) maranat (than death) atiricyate (becomes more than).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (81, 2.035, 'Sankhya-Yoga', 81, 'bhayad ranad uparatam', 'mamsyante tvam maha-rathah', 'yesham ch tvam bahu-mato', 'bhutva yasyasi laghavam', '', '', 'bhayat (out of fear) ranat (from the battlefield) uparatam (ceased) mamsyante (will consider) tvam (unto you) maha-rathah (the great generals) yesam (of those who) ch (also) tvam (you) bahu-matah (in great estimation) bhutva (will become) yasyasi (will go) laghavam (decreased in value).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (82, 2.036, 'Sankhya-Yoga', 82, 'avacya-vadams ch bahun', 'vadisyanti tavahitah', 'nindantas tava samarthyam', 'tato duhkhataram nu kim', '', '', 'avacya (unkind) vadan (fabricated words) ch (also) bahun (many) vadisyanti (will say) tava (your) ahitah (enemies) nindantah (while vilifying) tava (your) samarthyam (ability) tatah (thereafter) duhkha-taram (more painful) nu (of course) kim (what is there).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (83, 2.037, 'Sankhya-Yoga', 83, 'hato va prapsyasi svargam ', 'jitva va bhoksyase mahim ', 'tasmad uttistha kaunteya ', 'yuddhaya krta-niscayah', '', '', 'hatah (being killed) va (either) prapsyasi (you will gain) svargam (the heavenly kingdom) jitva (by conquering) va (or) bhoksyase (you enjoy) mahim (the world) tasmat (therefore) uttistha (get up) kaunteya (O son of Kunti) yuddhaya (to fight) krta (determined) niscayah (uncertainty).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (84, 2.038, 'Sankhya-Yoga', 84, 'sukha-duhkhe same kritva', 'labhalabhau jayajayau', 'tato yuddhaya yujyasva', 'naivam papam avapsyasi', '', '', 'sukha (happiness) duhkhe (in distress) same (in equanimity) krtva (doing so) labha-alabhau (both in loss and profit) jaya-ajayau (both in defeat and victory) tatah (thereafter) yuddhaya (for the sake of fighting) yujyasva (do fight) na (never) evam (in this way) papam (sinful reaction) avapsyasi (you will gain).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (85, 2.039, 'Sankhya-Yoga', 85, 'esa te ’bhihita sankhye', 'buddhir yoge tv imam shrinu', 'buddhya yukto yaya partha', 'karma-bandham prahasyasi', '', '', 'esa (all these) te (unto you) abhihita (described) sankhye (by analytical study) buddhih (intelligence) yoge (work without fruitive result) tu (but) imam (this) srnu (just hear) buddhya (by intelligence) yuktah (dovetailed) yaya (by which) partha (O son of Prtha) karma-bandham (bondage of reaction) prahasyasi (you can be released from).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (86, 2.040, 'Sankhya-Yoga', 86, 'nehabhikrama-naso ’sti', 'pratyavayo na vidyate', 'sv-alpam apy asya dharmasya', 'trayate mahato bhayat', '', '', 'na (there is not) iha (in this world) abhikrama (endeavoring) nasah (loss) asti (there is) pratyavayah (diminution) na (never) vidyate (there is) svalpam (little) api (although) asya (of this) dharmasya (of this occupation) trayate (releases) mahatah (of very great) bhayat (danger).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (87, 2.041, 'Sankhya-Yoga', 87, 'vyavasayatmika buddhir', 'ekeha kuru-nandana', 'bahu-sakha hy anantas ch', 'buddhayo ’vyavasayinam', '', '', 'vyavasaya-atmika (resolute Krishna consciousness) buddhih (intelligence) eka (only one) iha (in this world) kuru-nandana (O beloved child of the Kurus) bahu-sakhah (various branches) hi (indeed) anantah (unlimited) ch (also) buddhayah (intelligence) avyavasayinam (of those who are not in Krishna consciousness).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (88, 2.042, 'Sankhya-Yoga', 88, 'yam imam puspitam vacam', 'pravadanty avipascitah', 'veda-vada-ratah partha', 'nanyad astiti vadinah', '', '', 'yam imam (all these) puspitam (flowery) vacam (words) pravadanti (say) avipascitah (men with a poor fund of knowledge) veda-vada-ratah (supposed followers of the Vedas) partha (O son of Prtha) na (never) anyat (anything else) asti (there is) iti (thus) vadinah (advocates)', NULL, NULL, 'admin');
INSERT INTO gita VALUES (89, 2.043, 'Sankhya-Yoga', 89, 'kamatmanah svarga-para', 'janma-karma-phala-pradam', 'kriya-visesa-bahulam', 'bhogaisvarya-gatim prati', '', '', 'kama-atmanah (desirous of sense gratification) svarga-parah (aiming to achieve heavenly planets) janma-karma-phala-pradam (resulting in fruitive actions, good birth, etc.) kriya-visesa (pompous ceremonies) bahulam (various) bhoga (sense enjoyment) aisvarya (opulence) gatim (progress) prati (towards).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (90, 2.044, 'Sankhya-Yoga', 90, 'bhogaisvarya-prasaktanam', 'tayapahrta-cetasam', 'vyavasayatmika buddhih', 'samadhau na vidhiyate', '', '', 'bhoga (material enjoyment) aisvarya (opulence) prasaktanam (those who are so attached) taya (by such things) apahrta-cetasam (bewildered in mind) vyavasaya-atmika (fixed determination) buddhih (devotional service of the Lord) samadhau (in the controlled mind) na (never) vidhiyate (does take place).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (91, 2.045, 'Sankhya-Yoga', 91, 'trai-gunya-visaya veda', 'nistrai-gunyo bhavarjuna', 'nirdvandvo nitya-sattva-stho', 'niryoga-ksema atmavan', '', '', 'trai-gunya (pertaining to the three modes of material nature) visayah (on the subject matter) vedah (Vedic literatures) nistraigunyah (in a pure state of spiritual existence) bhava (be) arjuna (O Arjuna) nirdvandvah (free from the pains of opposites) nitya-sattva-sthah (ever remaining in sattva (goodness); niryoga-ksemah (free from (the thought of) acquisition and preservation) atma-van (established in the self).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (92, 2.046, 'Sankhya-Yoga', 92, 'yavan artha udapane', 'sarvatah samplutodake', 'tavan sarveshu vedesu', 'brahmanasya vijanatah', '', '', 'yavan (all that) arthah (is meant) uda-pane (in a well of water) sarvatah (in all respects) sampluta-udake (in a great reservoir of water) tavan (similarly) sarvesu (in all) vedesu (Vedic literatures) brahmanasya (of the man who knows the Supreme Brahman) vijanatah (of one who is in complete knowledge).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (93, 2.047, 'Sankhya-Yoga', 93, 'karmany evadhikaras te', 'ma phalesu kadacana', 'ma karma-phala-hetur bhur', 'ma te sango ’stv akarmani', '', '', 'karmani (prescribed duties) eva (certainly) adhikarah (right) te (of you) ma (never) phalesu (in the fruits) kadacana (at any time) ma (never) karma-phala (in the result of the work) hetuh (cause) bhuh (become) ma (never) te (of you) sangah (attachment) astu (be there) akarmani (in not doing).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (94, 2.048, 'Sankhya-Yoga', 94, 'yoga-sthah kuru karmani', 'sangam tyaktva dhananjaya', 'siddhy-asiddhyoh samo bhutva', 'samatvam yoga ucyate', '', '', 'yoga-sthah (steadfast in yoga) kuru (perform) karmani (your duties) sangam (attachment) tyaktva (having abandoned) dhananjaya (O Dhananjaya) siddhi-asiddhyoh (success and failure) samah (the same) bhutva (having become) samatvam (evenness of mind) yogah (yoga) ucyate (is called).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (95, 2.049, 'Sankhya-Yoga', 95, 'durena hy avaram karma', 'buddhi-yogad dhananjaya', 'buddhau saranam anviccha', 'kripanah phala-hetavah', '', '', 'durena (by discarding it at a long distance) hi (certainly) avaram (abominable) karma (activities) buddhi-yogat (on the strength of Krishna consciousness) dhananjaya (O conqueror of wealth) buddhau (in such consciousness) saranam (full surrender) anviccha (desire) krpanah (the misers) phala-hetavah (those desiring fruitive action).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (96, 2.050, 'Sankhya-Yoga', 96, 'buddhi-yukto jahatiha', 'ubhe sukrita-duskrte', 'tasmad yogaya yujyasva', 'yogah karmasu kausalam', '', '', 'buddhi-yuktah (one who is engaged in devotional service) jahati (can get rid of) iha (in this life) ubhe (in both) sukrta-duskrte (in good and bad results) tasmat (therefore) yogaya (for the sake of devotional service) yujyasva (be so engaged) yogah (Krishna consciousness) karmasu (in all activities) kausalam (art).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (97, 2.051, 'Sankhya-Yoga', 97, 'karma-jam buddhi-yukta hi', 'phalam tyaktva manisinah', 'janma-bandha-vinirmuktah', 'padam gacchanty anamayam', '', '', 'karma-jam (because of fruitive activities) buddhi-yuktah (being done in devotional service) hi (certainly) phalam (results) tyaktva (giving up) manisinah (devotees who are great sages) janma-bandha (the bondage of birth and death) vinirmuktah (liberated soul) padam (position) gacchanti (reached) anamayam (without miseries).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (98, 2.052, 'Sankhya-Yoga', 98, 'yada te moha-kalilam', 'buddhir vyatitarisyati', 'tada gantasi nirvedam', 'srotavyasya srutasya ch', '', '', 'yada (when) te (your) moha (illusory) kalilam (dense forest) buddhih (transcendental service with intelligence) vyatitarisyati (surpasses) tada (at that time) ganta asi (you shall go) nirvedam (callousness) srotavyasya (all that is to be heard) srutasya (all that is already heard) ch (also).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (99, 2.053, 'Sankhya-Yoga', 99, 'shruti-vipratipanna te', 'yada sthasyati niscala', 'samadhav acala buddhis', 'tada yogam avapsyasi', '', '', 'sruti (Vedic revelation) vipratipanna (without being influenced by the fruitive results of the Vedas) te (your) yada (when) sthasyati (remains) niscala (unmoved) samadhau (in transcendental consciousness, or Krishna consciousness) acala (unflinching) buddhih (intelligence) tada (at that time) yogam (self-realization) avapsyasi (you will achieve).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (100, 2.054, 'Sankhya-Yoga', 100, 'arjuna uvach; अर्जुन उवाच', 'sthita-prajnasya ka bhasa', 'samadhi-sthasya keshava', 'sthita-dhih kim prabhaseta', 'kim asita vrajeta kim', '', 'arjunah uvach (Arjuna says) sthita-prajnasya (of one who is situated in fixed Krishna consciousness) ka (what) bhasa (language) samadhi-sthasya (of one situated in trance) kesava (O Krishna) sthita-dhih (one fixed in Krishna consciousness) kim (what) prabhaseta (speak) kim (how) asita (does remain) vrajeta (walks) kim (how).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (101, 2.055, 'Sankhya-Yoga', 101, 'sri-bhagavan uvach; श्रीभगवानुवाच', 'prajahati yada kaman', 'sarvan partha mano-gatan', 'atmany evatmana tustah', 'sthita-prajnas tadocyate', '', 'sri-bhagavan uvach (The Lord says) prajahati (gives up) yada (when) kaman (desires for sense gratification) sarvan (of all varieties) partha (O son of Prtha) manah-gatan (of mental concoction) atmani (in the pure state of the soul) eva (certainly) atmana (by the purified mind) tustah (satisfied) sthita-prajnah (transcendentally situated) tada (at that time) ucyate (is says).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (102, 2.056, 'Sankhya-Yoga', 102, 'duhkhesv anudvigna-manah', 'sukhesu vigata-sprhah', 'vita-raga-bhaya-krodhah', 'sthita-dhir munir ucyate', '', '', 'duhkhesu (in the threefold miseries) anudvigna-manah (without being agitated in mind) sukhesu (in happiness) vigata-sprhah (without being too interested) vita (free from) raga (attachment) bhaya (fear) krodhah (anger) sthita-dhih (one who is steady) munih (a sage) ucyate (is called).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (103, 2.057, 'Sankhya-Yoga', 103, 'yah sarvatranabhisnehas', 'tat tat prapya subhasubham', 'nabhinandati na dvesti', 'tasya prajna pratisthita', '', '', 'yah (one who) sarvatra (everywhere) anabhisnehah (without affection) tat (that) tat (that) prapya (achieving) subha (good) asubham (evil) na (never) abhinandati (praise) na (never) dvesti (envies) tasya (his) prajna (perfect knowledge) pratisthita (fixed).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (104, 2.058, 'Sankhya-Yoga', 104, 'yada samharate cayam', 'kurmo ’nganiva sarvasah', 'indriyanindriyarthebhyas', 'tasya prajna pratisthita', '', '', 'yada (when) samharate (winds up) ch (also) ayam (all these) kurmah (tortoise) angani (limbs) iva (like) sarvasah (altogether) indriyani (senses) indriya-arthebhyah (from the sense objects) tasya (his) prajna (consciousness) pratisthita (fixed up).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (105, 2.059, 'Sankhya-Yoga', 105, 'visaya vinivartante', 'niraharasya dehinah', 'rasa-varjam raso ’py asya', 'param drishtva nivartate', '', '', 'visayah (objects for sense enjoyment) vinivartante (are practiced to be refrained from) niraharasya (by negative restrictions) dehinah (for the embodied) rasa-varjam (giving up the taste) rasah (sense of enjoyment) api (although there is) asya (his) param (far superior things) drstva (by experiencing) nivartate (ceases from).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (106, 2.060, 'Sankhya-Yoga', 106, 'yatato hy api kaunteya', 'purushasya vipascitah', 'indriyani pramathini', 'haranti prasabham manah', '', '', 'yatatah (while endeavoring) hi (certainly) api (in spite of) kaunteya (O son of Kunti) purusasya (of the man) vipascitah (full of discriminating knowledge) indriyani (the senses) pramathini (stimulated) haranti (throws forcefully) prasabham (by force) manah (the mind).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (107, 2.061, 'Sankhya-Yoga', 107, 'tani sarvani samyamya', 'yukta asita mat-parah', 'vase hi yasyendriyani', 'tasya prajna pratisthita', '', '', 'tani (those senses) sarvani (all) samyamya (keeping under control) yuktah (being engaged) asita (being so situated) mat-parah (in relationship with Me) vase (in full subjugation) hi (certainly) yasya (one whose) indriyani (senses) tasya (his) prajna (consciousness) pratisthita (fixed).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (108, 2.062, 'Sankhya-Yoga', 108, 'dhyayato visayan pumsah', 'sangas tesupajayate', 'sangat sanjayate kamah', 'kamat krodho ’bhijayate', '', '', 'dhyayatah (while contemplating) visayan (sense objects) pumsah (of the person) sangah (attachment) tesu (in the sense objects) upajayate (develops) sangat (attachment) sanjayate (develops) kamah (desire) kamat (from desire) krodhah (anger) abhijayate (becomes manifest).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (109, 2.063, 'Sankhya-Yoga', 109, 'krodhad bhavati sammohah', 'sammohat smriti-vibhramah', 'smriti-bhramsad buddhi-naso', 'buddhi-nasat pranasyati', '', '', 'krodhat (from anger) bhavati (takes place) sammohah (perfect illusion) sammohat (from illusion) smrti (of memory) vibhramah (bewilderment) smrti-bhramsat (after bewilderment of memory) buddhi-nasah (loss of intelligence) buddhi-nasat (and from loss of intelligence) pranasyati (falls down).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (110, 2.064, 'Sankhya-Yoga', 110, 'raga-dvesa-vimuktais tu', 'visayan indriyais caran', 'atma-vasyair vidheyatma', 'prasadam adhigacchati', '', '', 'raga (attachment) dvesa (detachment) vimuktaih (by one who has been free from such things) tu (but) visayan (sense objects) indriyaih (by the senses) caran (acting) atma-vasyaih (one who has control over) vidheya-atma (one who follows regulated freedom) prasadam (the mercy of the Lord) adhigacchati (attains).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (111, 2.065, 'Sankhya-Yoga', 111, 'prasade sarva-duhkhanam', 'hanir asyopajayate', 'prasanna-cetaso hy asu', 'buddhih paryavatisthate', '', '', 'prasade (on achievement of the causeless mercy of the Lord) sarva (of all) duhkhanam (material miseries) hanih (destruction) asya (his) upajayate (takes place) prasanna-cetasah (of the happy-minded) hi (certainly) asu (very soon) buddhih (intelligence) pari (sufficiently) avatisthate (established).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (112, 2.066, 'Sankhya-Yoga', 112, 'nasti buddhir ayuktasya', 'na cayuktasya bhavana', 'na cabhavayatah shantir', 'asantasya kutah sukham', '', '', 'na asti (there cannot be) buddhih (transcendental intelligence) ayuktasya (of one who is not connected (with Krishna consciousness); na (neither) ch (and) ayuktasya (of one devoid of Krishna consciousness) bhavana (mind fixed in happiness) na (neither) ch (and) abhavayatah (one who is not fixed) santih (peace) asantasya (of the unpeaceful) kutah (where is) sukham (happiness).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (113, 2.067, 'Sankhya-Yoga', 113, 'indriyanam hi caratam', 'yan mano ’nuvidhiyate', 'tad asya harati prajnam', 'vayur navam ivambhasi', '', '', 'indriyanam (of the senses) hi (certainly) caratam (while herding over) yat (that) manah (mind) anuvidhiyate (becomes constantly engaged) tat (that) asya (his) harati (takes away) prajnam (intelligence) vayuh (wind) navam (a boat) iva (like) ambhasi (on the water).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (114, 2.068, 'Sankhya-Yoga', 114, 'tasmad yasya maha-baho', 'nigrhitani sarvasah', 'indriyanindriyarthebhyas', 'tasya prajna pratisthita', '', '', 'tasmat (therefore) yasya (of ones) maha-baho (O mighty-armed one) nigrhitani (so curbed down) sarvasah (all around) indriyani (the senses) indriya-arthebhyah (for the sake of sense objects) tasya (his) prajna (intelligence) pratisthita (fixed).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (115, 2.069, 'Sankhya-Yoga', 115, 'ya nisa sarva-bhutanam', 'tasyam jagarti samyami', 'yasyam jagrati bhutani', 'sa nisa pasyato muneh', '', '', 'ya (what) nisa (is night) sarva (all) bhutanam (of living entities) tasyam (in that) jagarti (wakeful) samyami (the self-controlled) yasyam (in which) jagrati (awake) bhutani (all beings) sa (that is) nisa (night) pasyatah (for the introspective) muneh (sage).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (116, 2.070, 'Sankhya-Yoga', 116, 'apuryamanam acala-pratishtham', 'samudram apah pravishanti yadvat', 'tadvat kama yam pravishanti sarve', 'sa shantim apnoti na kama-kami', '', '', 'apuryamanam (always filled) acala-pratistham (steadily situated) samudram (the ocean) apah (water) pravisanti (enter) yadvat (as) tadvat (so) kamah (desires) yam (unto one) pravisanti (enter) sarve (all) sah (that person) santim (peace) apnoti (achieves) na (not) kama-kami (one who desires to fulfill desires).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (117, 2.071, 'Sankhya-Yoga', 117, 'vihaya kaman yah sarvan', 'pumams carati nihsprhah', 'nirmamo nirahankarah', 'sa shantim adhigacchati', '', '', 'vihaya (after giving up) kaman (all material desires for sense gratification) yah (the person) sarvan (all) puman (a person) carati (lives) nihsprhah (desireless) nirmamah (without a sense of proprietorship) nirahankarah (without false ego) sah (all) santim (perfect peace) adhigacchati (attains).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (118, 2.072, 'Sankhya-Yoga', 118, 'esa brahmi sthitih partha', 'nainam prapya vimuhyati', 'sthitvasyam anta-kale ’pi', 'brahma-nirvanam rcchati', '', '', 'esa (this) brahmi (spiritual) sthitih (situation) partha (O son of Prtha) na (never) enam (this) prapya (achieving) vimuhyati (bewilders) sthitva (being so situated) asyam (being so) anta-kale (at the end of life) api (also) brahma-nirvanam (spiritual (kingdom of God); rcchati (attains).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (119, 3.001, 'Karma-Yoga', 119, 'arjuna uvach; अर्जुन उवाच', 'jyayasi cet karmanas te', 'mata buddhir janardana', 'tat kim karmani ghore mam', 'niyojayasi keshava', '', 'arjunah (Arjuna) uvach (says) jyayasi (speaking very highly) cet (although) karmanah (than fruitive action) te (Your) mata (opinion) buddhih (intelligence) janardana (O Krishna) tat (therefore) kim (why) karmani (in action) ghore (ghastly) mam (me) niyojayasi (engaging me) kesava (O Krishna).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (120, 3.002, 'Karma-Yoga', 120, 'vyamisreneva vakyena', 'buddhim mohayashiva me', 'tad ekam vada niscitya', 'yena sreyo ’ham apnuyam', '', '', 'vyamisrena (by equivocal) iva (as) vakyena (words) buddhim (intelligence) mohayasi (bewildering) iva (as) me (my) tat (therefore) ekam (only one) vada (please tell) niscitya (ascertaining) yena (by which) sreyah (real benefit) aham (I) apnuyam (may have it).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (121, 3.003, 'Karma-Yoga', 121, 'sri-bhagavan uvach; श्रीभगवानुवाच', 'loke ’smin dvi-vidha nistha', 'pura prokta mayanagha', 'jnana-yogena sankhyanam', 'karma-yogena yoginam', '', 'sri-bhagavan uvach (The Lord says) loke (in the world) asmin (this) dvi-vidha (two kinds of) nistha (faith) pura (formerly) prokta (were says) maya (by Me) anagha (O sinless one) jnana-yogena (by the linking process of knowledge) sankhyanam (of the empiric philosophers) karma-yogena (by the linking process of devotion) yoginam (of the devotees).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (122, 3.004, 'Karma-Yoga', 122, 'na karmanam anarambhan', 'naishkarmyam purusho ’snute', 'na ch sannyasanad eva', 'siddhim samadhigacchati', '', '', 'na (without) karmanam (of the prescribed duties) anarambhat (nonperformance) naiskarmyam (freedom from reaction) purusah (man) asnute (achieve) na (nor) ch (also) sannyasanat (by renunciation) eva (simply) siddhim (success) samadhigacchati (attain).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (123, 3.005, 'Karma-Yoga', 123, 'na hi kascit ksanam api', 'jatu tisthaty akarma-krt', 'karyate hy avasah karma', 'sarvah prakriti-jair gunaih', '', '', 'na (nor) hi (certainly) kascit (anyone) ksanam (even a moment) api (also) jatu (even) tisthati (stands) akarma-krt (without doing something) karyate (forced to work) hi (certainly) avasah (helplessly) karma (work) sarvah (everything) prakrti-jaih (out of the modes of material nature) gunaih (by the qualities).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (124, 3.006, 'Karma-Yoga', 124, 'karmendriyani samyamya', 'ya aste manasa smaran', 'indriyarthan vimudhatma', 'mithyacarah sa ucyate', '', '', 'karma-indriyani (the five working sense organs) samyamya (controlling) yah (anyone who) aste (remains) manasa (by mind) smaran (thinking) indriya-arthan (sense objects) vimudha (foolish) atma (soul) mithya-acarah (pretender) sah (he) ucyate (is called).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (125, 3.007, 'Karma-Yoga', 125, 'yas tv indriyani manasa', 'niyamyarabhate ’rjuna', 'karmendriyaih karma-yogam', 'asaktah sa visisyate', '', '', 'yah (one who) tu (but) indriyani (senses) manasa (by the mind) niyamya (regulating) arabhate (begins) arjuna (O Arjuna) karma-indriyaih (by the active sense organs) karma-yogam (devotion) asaktah (without attachment) sah (he) visisyate (by far the better).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (126, 3.008, 'Karma-Yoga', 126, 'niyatam kuru karma tvam', 'karma jyayo hy akarmanah', 'sarira-yatrapi ch te', 'na prasiddhyed akarmanah', '', '', 'niyatam (prescribed) kuru (do) karma (duties) tvam (you) karma (work) jyayah (better) hi (than) akarmanah (without work) sarira (bodily) yatra (maintenance) api (even) ch (also) te (your) na (never) prasiddhyet (effected) akarmanah (without work).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (127, 3.009, 'Karma-Yoga', 127, 'yajnarthat karmano ’nyatra', 'loko ’yam karma-bandhanah', 'tad-artham karma kaunteya', 'mukta-sangah samacara', '', '', 'yajna-arthat (only for the sake of Yajna, or Visnu) karmanah (work done) anyatra (otherwise) lokah (this world) ayam (this) karma-bandhanah (bondage by work) tat (Him) artham (for the sake) karma (work) kaunteya (O son of Kunti) mukta-sangah (liberated from association) samacara (do perfectly).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (128, 3.010, 'Karma-Yoga', 128, 'saha-yajnah prajah srstva', 'purovaca prajapatih', 'anena prasavisyadhvam', 'esa vo ’stv ista-kama-dhuk', '', '', 'saha (along with) yajnah (sacrifices) prajah (generations) srstva (creating) pura (anciently) uvach (says) praja-patih (the Lord of creatures) anena (by this) prasavisyadhvam (be more and more prosperous) esah (certainly) vah (your) astu (let it be) ista (all desirable) kama-dhuk (bestower).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (129, 3.011, 'Karma-Yoga', 129, 'devan bhavayatanena', 'te deva bhavayantu vah', 'parasparam bhavayantah', 'sreyah param avapsyatha', '', '', 'devan (demigods) bhavayata (having pleased) anena (by this sacrifice) te (those) devah (the demigods) bhavayantu (will please) vah (you) parasparam (mutual) bhavayantah (pleasing one another) sreyah (benediction) param (the supreme) avapsyatha (do you achieve).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (130, 3.012, 'Karma-Yoga', 130, 'istan bhogan hi vo deva', 'dasyante yajna-bhavitah', 'tair dattan apradayaibhyo', 'yo bhunkte stena eva sah', '', '', 'istan (desired) bhogan (necessities of life) hi (certainly) vah (unto you) devah (the demigods) dasyante (award) yajna-bhavitah (being satisfied by the performance of sacrifices) taih (by them) dattan (things given) apradaya (without offering) ebhyah (to the demigods) yah (he who) bhunkte (enjoys) stenah (thief) eva (certainly) sah (is he).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (131, 3.013, 'Karma-Yoga', 131, 'yajna-sistasinah santo', 'mucyante sarva-kilbisaih', 'bhunjate te tv agham papa', 'ye pacanty atma-karanat', '', '', 'yajna-sista (food taken after performance of yajna) asinah (eaters) santah (the devotees) mucyante (get relief from) sarva (all kinds of) kilbisaih (from sins) bhunjate (enjoy) te (they) tu (but) agham (grievous sins) papah (sinners) ye (those) pacanti (prepare food) atma-karanat (for sense enjoyment).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (132, 3.014, 'Karma-Yoga', 132, 'annad bhavanti bhutani', 'parjanyad anna-sambhavah', 'yajnad bhavati parjanyo', 'yajnah karma-samudbhavah', '', '', 'annat (from grains) bhavanti (grow) bhutani (the material bodies) parjanyat (from rains) anna (of food grains) sambhavah (are made possible) yajnat (from the performance of sacrifice) bhavati (becomes possible) parjanyah (rains) yajnah (performance of yajna) karma (prescribed duties) samudbhavah (born of).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (133, 3.015, 'Karma-Yoga', 133, 'karma brahmodbhavam viddhi', 'brahmakshara-samudbhavam', 'tasmat sarva-gatam brahma', 'nityam yajne pratisthitam', '', '', 'karma (work) brahma (Vedas) udbhavam (produced from) viddhi (one should know) brahma (the Vedas) aksara (the Supreme Brahman (Personality of Godhead); samudbhavam (directly manifested) tasmat (therefore) sarva-gatam (all-pervading) brahma (transcendence) nityam (eternally) yajne (in sacrifice) pratisthitam (situated).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (134, 3.016, 'Karma-Yoga', 134, 'evam pravartitam cakram', 'nanuvartayatiha yah', 'aghayur indriyaramo', 'mogham partha sa jivati', '', '', 'evam (thus prescribed) pravartitam (established by the Vedas) cakram (cycle) na (does not) anuvartayati (adopt) iha (in this life) yah (one who) agha-ayuh (life full of sins) indriya-aramah (satisfied in sense gratification) mogham (useless) partha (O son of Prtha (Arjuna); sah (one who does so) jivati (lives).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (135, 3.017, 'Karma-Yoga', 135, 'yas tv atma-ratir eva syad', 'atma-trptas ch manavah', 'atmany eva ch santustas', 'tasya karyam na vidyate', '', '', 'yah (one who) tu (but) atma-ratih (takes pleasure) eva (certainly) syat (remains) atma-trptah (self-illuminated) ch (and) manavah (a man) atmani (in himself) eva (only) ch (and) santustah (perfectly satiated) tasya (his) karyam (duty) na (does not) vidyate (exist).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (136, 3.018, 'Karma-Yoga', 136, 'naiva tasya krtenartho', 'nakrteneha kascana', 'na casya sarva-bhutesu', 'kascid artha-vyapasrayah', '', '', 'na (never) eva (certainly) tasya (his) krtena (by discharge of duty) arthah (purpose) na (nor) akrtena (without discharge of duty) iha (in this world) kascana (whatever) na (never) ch (and) asya (of him) sarva-bhutesu (in all living beings) kascit (any) artha (purpose) vyapasrayah (taking shelter of).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (137, 3.019, 'Karma-Yoga', 137, 'tasmad asaktah satatam', 'karyam karma samacara', 'asakto hy acaran karma', 'param apnoti purushah', '', '', 'tasmat (therefore) asaktah (without attachment) satatam (constantly) karyam (as duty) karma (work) samacara (perform) asaktah (nonattached) hi (certainly) acaran (performing) karma (work) param (the Supreme) apnoti (achieves) purusah (a man).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (138, 3.020, 'Karma-Yoga', 138, 'karmanaiva hi samsiddhim', 'asthita janakadayah', 'loka-sangraham evapi', 'sampasyan kartum arhasi', '', '', 'karmana (by work) eva (even) hi (certainly) samsiddhim (perfection) asthitah (situated) janaka-adayah (Janaka and other kings) loka-sangraham (educating the people in general) eva (also) api (for the sake of) sampasyan (by considering) kartum (to act) arhasi (deserve).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (139, 3.021, 'Karma-Yoga', 139, 'yad yad acarati sresthas', 'tat tad evetaro janah', 'sa yat pramanam kurute', 'lokas tad anuvartate', '', '', 'yat (whatever) yat (and whichever) acarati (does he act) sresthah (a respectable leader) tat (that) tat (and that alone) eva (certainly) itarah (common) janah (person) sah (he) yat (whichever) pramanam (evidence) kurute (does perform) lokah (all the world) tat (that) anuvartate (follow in the footsteps).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (140, 3.022, 'Karma-Yoga', 140, 'na me parthasti kartavyam', 'trisu lokesu kincana', 'nanavaptam avaptavyam', 'varta eva ch karmani', '', '', 'na (not) me (Mine) partha (O son of Prtha) asti (there is) kartavyam (any prescribed duty) trisu (in the three) lokesu (planetary systems) kincana (anything) na (no) anavaptam (in want) avaptavyam (to be gained) varte (engaged) eva (certainly) ch (also) karmani (in ones prescribed duty).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (141, 3.023, 'Karma-Yoga', 141, 'yadi hy aham na varteyam', 'jatu karmany atandritah', 'mama vartmanuvartante', 'manushyah partha sarvasah', '', '', 'yadi (if) hi (certainly) aham (I) na (do not) varteyam (thus engage) jatu (ever) karmani (in the performance of prescribed duties) atandritah (with great care) mama (My) vartma (path) anuvartante (would follow) manusyah (all men) partha (O son of Prtha) sarvasah (in all respects).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (142, 3.024, 'Karma-Yoga', 142, 'utsideyur ime loka', 'na kuryam karma ced aham', 'sankarasya ch karta syam', 'upahanyam imah prajah ', '', '', 'utsideyuh (put into ruin) ime (all these) lokah (worlds) na (do not) kuryam (perform) karma (prescribed duties) cet (if) aham (I) sankarasya (of unwanted population) ch (and) karta (creator) syam (shall be) upahanyam (destroy) imah (all these) prajah (living entities).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (143, 3.025, 'Karma-Yoga', 143, 'saktah karmany avidvamso', 'yatha kurvanti bharata', 'kuryad vidvams tathasaktas', 'cikirsur loka-sangraham', '', '', 'saktah (being attached) karmani (prescribed duties) avidvamsah (the ignorant) yatha (as much as) kurvanti (do it) bharata (O descendant of Bharata) kuryat (must do) vidvan (the learned) tatha (thus) asaktah (without attachment) cikirsuh (desiring to) loka-sangraham (leading the people in general).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (144, 3.026, 'Karma-Yoga', 144, 'na buddhi-bhedam janayed', 'ajnanam karma-sanginam', 'josayet sarva-karmani', 'vidvan yuktah samacaran', '', '', 'na (do not) buddhi-bhedam (disrupt the intelligence) janayet (do) ajnanam (of the foolish) karma-sanginam (attached to fruitive work) josayet (dovetailed) sarva (all) karmani (work) vidvan (learned) yuktah (all engaged) samacaran (practicing).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (145, 3.027, 'Karma-Yoga', 145, 'prakriteh kriyamanani', 'gunaih karmani sarvasah', 'ahankara-vimudhatma', 'kartaham iti manyate', '', '', 'prakrteh (of material nature) kriyamanani (all being done) gunaih (by the modes) karmani (activities) sarvasah (all kinds of) ahankara-vimudha (bewildered by false ego) atma (the spirit soul) karta (doer) aham (I) iti (thus) manyate (thinks).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (146, 3.028, 'Karma-Yoga', 146, 'tattva-vit tu maha-baho', 'guna-karma-vibhagayoh', 'guna gunesu vartanta', 'iti matva na sajjate', '', '', 'tattva-vit (the knower of the Absolute Truth) tu (but) maha-baho (O mighty-armed one) guna-karma (works under material influence) vibhagayoh (differences) gunah (senses) gunesu (in sense gratification) vartante (being engaged) iti (thus) matva (thinking) na (never) sajjate (becomes attached).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (147, 3.029, 'Karma-Yoga', 147, 'prakriter guna-sammudhah', 'sajjante guna-karmasu', 'tan akrtsna-vido mandan', 'krtsna-vin na vicalayet', '', '', 'prakrteh (impelled by the material modes) guna-sammudhah (befooled by material identification) sajjante (become engaged) guna-karmasu (in material activities) tan (all those) akrtsna-vidah (persons with a poor fund of knowledge) mandan (lazy to understand self-realization) krtsna-vit (one who is in factual knowledge) na (may not) vicalayet (try to agitate).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (148, 3.030, 'Karma-Yoga', 148, 'mayi sarvani karmani', 'sannyasyadhyatma-cetasa', 'nirasir nirmamo bhutva', 'yudhyasva vigata-jvarah', '', '', 'mayi (unto Me) sarvani (all sorts of) karmani (activities) sannyasya (giving up completely) adhyatma (with full knowledge of the self) cetasa (consciousness) nirasih (without desire for profit) nirmamah (without ownership) bhutva (so being) yudhyasva (fight) vigata-jvarah (without being lethargic).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (149, 3.031, 'Karma-Yoga', 149, 'ye me matam idam nityam', 'anutishthanti manavah', 'shraddhavanto ’nasuyanto', 'mucyante te ’pi karmabhih', '', '', 'ye (those) me (My) matam (injunctions) idam (this) nityam (eternal function) anutisthanti (execute regularly) manavah (humankind) sraddha-vantah (with faith and devotion) anasuyantah (without envy) mucyante (become free) te (all of them) api (even) karmabhih (from the bondage of the law of fruitive action).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (150, 3.032, 'Karma-Yoga', 150, 'ye tv etad abhyasuyanto', 'nanutishthanti me matam', 'sarva-jnana-vimudhams tan', 'viddhi nastan acetasah', '', '', 'ye (those) tu (however) etat (this) abhyasuyantah (out of envy) na (do not) anutisthanti (regularly perform) me (My) matam (injunction) sarva-jnana (all sorts of knowledge) vimudhan (perfectly befooled) tan (they are) viddhi (know it well) nastan (all ruined) acetasah (without Krishna consciousness).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (151, 3.033, 'Karma-Yoga', 151, 'sadrsam cestate svasyah', 'prakriter jnanavan api', 'prakritim yanti bhutani', 'nigrahah kim karishyati', '', '', 'sadrsam (accordingly) cestate (tries) svasyah (in ones own nature) prakrteh (modes) jnana-van (learned) api (although) prakrtim (nature) yanti (undergo) bhutani (all living entities) nigrahah (suppression) kim (what) karisyati (can do).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (152, 3.034, 'Karma-Yoga', 152, 'indriyasyendriyasyarthe', 'raga-dvesau vyavasthitau', 'tayor na vasam agacchet', 'tau hy asya paripanthinau', '', '', 'indriyasya (of the senses) indriyasya arthe (in the sense objects) raga (attachment) dvesau (also detachment) vyavasthitau (put under regulations) tayoh (of them) na (never) vasam (control) agacchet (one should come) tau (those) hi (certainly) asya (his) paripanthinau (stumbling blocks).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (153, 3.035, 'Karma-Yoga', 153, 'sreyan sva-dharmo vigunah', 'para-dharmat sv-anusthitat', 'sva-dharme nidhanam sreyah', 'para-dharmo bhayavahah', '', '', 'sreyan (far better) sva-dharmah (ones prescribed duties) vigunah (even faulty) para-dharmat (from duties mentioned for others) svanusthitat (than perfectly done) sva-dharme (in ones prescribed duties) nidhanam (destruction) sreyah (better) para-dharmah (duties prescribed for others) bhaya-avahah (dangerous).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (154, 3.036, 'Karma-Yoga', 154, 'arjuna uvach; अर्जुन उवाच', 'atha kena prayukto ’yam', 'papam carati purushah', 'anicchann api varsneya', 'balad iva niyojitah', '', 'arjunah uvach (Arjuna says) atha (hereafter) kena (by what) prayuktah (impelled) ayam (one) papam (sins) carati (acts) purusah (a man) anicchan (without desiring) api (although) varsneya (O descendant of Vrsni) balat (by force) iva (as if) niyojitah (engaged).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (155, 3.037, 'Karma-Yoga', 155, 'sri-bhagavan uvach; श्रीभगवानुवाच', 'kama esa krodha esa', 'rajo-guna-samudbhavah', 'mahasano maha-papma', 'viddhy enam iha vairinam ', '', 'sri-bhagavan uvach (the Personality of Godhead says) kamah (lust) esah (all these) krodhah (wrath) esah (all these) rajah-guna (the mode of passion) samudbhavah (born of) maha-asanah (all-devouring) maha-papma (greatly sinful) viddhi (know) enam (this) iha (in the material world) vairinam (greatest enemy).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (156, 3.038, 'Karma-Yoga', 156, 'dhumenavriyate vahnir', 'yathadarso malena ch', 'yatholbenavrto garbhas', 'tatha tenedam avrtam ', '', '', 'dhumena (by smoke) avriyate (covered) vahnih (fire) yatha (just as) adarsah (mirror) malena (by dust) ch (also) yatha (just as) ulbena (by the womb) avrtah (is covered) garbhah (embryo) tatha (so) tena (by that lust) idam (this) avrtam (is covered).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (157, 3.039, 'Karma-Yoga', 157, 'avrtam jnanam etena', 'jnanino nitya-vairina', 'kama-rupena kaunteya', 'duspurenanalena ch', '', '', 'avrtam (covered) jnanam (pure consciousness) etena (by this) jnaninah (of the knower) nitya-vairina (eternal enemy) kama-rupena (in the form of lust) kaunteya (O son of Kunti) duspurena (never to be satisfied) analena (by the fire) ch (also).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (158, 3.040, 'Karma-Yoga', 158, 'indriyani mano buddhir', 'asyadhisthanam ucyate', 'etair vimohayaty esa', 'jnanam avrtya dehinam', '', '', 'indriyani (the senses) manah (the mind) buddhih (the intelligence) asya (of the lust) adhisthanam (sitting place) ucyate (called) etaih (by all these) vimohayati (bewilders) esah (of this) jnanam (knowledge) avrtya (covering) dehinam (the embodied).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (159, 3.041, 'Karma-Yoga', 159, 'tasmat tvam indriyany adau', 'niyamya bharatarsabha', 'papmanam prajahi hy enam', 'jnana-vijnana-nasanam', '', '', 'tasmat (therefore) tvam (you) indriyani (senses) adau (in the beginning) niyamya (by regulating) bharata-rsabha (O chief amongst the descendants of Bharata) papmanam (the great symbol of sin) prajahi (curb) hi (certainly) enam (this) jnana (knowledge) vijnana (scientific knowledge of the pure soul) nasanam (the destroyer).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (160, 3.042, 'Karma-Yoga', 160, 'indriyani parany ahur', 'indriyebhyah param manah', 'manasas tu para buddhir', 'yo buddheh paratas tu sah', '', '', 'indriyani (senses) parani (superior) ahuh (is says) indriyebhyah (more than the senses) param (superior) manah (the mind) manasah (more than the mind) tu (also) para (superior) buddhih (intelligence) yah (one who) buddheh (more than the intelligence) paratah (superior) tu (but) sah (he).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (161, 3.043, 'Karma-Yoga', 161, 'evam buddheh param buddhva', 'samstabhyatmanam atmana', 'jahi satrum maha-baho', 'kama-rupam durasadam', '', '', 'evam (thus) buddheh (of intelligence) param (superior) buddhva (so knowing) samstabhya (by steadying) atmanam (of the mind) atmana (by deliberate intelligence) jahi (conquer) satrum (the enemy) maha-baho (O mighty-armed one) kama-rupam (the form of lust) durasadam (formidable).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (162, 4.001, 'Gyan Karma Samnyasa Yoga', 162, 'sri-bhagavan uvach; श्रीभगवानुवाच', 'imam vivasvate yogam', 'proktavan aham avyayam', 'vivasvan manave praha', 'manur iksvakave ’bravit', '', 'sri-bhagavan uvach (The Lord says) imam (this) vivasvate (unto the sun-god) yogam (the science of ones relationship to the Supreme) proktavan (instructed) aham (I) avyayam (imperishable) vivasvan (Vivasvan (the sun-gods name); manave (unto the father of mankind (of the name Vaivasvata); praha (told) manuh (the father of mankind) iksvakave (unto King Iksvaku) abravit (says).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (163, 4.002, 'Gyan Karma Samnyasa Yoga', 163, 'evam parampara-praptam', 'imam rajarsayo viduh', 'sa kaleneha mahata', 'yogo nastah parantapa', '', '', 'evam (thus) parampara (disciplic succession) praptam (received) imam (this science) raja-rsayah (the saintly kings) viduh (understood) sah (that knowledge) kalena (in the course of time) iha (in this world) mahata (by great) yogah (the science of ones relationship with the Supreme) nastah (scattered) parantapa (O Arjuna, subduer of the enemies).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (164, 4.003, 'Gyan Karma Samnyasa Yoga', 164, 'sa evayam maya te ’dya', 'yogah proktah puratanah', 'bhakto ’si me sakha ceti', 'rahasyam hy etad uttamam', '', '', 'sah (the same ancient) eva (certainly) ayam (this) maya (by Me) te (unto you) adya (today) yogah (the science of yoga) proktah (spoken) puratanah (very old) bhaktah (devotee) asi (you are) me (My) sakha (friend) ch (also) iti (therefore) rahasyam (mystery) hi (certainly) etat (this) uttamam (transcendental).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (165, 4.004, 'Gyan Karma Samnyasa Yoga', 165, 'arjuna uvach; अर्जुन उवाच', 'aparam bhavato janma', 'param janma vivasvatah', 'katham etad vijaniyam', 'tvam adau proktavan iti', '', 'arjunah uvach (Arjuna says) aparam (junior) bhavatah (Your) janma (birth) param (superior) janma (birth) vivasvatah (of the sun-god) katham (how) etat (this) vijaniyam (shall I understand) tvam (You) adau (in the beginning) proktavan (instructed) iti (thus).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (166, 4.005, 'Gyan Karma Samnyasa Yoga', 166, 'sri-bhagavan uvach; श्रीभगवानुवाच', 'bahuni me vyatitani', 'janmani tava carjuna', 'tany aham veda sarvani', 'na tvam vettha parantapa', '', 'sri-bhagavan uvach (the Personality of Godhead says) bahuni (many) me (of Mine) vyatitani (have passed) janmani (births) tava (of yours) ch (and also) arjuna (O Arjuna) tani (all those) aham (I) veda (do know) sarvani (all) na (not) tvam (yourself) vettha (know) parantapa (O subduer of the enemy).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (167, 4.006, 'Gyan Karma Samnyasa Yoga', 167, 'ajo ’pi sann avyayatma', 'bhutanam isvaro ’pi san', 'prakritim svam adhisthaya', 'sambhavamy atma-mayaya', '', '', 'ajah (unborn) api (although) san (being so) avyaya (without deterioration) atma (body) bhutanam (all those who are born) isvarah (the Supreme Lord) api (although) san (being so) prakrtim (transcendental form) svam (of Myself) adhisthaya (being so situated) sambhavami (I do incarnate) atma-mayaya (by My internal energy).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (168, 4.007, 'Gyan Karma Samnyasa Yoga', 168, 'yada yada hi dharmasya', 'glanir bhavati bharata', 'abhyutthanam-adharmasya', 'tadatmanam srajamay-aham', '', '', 'yada (whenever) yada (wherever) hi (certainly) dharmasya (of religion) glanih (discrepancies) bhavati (manifested, becomes) bharata (O descendant of Bharata) abhyutthanam (predominance) adharmasya (of irreligion) tada (at that time) atmanam (self) srjami (manifest) aham (I).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (169, 4.008, 'Gyan Karma Samnyasa Yoga', 169, 'paritranaya sadhunam', 'vinasaya ch duskritam', 'dharma-samsthapanarthaya', 'sangbhavami yugey yugey', '', '', 'paritranaya (for the deliverance) sadhunam (of the devotees) vinasaya (for the annihilation) ch (also) duskrtam (of the miscreants) dharma (principles of religion) samsthapana-arthaya (to reestablish) sambhavami (I do appear) yuge (millennium) yuge (after millennium).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (170, 4.009, 'Gyan Karma Samnyasa Yoga', 170, 'janma karma ch me divyam', 'evam yo vetti tattvatah', 'tyaktva deham punar janma', 'naiti mam eti so arjuna', '', '', 'janma (birth) karma (work) ch (also) me (of Mine) divyam (transcendental) evam (like this) yah (anyone who) vetti (knows) tattvatah (in reality) tyaktva (leaving aside) deham (this body) punah (again) janma (birth) na (never) eti (does attain) mam (unto Me) eti (does attain) sah (he) arjuna (O Arjuna).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (171, 4.010, 'Gyan Karma Samnyasa Yoga', 171, 'vita-raga-bhaya-krodha', 'man-maya mam upasritah', 'bahavo jnana-tapasa', 'puta mad-bhavam agatah', '', '', 'vita (freed from) raga (attachment) bhaya (fear) krodhah (anger) mat-maya (fully in Me) mam (unto Me) upasritah (being fully situated) bahavah (many) jnana (knowledge) tapasa (by penance) putah (being purified) mat-bhavam (transcendental love for Me) agatah (attained).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (172, 4.011, 'Gyan Karma Samnyasa Yoga', 172, 'ye yatha mam prapadyante', 'tams tathaiva bhajamy aham', 'mama vartmanuvartante', 'manushyah partha sarvasah', '', '', 'ye (all of them) yatha (as) mam (unto Me) prapadyante (surrender) tan (unto them) tatha (so) eva (certainly) bhajami (do I reward) aham (I) mama (My) vartma (path) anuvartante (do follow) manusyah (all men) partha (O son of Prtha) sarvasah (in all respects).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (173, 4.012, 'Gyan Karma Samnyasa Yoga', 173, 'kanksantah karmanam siddhim', 'yajanta iha devatah', 'ksipram hi manuse loke', 'siddhir bhavati karma-ja', '', '', 'kanksantah (desiring) karmanam (of fruitive activities) siddhim (perfection) yajante (worship by sacrifices) iha (in the material world) devatah (the demigods) ksipram (very quickly) hi (certainly) manuse (in human society) loke (within this world) siddhih bhavati (becomes successful) karma-ja (the fruitive worker).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (174, 4.013, 'Gyan Karma Samnyasa Yoga', 174, 'catur-varnyam maya srstam', 'guna-karma-vibhagasah', 'tasya kartaram api mam', 'viddhy akartaram avyayam ', '', '', 'catuh-varnyam (the four divisions of human society) maya (by Me) srstam (created) guna (quality) karma (work) vibhagasah (in terms of division) tasya (of that) kartaram (the father) api (although) mam (Me) viddhi (you may know) akartaram (as the non-doer) avyayam (being unchangeable).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (175, 4.014, 'Gyan Karma Samnyasa Yoga', 175, 'na mam karmani limpanti', 'na me karma-phale sprha', 'iti mam yo ’bhijanati', 'karmabhir na sa badhyate', '', '', 'na (never) mam (unto Me) karmani (all kinds of work) limpanti (do affect) na (nor) me (My) karma-phale (in fruitive action) sprha (aspiration) iti (thus) mam (unto Me) yah (one who) abhijanati (does know) karmabhih (by the reaction of such work) na (never does) sah (he) badhyate (becomes entangled).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (176, 4.015, 'Gyan Karma Samnyasa Yoga', 176, 'evam jnatva kritam karma', 'purvair api mumuksubhih', 'kuru karmaiva tasmat tvam', 'purvaih purvataram kritam', '', '', 'evam (thus) jnatva (knowing well) krtam (performed) karma (work) purvaih (by past authorities) api (although) mumuksubhih (who attained liberation) kuru (just perform) karma (prescribed duty) eva (certainly) tasmat (therefore) tvam (you) purvaih (by the predecessors) purva-taram (ancient predecessors) krtam (as performed).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (177, 4.016, 'Gyan Karma Samnyasa Yoga', 177, 'kim karma kim akarmeti', 'kavayo ’py atra mohitah', 'tat te karma pravaksyami', 'yaj jnatva moksyase ’subhat ', '', '', 'kim (what is) karma (action) kim (what is) akarma (inaction) iti (thus) kavayah (the intelligent) api (also) atra (in this matter) mohitah (bewildered) tat (that) te (unto you) karma (work) pravaksyami (I shall explain) yat (which) jnatva (knowing) moksyase (be liberated) asubhat (from ill fortune).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (178, 4.017, 'Gyan Karma Samnyasa Yoga', 178, 'karmano hy api boddhavyam', 'boddhavyam ch vikarmanah', 'akarmanas ch boddhavyam', 'gahana karmano gatih', '', '', 'karmanah (working order) hi (certainly) api (also) boddhavyam (should be understood) boddhavyam (to be understood) ch (also) vikarmanah (forbidden work) akarmanah (inaction) ch (also) boddhavyam (should be understood) gahana (very difficult) karmanah (working order) gatih (to enter in to).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (179, 4.018, 'Gyan Karma Samnyasa Yoga', 179, 'karmany akarma yah pasyed', 'akarmani ch karma yah', 'sa buddhiman manusyesu', 'sa yuktah krtsna-karma-krt', '', '', 'karmani (in action) akarma (inaction) yah (one who) pasyet (observes) akarmani (in inaction) ch (also) karma (fruitive action) yah (one who) sah (he) buddhi-man (is intelligent) manusyesu (in human society) sah (he) yuktah (in the transcendental position) krtsna-karma-krt (although engaged in all activities).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (180, 4.019, 'Gyan Karma Samnyasa Yoga', 180, 'yasya sarve samarambhah', 'kama-sankalpa-varjitah', 'jnanagni-dagdha-karmanam', 'tam ahuh panditam budhah', '', '', 'yasya (one whose) sarve (all sorts of) samarambhah (in all attempts) kama (desire for sense gratification) sankalpa (determination) varjitah (are devoid of) jnana (of perfect knowledge) agni (fire) dagdha (being burnt by) karmanam (the performer) tam (him) ahuh (declare) panditam (learned) budhah (of those who know).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (181, 4.020, 'Gyan Karma Samnyasa Yoga', 181, 'tyaktva karma-phalasangam', 'nitya-trpto nirasrayah', 'karmany abhipravrtto ’pi', 'naiva kincit karoti sah', '', '', 'tyaktva (having given up) karma-phala-asangam (attachment for fruitive results) nitya (always) trptah (being satisfied) nirasrayah (without any shelter) karmani (in activity) abhipravrttah (being fully engaged) api (in spite of) na (does not) eva (certainly) kincit (anything) karoti (do) sah (he).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (182, 4.021, 'Gyan Karma Samnyasa Yoga', 182, 'nirasir yata-cittatma', 'tyakta-sarva-parigrahah', 'sariram kevalam karma', 'kurvan napnoti kilbisam', '', '', 'nirasih (without desire for the result) yata (controlled) citta-atma (mind and intelligence) tyakta (giving up) sarva (all) parigrahah (sense of proprietorship over all possessions) sariram (in keeping body and soul together) kevalam (only) karma (work) kurvan (doing so) na (never) apnoti (does acquire) kilbisam (sinful reactions).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (183, 4.022, 'Gyan Karma Samnyasa Yoga', 183, 'yadrccha-labha-santusto', 'dvandvatito vimatsarah', 'samah siddhav asiddhau ch', 'kritvapi na nibadhyate', '', '', 'yadrccha (out of its own accord) labha (gain) santustah (satisfied) dvandva (duality) atitah (surpassed) vimatsarah (free from envy) samah (steady) siddhau (in success) asiddhau (failure) ch (also) krtva (doing) api (although) na (never) nibadhyate (is affected).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (184, 4.023, 'Gyan Karma Samnyasa Yoga', 184, 'gata-sangasya muktasya', 'jnanavasthita-cetasah', 'yajnayacaratah karma', 'samagram praviliyate ', '', '', 'gata-sangasya (unattached to the modes of material nature) muktasya (of the liberated) jnana-avasthita (situated in transcendence) cetasah (of such wisdom) yajnaya (for the sake of Yajna (Krishna); acaratah (so active) karma (work) samagram (in total) praviliyate (merges entirely).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (185, 4.024, 'Gyan Karma Samnyasa Yoga', 185, 'brahmarpanam brahma havir', 'brahmagnau brahmana hutam', 'brahmaiva tena gantavyam', 'brahma-karma-samadhina ', '', '', 'brahma (spiritual in nature) arpanam (contribution) brahma (the Supreme) havih (butter) brahma (spiritual) agnau (in the fire of consummation) brahmana (by the spirit soul) hutam (offered) brahma (spiritual kingdom) eva (certainly) tena (by him) gantavyam (to be reached) brahma (spiritual) karma (activities) samadhina (by complete absorption).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (186, 4.025, 'Gyan Karma Samnyasa Yoga', 186, 'daivam evapare yajnam', 'yoginah paryupasate', 'brahmagnav apare yajnam', 'yajnenaivopajuhvati', '', '', 'daivam (in worshiping the demigods) eva (like this) apare (some) yajnam (sacrifices) yoginah (the mystics) paryupasate (worship perfectly) brahma (the Absolute Truth) agnau (in the fire of) apare (others) yajnam (sacrifice) yajnena (by sacrifice) eva (thus) upajuhvati (worship).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (187, 4.026, 'Gyan Karma Samnyasa Yoga', 187, 'srotradinindriyany anye', 'samyamagnisu juhvati', 'shabdadin visayan anya', 'indriyagnisu juhvati ', '', '', 'srotra-adini (hearing process) indriyani (senses) anye (others) samyama (of restraint) agnisu (in the fire) juhvati (offers) sabda-adin (sound vibration, etc.) visayan (objects of sense gratification) anye (others) indriya (of sense organs) agnisu (in the fire) juhvati (sacrifice).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (188, 4.027, 'Gyan Karma Samnyasa Yoga', 188, 'sarvanindriya-karmani', 'prana-karmani capare', 'atma-samyama-yogagnau', 'juhvati jnana-dipite', '', '', 'sarvani (all) indriya (senses) karmani (functions) prana-karmani (functions of the life breath) ch (also) apare (others) atma-samyama (controlling the mind) yoga (linking process) agnau (in the fire of) juhvati (offers) jnana-dipite (because of the urge for self-realization).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (189, 4.028, 'Gyan Karma Samnyasa Yoga', 189, 'dravya-yajnas tapo-yajna', 'yoga-yajnas tathapare', 'svadhyaya-jnana-yajnas ch', 'yatayah samsita-vratah', '', '', 'dravya-yajnah (sacrificing ones possessions) tapah-yajnah (sacrifice in austerities) yoga-yajnah (sacrifice in eightfold mysticism) tatha (thus) apare (others) svadhyaya (sacrifice in the study of the Vedas) jnana-yajnah (sacrifice in advancement of transcendental knowledge) ch (also) yatayah (enlightened) samsita (taken to strict) vratah (vows).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (190, 4.029, 'Gyan Karma Samnyasa Yoga', 190, 'apane juhvati pranam', 'prane ’panam tathapare', 'pranapana-gati ruddhva', 'pranayama-parayanah', 'apare niyataharah', 'pranan pranesu juhvati', 'apane (air which acts downward) juhvati (offers) pranam (air which acts outward) prane (in the air going outward) apanam (air going downward) tatha (as also) apare (others) prana (air going outward) apana (air going downward) gati (movement) ruddhva (checking) prana-ayama (trance induced by stopping all breathing) parayanah (so inclined) apare (others) niyata (controlled) aharah (eating) pranan (outgoing air) pranesu (in the outgoing air) juhvati (sacrifices).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (191, 4.030, 'Gyan Karma Samnyasa Yoga', 191, 'sarve ’py ete yajna-vido', 'yajna-ksapita-kalmasah', 'yajna-sistamrita-bhujo', 'yanti brahma sanatanam', '', '', 'sarve (all) api (although apparently different) ete (all these) yajna-vidah (conversant with the purpose of performing) yajna (sacrifices) ksapita (being cleansed as the result of such performances) kalmasah (sinful reactions) yajna-sista (as a result of such performances of yajna) amrta-bhujah (those who have tasted such nectar) yanti (do approach) brahma (the supreme) sanatanam (eternal atmosphere).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (192, 4.031, 'Gyan Karma Samnyasa Yoga', 192, 'nayam loko ’sty ayajnasya', 'kuto ’nyah kuru-sattama', '', '', '', '', 'na (never) ayam (this) lokah (planet) asti (there is) ayajnasya (of the foolish) kutah (where is) anyah (the other) kuru-sat-tama (O best amongst the Kurus).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (193, 4.032, 'Gyan Karma Samnyasa Yoga', 193, 'evam bahu-vidha yajna', 'vitata brahmano mukhe', 'karma-jan viddhi tan sarvan', 'evam jnatva vimoksyase', '', '', 'evam (thus) bahu-vidhah (various kinds of) yajnah (sacrifices) vitatah (widespread) brahmanah (of the Vedas) mukhe (in the face of) karma-jan (born of work) viddhi (you should know) tan (then) sarvan (all) evam (thus) jnatva (knowing) vimoksyase (be liberated).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (194, 4.033, 'Gyan Karma Samnyasa Yoga', 194, 'sreyan dravya-mayad yajnaj', 'jnana-yajnah parantapa', 'sarvam karmakhilam partha', 'jnane parisamapyate', '', '', 'sreyan (greater) dravya-mayat (than the sacrifice of material possessions) yajnat (knowledge) jnana-yajnah (sacrifice in knowledge) parantapa (O chastiser of the enemy) sarvam (all) karma (activities) akhilam (in totality) partha (O son of Prtha) jnane (in knowledge) parisamapyate (ends in).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (195, 4.034, 'Gyan Karma Samnyasa Yoga', 195, 'tad viddhi pranipatena', 'pariprasnena sevaya', 'upadeksyanti te jnanam', 'jnaninas tattva-darshinah', '', '', 'tat (that knowledge of different sacrifices) viddhi (try to understand) pranipatena (by approaching a spiritual master) pariprasnena (by submissive inquiries) sevaya (by the rendering of service) upadeksyanti (initiate) te (unto you) jnanam (knowledge) jnaninah (the self-realized) tattva (truth) darsinah (the seers).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (196, 4.035, 'Gyan Karma Samnyasa Yoga', 196, 'yaj jnatva na punar moham', 'evam yasyasi pandava', 'yena bhutany asesani', 'draksyasy atmany atho mayi ', '', '', 'yat (which) jnatva (knowing) na (never) punah (again) moham (illusion) evam (like this) yasyasi (you shall go) pandava (O son of Pandu) yena (by which) bhutani (all living entities) asesani (totally) draksyasi (you will see) atmani (in the Supreme Soul) atho (or in other words) mayi (in Me).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (197, 4.036, 'Gyan Karma Samnyasa Yoga', 197, 'api ced asi papebhyah', 'sarvebhyah papa-krt-tamah', 'sarvam jnana-plavenaiva', 'vrjinam santarisyasi', '', '', 'api (even) cet (if) asi (you are) papebhyah (of sinners) sarvebhyah (of all) papa-krttamah (the greatest sinner) sarvam (all such sinful actions) jnana-plavena (by the boat of transcendental knowledge) eva (certainly) vrjinam (the ocean of miseries) santarisyasi (you will cross completely).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (198, 4.037, 'Gyan Karma Samnyasa Yoga', 198, 'yathaidhamsi samiddho ’gnir', 'bhasma-sat kurute ’rjuna', 'jnanagnih sarva-karmani', 'bhasma-sat kurute tatha', '', '', 'yatha (just as) edhamsi (firewood) samiddhah (blazing) agnih (fire) bhasmasat (turns into ashes) kurute (so does) arjuna (O Arjuna) jnana-agnih (the fire of knowledge) sarva-karmani (all reactions to material activities) bhasmasat (to ashes) kurute (it so does) tatha (similarly).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (199, 4.038, 'Gyan Karma Samnyasa Yoga', 199, 'na hi jnanena sadrsam', 'pavitram iha vidyate', 'tat svayam yoga-samsiddhah', 'kalenatmani vindati', '', '', 'na (never) hi (certainly) jnanena (with knowledge) sadrsam (in comparison) pavitram (sanctified) iha (in this world) vidyate (exists) tat (that) svayam (himself) yoga (in devotion) samsiddhah (matured) kalena (in course of time) atmani (in himself) vindati (enjoys).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (200, 4.039, 'Gyan Karma Samnyasa Yoga', 200, 'shraddhaval labhate jnanam', 'tat-parah samyatendriyah', 'jnanam labdhva param shantim', 'acirenadhigacchati', '', '', 'sraddha-van (a faithful man) labhate (achieves) jnanam (knowledge) tat-parah (very much attached to it) samyata (controlled) indriyah (senses) jnanam (knowledge) labdhva (having achieved) param (transcendental) santim (peace) acirena (very soon) adhigacchati (attains).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (201, 4.040, 'Gyan Karma Samnyasa Yoga', 201, 'ajnas casraddadhanas ch', 'samsayatma vinasyati', 'nayam loko ’sti na paro', 'na sukham samsayatmanah', '', '', 'ajnah (fools who have no knowledge in standard scriptures) ch (and) asraddadhanah (without faith in revealed scriptures) ch (also) samsaya (doubts) atma (a person) vinasyati (falls back) na (never) ayam (this) lokah (world) asti (there is) na (neither) parah (next life) na (not) sukham (happiness) samsaya (doubtful) atmanah (of the person).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (202, 4.041, 'Gyan Karma Samnyasa Yoga', 202, 'yoga-sannyasta-karmanam', 'jnana-sanchinna-samsayam', 'atmavantam na karmani', 'nibadhnanti dhananjaya', '', '', 'yoga (devotional service in karma-yoga) sannyasta (renounced) karmanam (of the performers) jnana (knowledge) sanchinna (cut by the advancement of knowledge) samsayam (doubts) atma-vantam (situated in the self) na (never) karmani (work) nibadhnanti (do bind up) dhananjaya (O conqueror of riches).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (203, 4.042, 'Gyan Karma Samnyasa Yoga', 203, 'tasmad ajnana-sambhutam', 'hrt-stham jnanasinatmanah', 'chittvainam samsayam yogam', 'atisthottistha bharata', '', '', 'tasmat (therefore) ajnana-sambhutam (outcome of ignorance) hrt-stham (situated in the heart) jnana (knowledge) asina (by the weapon of) atmanah (of the self) chittva (cutting off) enam (this) samsayam (doubt) yogam (in yoga) atistha (be situated) uttistha (stand up to fight) bharata (O descendant of Bharata).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (204, 5.001, 'Samnyasa Yoga', 204, 'arjuna uvach; अर्जुन उवाच', 'sannyasam karmanam krishna', 'punar yogam ch samsasi', 'yac chreya etayor ekam', 'tan me bruhi su-niscitam', '', 'arjunah uvach (Arjuna says) sannyasam (renunciation) karmanam (of all activities) Krishna (O Krishna) punah (again) yogam (devotional service) ch (also) samsasi (You are praising) yat (which) sreyah (is beneficial) etayoh (of these two) ekam (one) tat (that) me (unto me) bruhi (please tell) suniscitam (definitely).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (205, 5.002, 'Samnyasa Yoga', 205, 'sri-bhagavan uvach; श्रीभगवानुवाच', 'sannyasah karma-yogas ch', 'nihsreyasa-karav ubhau', 'tayos tu karma-sannyasat', 'karma-yogo visisyate ', '', 'sri-bhagavan uvach (the Personality of Godhead says) sannyasah (renunciation of work) karma-yogah (work in devotion) ch (also) nihsreyasa-karau (all leading to the path of liberation) ubhau (both) tayoh (of the two) tu (but) karma-sannyasat (in comparison to the renunciation of fruitive work) karma-yogah (work in devotion) visisyate (is better).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (206, 5.003, 'Samnyasa Yoga', 206, 'jneyah sa nitya-sannyasi', 'yo na dvesti na kanksati', 'nirdvandvo hi maha-baho', 'sukham bandhat pramucyate', '', '', 'jneyah (should be known) sah (he) nitya (always) sannyasi (renouncer) yah (who) na (never) dvesti (abhors) na (nor) kanksati (desires) nirdvandvah (free from all dualities) hi (certainly) maha-baho (O mighty-armed one) sukham (happily) bandhat (from bondage) pramucyate (is completely liberated).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (207, 5.004, 'Samnyasa Yoga', 207, 'sankhya-yogau prithag balah', 'pravadanti na panditah', 'ekam apy asthitah samyag', 'ubhayor vindate phalam', '', '', 'sankhya (analytical study of the material world) yogau (work in devotional service) prthak (different) balah (the less intelligent) pravadanti (say) na (never) panditah (the learned) ekam (in one) api (even) asthitah (being situated) samyak (complete) ubhayoh (of both) vindate (enjoys) phalam (the result).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (208, 5.005, 'Samnyasa Yoga', 208, 'yat sankhyaih prapyate sthanam', 'tad yogair api gamyate', 'ekam sankhyam ch yogam ch', 'yah pasyati sa pasyati', '', '', 'yat (what) sankhyaih (by means of Sankhya philosophy) prapyate (is achieved) sthanam (place) tat (that) yogaih (by devotional service) api (also) gamyate (one can attain) ekam (one) sankhyam (analytical study) ch (and) yogam (action in devotion) ch (and) yah (one who) pasyati (sees) sah (he) pasyati (actually sees).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (209, 5.006, 'Samnyasa Yoga', 209, 'sannyasas tu maha-baho', 'duhkham aptum ayogatah', 'yoga-yukto munir brahma', 'na cirenadhigacchati', '', '', 'sannyasah (the renounced order of life) tu (but) maha-baho (O mighty-armed one) duhkham (distress) aptum (afflicts one with) ayogatah (without devotional service) yoga-yuktah (one engaged in devotional service) munih (a thinker) brahma (the Supreme) na cirena (without delay) adhigacchati (attains).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (210, 5.007, 'Samnyasa Yoga', 210, 'yoga-yukto vishuddhatma', 'vijitatma jitendriyah', 'sarva-bhutatma-bhutatma', 'kurvann api na lipyate', '', '', 'yoga-yuktah (engaged in devotional service) visuddha-atma (a purified soul) vijita-atma (self-controlled) jita-indriyah (having conquered the senses) sarva-bhuta (to all living entities) atma-bhuta-atma (compassionate) kurvan api (although engaged in work) na (never) lipyate (is entangled).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (211, 5.008, 'Samnyasa Yoga', 211, 'naiva kincit karomiti', 'yukto manyeta tattva-vit', 'pasyan shrinvan sprsan jighrann', 'asnan gacchan svapan svasan', '', '', 'na (never) eva (certainly) kincit (anything) karomi (I do) iti (thus) yuktah (engaged in the divine consciousness) manyeta (thinks) tattva-vit (one who knows the truth) pasyan (seeing) srnvan (hearing) sprsan (touching) jighran (smelling) asnan (eating) gacchan (going) svapan (dreaming) svasan (breathing)', NULL, NULL, 'admin');
INSERT INTO gita VALUES (212, 5.009, 'Samnyasa Yoga', 212, 'pralapan visrjan grhnann', 'unmisan nimisann api', 'indriyanindriyarthesu', 'vartanta iti dharayan', '', '', 'pralapan (talking) visrjan (giving up) grhnan (accepting) unmisan (opening) nimisan (closing) api (in spite of) indriyani (the senses) indriya-arthesu (in sense gratification) vartante (let them be so engaged) iti (thus) dharayan (considering).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (213, 5.010, 'Samnyasa Yoga', 213, 'brahmany adhaya karmani', 'sangam tyaktva karoti yah', 'lipyate na sa papena', 'padma-patram ivambhasa', '', '', 'brahmani (the Supreme Personality of Godhead) adhaya (resigning unto) karmani (all works) sangam (attachment) tyaktva (giving up) karoti (performs) yah (who) lipyate (is affected) na (never) sah (he) papena (by sin) padma-patram (lotus leaf) iva (like) ambhasa (in the water).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (214, 5.011, 'Samnyasa Yoga', 214, 'kayena manasa buddhya', 'kevalair indriyair api', 'yoginah karma kurvanti', 'sangam tyaktvatma-shuddhaye', '', '', 'kayena (with the body) manasa (with the mind) buddhya (with the intelligence) kevalaih (purified) indriyaih (with the senses) api (even with) yoginah (Krishna conscious persons) karma (actions) kurvanti (they act) sangam (attachment) tyaktva (giving up) atma (self) suddhaye (for the purpose of purification).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (215, 5.012, 'Samnyasa Yoga', 215, 'yuktah karma-phalam tyaktva', 'shantim apnoti naisthikim', 'ayuktah kama-karena', 'phale sakto nibadhyate', '', '', 'yuktah (one who is engaged in devotional service) karma-phalam (the results of all activities) tyaktva (giving up) santim (perfect peace) apnoti (achieves) naisthikim (unflinching) ayuktah (one who is not in Krishna consciousness) kama-karena (for enjoying the result of work) phale (in the result) saktah (attached) nibadhyate (becomes entangled).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (216, 5.013, 'Samnyasa Yoga', 216, 'sarva-karmani manasa', 'sannyasyaste sukham vasi', 'nava-dvare pure dehi', 'naiva kurvan na karayan', '', '', 'sarva (all) karmani (activities) manasa (by the mind) sannyasya (giving up) aste (remains) sukham (in happiness) vasi (one who is controlled) nava-dvare (in the place where there are nine gates) pure (in the city) dehi (the embodied soul) na (never) eva (certainly) kurvan (doing anything) na (not) karayan (causing to be done).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (217, 5.014, 'Samnyasa Yoga', 217, 'na kartrtvam na karmani', 'lokasya srjati prabhuh', 'na karma-phala-samyogam', 'svabhavas tu pravartate', '', '', 'na (never) kartrtvam (proprietorship) na (nor) karmani (activities) lokasya (of the people) srjati (creates) prabhuh (the master of the city of the body) na (nor) karma-phala (with the results of activities) samyogam (connection) svabhavah (the modes of material nature) tu (but) pravartate (act).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (218, 5.015, 'Samnyasa Yoga', 218, 'nadatte kasyacit papam', 'na caiva sukritam vibhuh', 'ajnanenavrtam jnanam', 'tena muhyanti jantavah', '', '', 'na (never) adatte (accepts) kasyacit (anyones) papam (sin) na (nor) ch (also) eva (certainly) su-krtam (pious activities) vibhuh (the Supreme Lord) ajnanena (by ignorance) avrtam (covered) jnanam (knowledge) tena (by that) muhyanti (are bewildered) jantavah (the living entities).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (219, 5.016, 'Samnyasa Yoga', 219, 'jnanena tu tad ajnanam', 'yesham nasitam atmanah', 'tesham aditya-vaj jnanam', 'prakasayati tat param', '', '', 'jnanena (by knowledge) tu (but) tat (that) ajnanam (nescience) yesam (whose) nasitam (is destroyed) atmanah (of the living entity) tesam (their) aditya-vat (like the rising sun) jnanam (knowledge) prakasayati (discloses) tat param (Krishna consciousness).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (220, 5.017, 'Samnyasa Yoga', 220, 'tad-buddhayas tad-atmanas', 'tan-nisthas tat-parayanah', 'gacchanty apunar-avrttim', 'jnana-nirdhuta-kalmasah', '', '', 'tat-buddhayah (one whose intelligence is always in the Supreme) tat-atmanah (one whose mind is always in the Supreme) tat-nisthah (whose mind is only meant for the Supreme) tat-parayanah (who has completely taken shelter of Him) gacchanti (go) apunah-avrttim (liberation) jnana (knowledge) nirdhuta (cleanses) kalmasah (misgivings).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (221, 5.018, 'Samnyasa Yoga', 221, 'vidya-vinaya-sampanne', 'brahmane gavi hastini', 'suni caiva sva-pake ch', 'panditah sama-darsinah', '', '', 'vidya (education) vinaya (gentleness) sampanne (fully equipped) brahmane (in the brahmana) gavi (in the cow) hastini (in the elephant) suni (in the dog) ch (and) eva (certainly) sva-pake (in the dog-eater (the outcaste); ch (respectively) panditah (those who are so wise) sama-darsinah (do see with equal vision).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (222, 5.019, 'Samnyasa Yoga', 222, 'ihaiva tair jitah sargo', 'yesham samye sthitam manah', 'nirdosam hi samam brahma', 'tasmad brahmani te sthitah', '', '', 'iha (in this life) eva (certainly) taih (by them) jitah (conquered) sargah (birth and death) yesam (of those) samye (in equanimity) sthitam (so situated) manah (mind) nirdosam (flawless) hi (certainly) samam (in equanimity) brahma (the Supreme) tasmat (therefore) brahmani (in the Supreme) te (they) sthitah (are situated).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (223, 5.020, 'Samnyasa Yoga', 223, 'na prahrsyet priyam prapya', 'nodvijet prapya capriyam', 'sthira-buddhir asammudho', 'brahma-vid brahmani sthitah', '', '', 'na (never) prahrsyet (rejoice) priyam (pleasant) prapya (achieving) na (does not) udvijet (agitated) prapya (obtaining) ch (also) apriyam (unpleasant) sthira-buddhih (self-intelligent) asammudhah (unbewildered) brahma-vit (one who knows the Supreme perfectly) brahmani (in the transcendence) sthitah (situated).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (224, 5.021, 'Samnyasa Yoga', 224, 'bahya-sparsesv asaktatma', 'vindaty atmani yat sukham', 'sa brahma-yoga-yuktatma', 'sukham akshayam asnute', '', '', 'bahya-sparsesu (in external sense pleasure) asakta-atma (one who is not so attached) vindati (enjoys) atmani (in the self) yat (that which) sukham (happiness) sah (that) brahma-yoga (concentrated in Brahman) yukta-atma (self-connected) sukham (happiness) aksayam (unlimited) asnute (enjoys).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (225, 5.022, 'Samnyasa Yoga', 225, 'ye hi samsparsha-ja bhoga', 'duhkha-yonaya eva te', 'ady-antavantah kaunteya', 'na tesu ramate budhah', '', '', 'ye (those) hi (certainly) samsparsa-jah (by contact with the material senses) bhogah (enjoyments) duhkha (distress) yonayah (sources of) eva (certainly) te (they are) adi (beginning) anta (end) vantah (subject to) kaunteya (O son of Kunti) na (never) tesu (in those) ramate (takes delight) budhah (the intelligent person).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (226, 5.023, 'Samnyasa Yoga', 226, 'saknotihaiva yah sodhum', 'prak sarira-vimokshanat', 'kama-krodhodbhavam vegam', 'sa yuktah sa sukhi narah', '', '', 'saknoti (able to do) iha eva (in the present body) yah (one who) sodhum (to tolerate) prak (before) sarira (body) vimoksanat (give up) kama (desire) krodha (anger) udbhavam (generated from) vegam (urge) sah (he) yuktah (in trance) sah (he) sukhi (happy) narah (human being).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (227, 5.024, 'Samnyasa Yoga', 227, 'yo ’ntah-sukho ’ntar-aramas', 'tathantar-jyotir eva yah', 'sa yogi brahma-nirvanam', 'brahma-bhuto ’dhigacchati', '', '', 'yah (one who) antah-sukhah (happy from within) antah-aramah (active within) tatha (as well as) antah-jyotih (aiming within) eva (certainly) yah (anyone) sah (he) yogi (mystic) brahma-nirvanam (liberated in the Supreme) brahma-bhutah (self-realized) adhigacchati (attains).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (228, 5.025, 'Samnyasa Yoga', 228, 'labhante brahma-nirvanam', 'rsayah ksina-kalmasah', 'chinna-dvaidha yatatmanah', 'sarva-bhuta-hite ratah', '', '', 'labhante (achieve) brahma-nirvanam (liberation in the Supreme) rsayah (those who are active within) ksina-kalmasah (who are devoid of all sins) chinna (torn off) dvaidhah (duality) yata-atmanah (engaged in self-realization) sarva-bhuta (in all living entities) hite (in welfare work) ratah (engaged).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (229, 5.026, 'Samnyasa Yoga', 229, 'kama-krodha-vimuktanam', 'yatinam yata-cetasam', 'abhito brahma-nirvanam', 'vartate viditatmanam', '', '', 'kama (desires) krodha (anger) vimuktanam (of those who are so liberated) yatinam (of the saintly persons) yata-cetasam (of persons who have full control over the mind) abhitah (assured in the near future) brahma-nirvanam (liberation in the Supreme) vartate (is there) vidita-atmanam (of those who are self-realized).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (230, 5.027, 'Samnyasa Yoga', 230, 'sparshan kritva bahir bahyams', 'caksus caivantare bhruvoh', 'pranapanau samau kritva', 'nasabhyantara-carinau', '', '', 'sparsan (external sense objects, such as sound, etc.) krtva (keeping) bahih (external) bahyan (unnecessary) caksuh (eyes) ch (also) eva (certainly) antare (within) bhruvoh (of the eyebrows) prana-apanau (up-and down-moving air) samau (in suspension) krtva (doing so) nasa-abhyantara (within the nostrils) carinau (blowing) ', NULL, NULL, 'admin');
INSERT INTO gita VALUES (231, 5.028, 'Samnyasa Yoga', 231, 'yatendriya-mano-buddhir', 'munir moksha-parayanah', 'vigateccha-bhaya-krodho', 'yah sada mukta eva sah', '', '', 'yata (controlled) indriya (senses) manah (mind) buddhih (intelligence) munih (the transcendentalist) moksa (liberation) parayanah (being so destined) vigata (discarded) iccha (wishes) bhaya (fear) krodhah (anger) yah (one who) sada (always) muktah (liberated) eva (certainly) sah (he is).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (232, 5.029, 'Samnyasa Yoga', 232, 'bhoktaram yajna-tapasam', 'sarva-loka-maheshvaram', 'suhridam sarva-bhutanam', 'jnatva mam shantim rcchati', '', '', 'bhoktaram (beneficiary) yajna (sacrifices) tapasam (of penances and austerities) sarva-loka (all planets and the demigods thereof) maha-isvaram (the Supreme Lord) su-hrdam (benefactor) sarva (all) bhutanam (of the living entities) jnatva (thus knowing) mam (Me (Lord Krishna); santim (relief from material pangs) rcchati (achieves).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (233, 6.001, 'Atmasamyama-Yoga', 233, 'sri-bhagavan uvach; श्रीभगवानुवाच', 'anasritah karma-phalam', 'karyam karma karoti yah', 'sa sannyasi ch yogi ch', 'na niragnir na cakriyah', '', 'sri-bhagavan uvach (the Lord says) anasritah (without shelter) karma-phalam (the result of work) karyam (obligatory) karma (work) karoti (performs) yah (one who) sah (he) sannyasi (in the renounced order) ch (also) yogi (mystic) ch (also) na (not) nih (without) agnih (fire) na (nor) ch (also) akriyah (without duty).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (234, 6.002, 'Atmasamyama-Yoga', 234, 'yam sannyasam iti prahur', 'yogam tam viddhi pandava', 'na hy asannyasta-sankalpo', 'yogi bhavati kascana', '', '', 'yam (what) sannyasam (renunciation) iti (thus) prahuh (they say) yogam (linking with the Supreme) tam (that) viddhi (you must know) pandava (O son of Pandu) na (never) hi (certainly) asannyasta (without giving up) sankalpah (self-satisfaction) yogi (a mystic transcendentalist) bhavati (becomes) kascana (anyone).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (235, 6.003, 'Atmasamyama-Yoga', 235, 'aruruksor muner yogam', 'karma karanam ucyate', 'yogarudhasya tasyaiva', 'samah karanam ucyate', '', '', 'aruruksoh (of one who has just begun yoga) muneh (of the sage) yogam (the eightfold yoga system) karma (work) karanam (the cause) ucyate (is says to be) yoga (eightfold yoga) arudhasya (of one who has attained) tasya (his) eva (certainly) samah (cessation of all material activities) karanam (the cause) ucyate (is says to be).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (236, 6.004, 'Atmasamyama-Yoga', 236, 'yada hi nendriyarthesu', 'na karmasv anusajjate', 'sarva-sankalpa-sannyasi', 'yogarudhas tadocyate', '', '', 'yada (when) hi (certainly) na (not) indriya-arthesu (in sense gratification) na (never) karmasu (in fruitive activities) anusajjate (does necessarily engage) sarva-sankalpa (all material desires) sannyasi (renouncer) yoga-arudhah (elevated in yoga) tada (at that time) ucyate (is says to be).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (237, 6.005, 'Atmasamyama-Yoga', 237, 'uddhared atmanatmanam', 'natmanam avasadayet', 'atmaiva hy atmano bandhur', 'atmaiva ripur atmanah', '', '', 'uddharet (one must deliver) atmana (by the mind) atmanam (the conditioned soul) na (never) atmanam (the conditioned soul) avasadayet (put into degradation) atma (mind) eva (certainly) hi (indeed) atmanah (of the conditioned soul) bandhuh (friend) atma (mind) eva (certainly) ripuh (enemy) atmanah (of the conditioned soul).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (238, 6.006, 'Atmasamyama-Yoga', 238, 'bandhur atmatmanas tasya', 'yenatmaivatmana jitah', 'anatmanas tu satrutve', 'vartetatmaiva satru-vat', '', '', 'bandhuh (friend) atma (mind) atmanah (of the living entity) tasya (of him) yena (by whom) atma (mind) eva (certainly) atmana (by the living entity) jitah (conquered) anatmanah (of one who has failed to control the mind) tu (but) satrutve (because of enmity) varteta (remains) atma eva (the very mind) satru-vat (as an enemy).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (239, 6.007, 'Atmasamyama-Yoga', 239, 'jitatmanah prashantasya', 'paramatma samahitah', 'sitosna-sukha-duhkhesu', 'tatha manapamanayoh', '', '', 'jita-atmanah (of one who has conquered his mind) prasantasya (of who has attained tranquility by such control over the mind) parama-atma (the Supersoul) samahitah (approached completely) sita (cold) usna (heat) sukha (in happiness) duhkhesu (in distress) tatha (also) mana (honor) apamanayoh (and dishonor).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (240, 6.008, 'Atmasamyama-Yoga', 240, 'jnana-vijnana-trptatma', 'kuta-stho vijitendriyah', 'yukta ity ucyate yogi', 'sama-lostrasma-kancanah', '', '', 'jnana (acquired knowledge) vijnana (realized knowledge) trpta (satisfied) atma (living entity) kuta-sthah (spiritually situated) vijita-indriyah (sensually controlled) yuktah (competent for self-realization) iti (thus) ucyate (is says) yogi (the mystic) sama (equipoised) lostra (pebbles) asma (stone) kancanah (gold).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (241, 6.009, 'Atmasamyama-Yoga', 241, 'suhrn-mitrary-udasina-', 'madhyastha-dvesya-bandhusu', 'sadhusv api ch papesu', 'sama-buddhir visisyate ', '', '', 'su-hrt (by nature a well-wisher) mitra (benefactor with affection) ari (enemy) udasina (neutral between belligerents) madhya-stha (mediator between the belligerents) dvesya (envious) bandhusu (among relatives or well-wishers) sadhusu (unto the pious) api (as well as) ch (and) papesu (unto the sinners) sama-buddhih (having equal intelligence) visisyate (is far advanced).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (242, 6.010, 'Atmasamyama-Yoga', 242, 'yogi yunjita satatam', 'atmanam rahasi sthitah', 'ekaki yata-cittatma', 'nirasir aparigrahah', '', '', 'yogi (a transcendentalist) yunjita (must concentrate in Krishna consciousness) satatam (constantly) atmanam (himself (by the body, mind and self); rahasi (in a secluded place) sthitah (being so situated) ekaki (alone) yata-citta-atma (always careful in mind) nirasih (without being attracted by anything else) aparigrahah (free from the feeling of possessiveness).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (243, 6.011, 'Atmasamyama-Yoga', 243, 'sucau dese pratishthapya', 'sthiram asanam atmanah', 'naty-ucchritam nati-nicam', 'cailajina-kusottaram', '', '', 'sucau (in a sanctified) dese (in the land) pratisthapya (placing) sthiram (firm) asanam (seat) atmanah (self-dependent) na (not) ati (too) ucchritam (high) na (nor) ati (too) nicam (low) caila-ajina (soft cloth and deerskin) kusottaram (kusa grass)', NULL, NULL, 'admin');
INSERT INTO gita VALUES (244, 6.012, 'Atmasamyama-Yoga', 244, 'tatraikagram manah kritva', 'yata-cittendriya-kriyah', 'upavisyasane yunjyad', 'yogam atma-vishuddhaye', '', '', 'tatra (thereupon) eka-agram (one attention) manah (mind) krtva (doing so) yata-citta (controlling the mind) indriya (senses) kriyah (activities) upavisya (sitting on) asane (on the seat) yunjyat (execute) yogam (yoga practice) atma (heart) visuddhaye (for clarifying).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (245, 6.013, 'Atmasamyama-Yoga', 245, 'samam kaya-siro-grivam', 'dharayann acalam sthirah', 'sampreksya nasikagram svam', 'disas canavalokayan', '', '', 'samam (straight) kaya-sirah (body and head) grivam (neck) dharayan (holding) acalam (unmoved) sthirah (still) sampreksya (looking) nasika (nose) agram (tip) svam (own) disah (all sides) ch (also) anavalokayan (not seeing)', NULL, NULL, 'admin');
INSERT INTO gita VALUES (246, 6.014, 'Atmasamyama-Yoga', 246, 'prashantatma vigata-bhir', 'brahmacari-vrate sthitah', 'manah samyamya mac-citto', 'yukta asita mat-parah', '', '', 'prasanta (unagitated) atma (mind) vigata-bhih (devoid of fear) brahmacari-vrate (in the vow of celibacy) sthitah (situated) manah (mind) samyamya (completely subduing) mat (upon Me (Krishna); cittah (concentrated) yuktah (the actual yogi) asita (being so) mat (unto Me) parah (the ultimate goal).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (247, 6.015, 'Atmasamyama-Yoga', 247, 'yunjann evam sadatmanam', 'yogi niyata-manasah', 'shantim nirvana-paramam', 'mat-samstham adhigacchati', '', '', 'yunjan (practicing like this) evam (as mentioned above) sada (constantly) atmanam (body, mind and soul) yogi (the mystic transcendentalist) niyata-manasah (regulated mind) santim (peace) nirvana-paramam (cessation of material existence) mat-samstham (in the spiritual sky (the kingdom of God); adhigacchati (does attain).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (248, 6.016, 'Atmasamyama-Yoga', 248, 'naty-asnatas ’tu yogo ’sti', 'na caikantam anasnatah', 'na cati-svapna-silasya', 'jagrato naiva carjuna', '', '', 'na (never) ati (too much) asnatah (of one who eats so) tu (but) yogah (linking with the Supreme) asti (there is) na (nor) ch (also) ekantam (very low) anasnatah (abstaining from eating) na (nor) ch (also) ati (too much) svapna-silasya (of one who sleeps too much) jagratah (or one who keeps night watch too much) na (not) eva (ever) ch (and) arjuna (O Arjuna).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (249, 6.017, 'Atmasamyama-Yoga', 249, 'yuktahara-viharasya', 'yukta-cestasya karmasu', 'yukta-svapnavabodhasya', 'yogo bhavati duhkha-ha ', '', '', 'yukta (regulated) ahara (eating) viharasya (recreation) yukta (regulated) cestasya (of one who works for maintenance) karmasu (in discharging duties) yukta (regulated) svapna-avabodhasya (regulated sleep and wakefulness) yogah (practice of yoga) bhavati (becomes) duhkha-ha (diminishing pains).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (250, 6.018, 'Atmasamyama-Yoga', 250, 'yada viniyatam cittam', 'atmany evavatisthate', 'nisprhah sarva-kamebhyo', 'yukta ity ucyate tada', '', '', 'yada (when) viniyatam (particularly disciplined) cittam (the mind and its activities) atmani (in the transcendence) eva (certainly) avatisthate (becomes situated) nisprhah (devoid of desire) sarva (all kinds of) kamebhyah (material desires) yuktah (well situated in yoga) iti (thus) ucyate (is says to be) tada (at that time).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (251, 6.019, 'Atmasamyama-Yoga', 251, 'yatha dipo nivata-stho', 'nengate sopama smrta', 'yogino yata-cittasya', 'yunjato yogam atmanah', '', '', 'yatha (as) dipah (a lamp) nivata-sthah (in a place without wind) na (does not) ingate (waver) sa upama (compared to that) smrta (likened) yoginah (of the yogi) yata-cittasya (whose mind is controlled) yunjatah (constantly engaged in) yogam (meditation) atmanah (on transcendence).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (252, 6.020, 'Atmasamyama-Yoga', 252, 'yatroparamate cittam', 'niruddham yoga-sevaya', 'yatra caivatmanatmanam', 'pasyann atmani tusyati', '', '', 'yatra (in that state of affairs) uparamate (when one feels transcendental happiness) cittam (mental activities) niruddham (restrained from matter) yoga-sevaya (by performance of yoga) yatra (in that) ch (also) eva (certainly) atmana (by the pure mind) atmanam (the self) pasyan (realizing the position) atmani (in the self) tusyati (becomes satisfied)', NULL, NULL, 'admin');
INSERT INTO gita VALUES (253, 6.021, 'Atmasamyama-Yoga', 253, 'sukham atyantikam yat tad', 'buddhi-grahyam atindriyam', 'vetti yatra na caivayam', 'sthitas calati tattvatah', '', '', 'sukham (happiness) atyantikam (supreme) yat (in which) tat (that) buddhi (intelligence) grahyam (accessible) atindriyam (transcendental) vetti (knows) yatra (wherein) na (never) ch (also) eva (certainly) ayam (in this) sthitah (situated) calati (moves) tattvatah (from the truth) ', NULL, NULL, 'admin');
INSERT INTO gita VALUES (254, 6.022, 'Atmasamyama-Yoga', 254, 'yam labdhva caparam labham', 'manyate nadhikam tatah', 'yasmin sthito na duhkhena', 'gurunapi vicalyate', '', '', 'yam (that which) labdhva (by attainment) ch (also) aparam (any other) labham (gain) manyate (does not mind) na (never) adhikam (more than that) tatah (from that) yasmin (in which) sthitah (being situated) na (never) duhkhena (by miseries) guruna api (even though very difficult) vicalyate (becomes shaken)', NULL, NULL, 'admin');
INSERT INTO gita VALUES (255, 6.023, 'Atmasamyama-Yoga', 255, 'tam vidyad duhkha-samyoga-', 'viyogam yoga-samjnitam', '', '', '', '', 'tam (that) vidyat (you must know) duhkha-samyoga (miseries of material contact) viyogam (extermination) yoga-samjnitam (trance in yoga.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (256, 6.024, 'Atmasamyama-Yoga', 256, 'sa niscayena yoktavyo', 'yogo ’nirvinna-cetasa', 'sankalpa-prabhavan kamams', 'tyaktva sarvan asesatah', 'manasaivendriya-gramam', 'viniyamya samantatah', 'sah (that yoga system) niscayena (with firm determination) yoktavyah (must be practiced) yogah (in such practice) anirvinna-cetasa (without deviation) sankalpa (material desires) prabhavan (born of) kaman (sense gratification) tyaktva (giving up) sarvan (all) asesatah (completely) manasa (by the mind) eva (certainly) indriya-gramam (the full set of senses) viniyamya (regulating) samantatah (from all sides).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (257, 6.025, 'Atmasamyama-Yoga', 257, 'sanaih sanair uparamed', 'buddhya dhrti-grhitaya', 'atma-samstham manah kritva', 'na kincid api cintayet ', '', '', 'sanaih (gradually) sanaih (step by step) uparamet (hesitated) buddhya (by intelligence) dhrti-grhitaya (carrying the conviction) atma-samstham (placed in transcendence) manah (mind) krtva (doing so) na (not) kincit (anything else) api (even) cintayet (be thinking of).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (258, 6.026, 'Atmasamyama-Yoga', 258, 'yato yato niscalati', 'manas cancalam asthiram', 'tatas tato niyamyaitad', 'atmany eva vasam nayet', '', '', 'yatah (whatever) yatah (wherever) niscalati (verily agitated) manah (the mind) cancalam (flickering) asthiram (unsteady) tatah (from there) tatah (and thereafter) niyamya (regulating) etat (this) atmani (in the self) eva (certainly) vasam (control) nayet (must bring in).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (259, 6.027, 'Atmasamyama-Yoga', 259, 'prashanta-manasam hy enam', 'yoginam sukham uttamam', 'upaiti santa-rajasam', 'brahma-bhutam akalmasam ', '', '', 'prasanta (mind fixed on the lotus feet of Krishna) manasam (of one whose mind is so fixed) hi (certainly) enam (this) yoginam (the yogi) sukham (happiness) uttamam (the highest) upaiti (attains) santa-rajasam (pacified passion) brahma-bhutam (liberation by identification with the Absolute) akalmasam (freed from all past sinful reaction.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (260, 6.028, 'Atmasamyama-Yoga', 260, 'yunjann evam sadatmanam', 'yogi vigata-kalmasah', 'sukhena brahma-samsparsham', 'atyantam sukham asnute', '', '', 'yunjan (thus being engaged in yoga practice) evam (thus) sada (always) atmanam (self) yogi (one who is in touch with the Supreme Self) vigata (is freed from) kalmasah (all material contamination) sukhena (in transcendental happiness) brahma-samsparsam (being in constant touch with the Supreme) atyantam (highest) sukham (happiness) asnute (attains.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (261, 6.029, 'Atmasamyama-Yoga', 261, 'sarva-bhuta-stham atmanam', 'sarva-bhutani catmani', 'iksate yoga-yuktatma', 'sarvatra sama-darshanah', '', '', 'sarva-bhuta-stham (situated in all beings) atmanam (the Supersoul) sarva (all) bhutani (entities) ch (also) atmani (in the self) iksate (does see) yoga-yukta-atma (one who is dovetailed in Krishna consciousness) sarvatra (everywhere) sama-darsanah (seeing equally.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (262, 6.030, 'Atmasamyama-Yoga', 262, 'yo mam pasyati sarvatra', 'sarvam ch mayi pasyati', 'tasyaham na pranasyami', 'sa ch me na pranasyati ', '', '', 'yah (whoever) mam (Me) pasyati (sees) sarvatra (everywhere) sarvam (everything) ch (and) mayi (in Me) pasyati (he sees) tasya (his) aham (I) na (not) pranasyami (am lost) sah (he) ch (also) me (to Me) na (nor) pranasyati (is lost).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (263, 6.031, 'Atmasamyama-Yoga', 263, 'sarva-bhuta-sthitam yo mam', 'bhajaty ekatvam asthitah', 'sarvatha vartamano ’pi', 'sa yogi mayi vartate ', '', '', 'sarva-bhuta-sthitam (situated in everyones heart) yah (he who) mam (unto Me) bhajati (serves in devotional service) ekatvam (oneness) asthitah (thus situated) sarvatha (in all respects) varta-manah (being situated) api (in spite of) sah (he) yogi (transcendentalist) mayi (unto Me) vartate (remains.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (264, 6.032, 'Atmasamyama-Yoga', 264, 'atmaupamyena sarvatra', 'samam pasyati yo ’rjuna', 'sukham va yadi va duhkham', 'sa yogi paramo matah', '', '', 'atma (self) aupamyena (by comparison) sarvatra (everywhere) samam (equally) pasyati (sees) yah (he who) arjuna (O Arjuna) sukham (happiness) va (or) yadi (if) va (or) duhkham (distress) sah (such) yogi (transcendentalist) paramah (perfect) matah (considered.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (265, 6.033, 'Atmasamyama-Yoga', 265, 'arjuna uvach; अर्जुन उवाच', 'yo ’yam yogas tvaya proktah', 'samyena madhusudana', 'etasyaham na pasyami', 'cancalatvat sthitim sthiram', '', 'arjunah uvach (Arjuna says) yah (the system) ayam (this) yogah (mysticism) tvaya (by You) proktah (described) samyena (generally) madhu-sudana (O killer of the demon Madhu) etasya (of this) aham (I) na (do not) pasyami (see) cancalatvat (due to being restless) sthitim (situation) sthiram (stable.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (266, 6.034, 'Atmasamyama-Yoga', 266, 'cancalam hi manah krishna', 'pramathi balavad drdham', 'tasyaham nigraham manye', 'vayor iva su-duskaram', '', '', 'cancalam (flickering) hi (certainly) manah (mind) Krishna (O Krishna) pramathi (agitating) bala-vat (strong) drdham (obstinate) tasya (its) aham (I) nigraham (subduing) manye (think) vayoh (of the wind) iva (like) su-duskaram (difficult.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (267, 6.035, 'Atmasamyama-Yoga', 267, 'sri-bhagavan uvach; श्रीभगवानुवाच', 'asamsayam maha-baho', 'mano durnigraham calam', 'abhyasena tu kaunteya', 'vairagyena ch grhyate', '', 'sri-bhagavan uvach (the Personality of Godhead says) asamsayam (undoubtedly) maha-baho (O mighty-armed one) manah (mind) durnigraham (difficult to curb) calam (flickering) abhyasena (by practice) tu (but) kaunteya (O son of Kunti) vairagyena (by detachment) ch (also) grhyate (can be so controlled.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (268, 6.036, 'Atmasamyama-Yoga', 268, 'asamyatatmana yogo', 'dusprapa iti me matih', 'vasyatmana tu yatata', 'sakyo ’vaptum upayatah', '', '', 'asamyata (unbridled) atmana (by the mind) yogah (self-realization) dusprapah (difficult to obtain) iti (thus) me (My) matih (opinion) vasya (controlled) atmana (by the mind) tu (but) yatata (while endeavoring) sakyah (practical) avaptum (to achieve) upayatah (appropriate means.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (269, 6.037, 'Atmasamyama-Yoga', 269, 'arjuna uvach; अर्जुन उवाच', 'ayatih shraddhayopeto', 'yogac calita-manasah', 'aprapya yoga-samsiddhim', 'kam gatim krishna gacchati', '', 'arjunah uvach (Arjuna says) ayatih (unsuccessful transcendentalist) sraddhaya (with faith) upetah (engaged) yogat (from the mystic link) calita (deviated) manasah (of one who has such a mind) aprapya (failing) yoga-samsiddhim (highest perfection in mysticism) kam (which) gatim (destination) Krishna (O Krishna) gacchati (achieves.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (270, 6.038, 'Atmasamyama-Yoga', 270, 'kaccin nobhaya-vibhrastas', 'chinnabhram iva nasyati', 'apratistho maha-baho', 'vimudho brahmanah pathi', '', '', 'kaccit (whether) na (not) ubhaya (both) vibhrastah (deviated from) chinna (fallen) abhram (cloud) iva (like) nasyati (perishes) apratisthah (without any position) maha-baho (O mighty-armed Krishna) vimudhah (bewildered) brahmanah (of transcendence) pathi (on the path.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (271, 6.039, 'Atmasamyama-Yoga', 271, 'etan me samsayam krishna', 'chettum arhasy asesatah', 'tvad-anyah samsayasyasya', 'chetta na hy upapadyate', '', '', 'etat (this is) me (my) samsayam (doubt) Krishna (O Krishna) chettum (to dispel) arhasi (requested to do) asesatah (completely) tvat (than You) anyah (without) samsayasya (of the doubt) asya (of this) chetta (remover) na (never) hi (certainly) upapadyate (to be found.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (272, 6.040, 'Atmasamyama-Yoga', 272, 'sri-bhagavan uvach; श्रीभगवानुवाच', 'partha naiveha namutra', 'vinasas tasya vidyate', 'na hi kalyana-krt kascid', 'durgatim tata gacchati', '', 'sri-bhagavan uvach (The Lord says) partha (O son of Prtha) na eva (never is it so) iha (in this material world) na (never) amutra (in the next life) vinasah (destruction) tasya (his) vidyate (exists) na (never) hi (certainly) kalyana-krt (one who is engaged in auspicious activities) kascit (anyone) durgatim (degradation) tata (thereafter) gacchati (goes.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (273, 6.041, 'Atmasamyama-Yoga', 273, 'prapya punya-kritam lokan', 'usitva sasvatih samah', 'sucinam srimatam gehe', 'yoga-bhrasto ’bhijayate ', '', '', 'prapya (after achieving) punya-krtam (of those who performed pious activities) lokan (planets) usitva (after dwelling) sasvatih (many) samah (years) sucinam (of the pious) sri-matam (of the prosperous) gehe (in the house of) yoga-bhrastah (one who is fallen from the path of self-realization) abhijayate (takes his birth.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (274, 6.042, 'Atmasamyama-Yoga', 274, 'atha va yoginam eva', 'kule bhavati dhimatam', 'etad dhi durlabhataram', 'loke janma yad idrsam', '', '', 'atha va (or) yoginam (of learned transcendentalists) eva (certainly) kule (in the family of) bhavati (takes birth) dhi-matam (of those who are endowed with great wisdom) etat (this) hi (certainly) durlabha-taram (very rare) loke (in this world) janma (birth) yat (that which) idrsam (like this.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (275, 6.043, 'Atmasamyama-Yoga', 275, 'tatra tam buddhi-samyogam', 'labhate paurva-dehikam', 'yatate ch tato bhuyah', 'samsiddhau kuru-nandana ', '', '', 'tatra (thereupon) tam (that) buddhi-samyogam (revival of such consciousness) labhate (regains) paurva (previous) dehikam (bodily consciousness) yatate (he endeavors) ch (also) tatah (thereafter) bhuyah (again) samsiddhau (for perfection) kuru-nandana (O son of Kuru.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (276, 6.044, 'Atmasamyama-Yoga', 276, 'purvabhyasena tenaiva', 'hriyate hy avaso ’pi sah', 'jijnasur api yogasya', 'shabda-brahmativartate', '', '', 'purva (previous) abhyasena (practice) tena (by the influence of that) eva (certainly) hriyate (is attracted) hi (surely) avasah (helpless) api (also) sah (he) jijnasuh (willing to know) api (so) yogasya (of yoga) sabda-brahma (ritualistic principles of scripture) ativartate (transcends.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (277, 6.045, 'Atmasamyama-Yoga', 277, 'prayatnad yatamanas tu', 'yogi samshuddha-kilbisah', 'aneka-janma-samsiddhas', 'tato yati param gatim ', '', '', 'prayatnat (by rigid practice) yatamanah (one who endeavors) tu (but) yogi (such a transcendentalist) samsuddha (washed off) kilbisah (all kinds of sins) aneka (many, many) janma (births) samsiddhah (so achieved perfection) tatah (thereafter) yati (attains) param (highest) gatim (destination.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (278, 6.046, 'Atmasamyama-Yoga', 278, 'tapasvibhyo ’dhiko yogi', 'jnanibhyo ’pi mato ’dhikah', 'karmibhyas cadhiko yogi', 'tasmad yogi bhavarjuna', '', '', 'tapasvibhyah (than the ascetic) adhikah (greater) yogi (the yogi) jnanibhyah (than the wise) api (also) matah (considered) adhikah (greater than) karmibhyah (than the fruitive worker) ch (also) adhikah (greater than) yogi (the yogi) tasmat (therefore) yogi (a transcendentalist) bhava (just become) arjuna (O Arjuna.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (279, 6.047, 'Atmasamyama-Yoga', 279, 'yoginam api sarvesam', 'mad-gatenantar-atmana', 'shraddhavan bhajate yo mam', 'sa me yuktatamo matah ', '', '', 'yoginam (of all yogis) api (also) sarvesam (all types of) mat-gatena (abiding in Me) antah-atmana (always thinking of Me within) sraddha-van (in full faith) bhajate (renders transcendental loving service) yah (one who) mam (Me (the Supreme Lord)) sah (he) me (Mine) yukta-tamah (the greatest yogi) matah (is considered.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (280, 7.001, 'Gyan-Vigyan-Yoga', 280, 'sri-bhagavan uvach; श्रीभगवानुवाच', 'mayy asaktamanah partha ', 'yogam yunjan madasrayah', 'asamsayam samagram mam ', 'yatha jnasyasi tac chrnu', '', 'sri-bhagavan uvach (the Supreme Lord says) mayi (unto Me) asakta-manah (mind attached) partha (O son of Prtha) yogam (self-realization) yunjan (so practicing) mat-asrayah (in consciousness of Me (Krishna consciousness)) asamsayam (without doubt) samagram (completely) mam (unto Me) yatha (as much as) jnasyasi (you can know) tat (that) srnu (try to hear.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (281, 7.002, 'Gyan-Vigyan-Yoga', 281, 'jnanam teham savijnanam ', 'idam vaksyamy asesatah', 'yaj jnatva neha bhuyo ', 'anyaj jnatavyam avasisyate', '', '', 'jnanam (phenomenal knowledge) te (unto you) aham (I) sa (with) vijnanam (noumenal knowledge) idam (this) vaksyami (shall explain) asesatah (in full) yat (which) jnatva (knowing) na (not) iha (in this world) bhuyah (further) anyat (anything more) jnatavyam (knowable) avasisyate (remains to be known.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (282, 7.003, 'Gyan-Vigyan-Yoga', 282, 'manusyanam sahasresu ', 'kascid yatati siddhaye', 'yatatam api siddhanam ', 'kascin mam vetti tattvatah', '', '', 'manusyanam (of men) sahasresu (out of many thousands) kascit (someone) yatati (endeavors) siddhaye (for perfection) yatatam (of those so endeavoring) api (indeed) siddhanam (of those who have achieved perfection) kascit (someone) mam (Me) vetti (does know) tattvatah (in fact.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (283, 7.004, 'Gyan-Vigyan-Yoga', 283, 'bhumir aponalo vayuh ', 'kham mano buddhir eva ch', 'ahamkara itiyam me ', 'bhinna prakrtir astadha', '', '', 'bhumih (earth) apah (water) analah (fire) vayuh (air) kham (ether) manah (mind) buddhih (intelligence) eva (certainly) ch (and) ahankarah (false ego) iti (thus) iyam (all these) me (My) bhinna (separated) prakrtih (energies) astadha (total eight.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (284, 7.005, 'Gyan-Vigyan-Yoga', 284, 'apareyam itas tvanyam ', 'prakrtim viddhi me param', 'jivabhutam mahabaho ', 'yayedam dharyate jagat', '', '', 'apara (inferior) iyam (this) itah (besides this) tu (but) anyam (another) prakrtim (energy) viddhi (just try to understand) me (My) param (superior) jiva-bhutam (the living entities) maha-baho (O mighty-armed one) yaya (by whom) idam (this) dharyate (being utilized or exploited) jagat (the material world.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (285, 7.006, 'Gyan-Vigyan-Yoga', 285, 'etadyonini bhutani ', 'sarvanity upadharaya', 'aham krtsnasya jagatah ', 'prabhavah pralayas tatha', '', '', 'etat (these two natures) yonini (source of birth) bhutani (everything created) sarvani (all) iti (thus) upadharaya (know) aham (I) krtsnasya (all-inclusive) jagatah (of the world) prabhavah (source of manifestation) pralayah (annihilation) tatha (as well as.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (286, 7.007, 'Gyan-Vigyan-Yoga', 286, 'mattah parataram nanyat ', 'kimcid asti dhanamjaya', 'mayi sarvam idam protam sutre ', 'manigana iva', '', '', 'mattah (beyond Myself) para-taram (superior) na (not) anyat kincit (anything else) asti (there is) dhananjaya (O conqueror of wealth) mayi (in Me) sarvam (all that be) idam (which we see) protam (strung) sutre (on a thread) mani-ganah (pearls) iva (likened.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (287, 7.008, 'Gyan-Vigyan-Yoga', 287, 'raso aham apsu kaunteya ', 'prabhasmi sasisuryayoh', 'pranavah sarvavedesu sabdah ', 'khe paurusam nrsu', '', '', 'rasah (taste) aham (I) apsu (in water) kaunteya (O son of Kunti) prabha asmi (I am the light) sasi-suryayoh (in the sun and the moon) pranavah (the letters A.U.M.) sarva (in all) vedesu (in the Vedas) sabdah (sound vibration) khe (in the ether) paurusam (ability) nrsu (in man.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (288, 7.009, 'Gyan-Vigyan-Yoga', 288, 'punyo gandhah prthivyam ch ', 'tejas casmi vibhavasau', 'jivanam sarvabhutesu ', 'tapas casmi tapasvisu', '', '', 'punyah (original) gandhah (fragrance) prthivyam (in the earth) ch (also) tejah (temperature) ch (also) asmi (I am) vibhavasau (in the fire) jivanam (life) sarva (all) bhutesu (living entities) tapah (penance) ch (also) asmi (I am) tapasvisu (in those who practice penance.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (289, 7.010, 'Gyan-Vigyan-Yoga', 289, 'bijam mam sarvabhutanam ', 'viddhi partha sanatanam', 'buddhir buddhimatam asmi ', 'tejas tejasvinam aham', '', '', 'bijam (the seed) mam (unto Me) sarva-bhutanam (of all living entities) viddhi (try to understand) partha (O son of Prtha) sanatanam (original, eternal) buddhih (intelligence) buddhi-matam (of the intelligent) asmi (I am) tejah (prowess) tejasvinam (of the powerful) aham (I am.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (290, 7.011, 'Gyan-Vigyan-Yoga', 290, 'balam balavatam caham ', 'kamaragavivarjitam', 'dharmaviruddho bhutesu ', 'kamo.asmi bharatarsabha', '', '', 'balam (strength) bala-vatam (of the strong) ch (and) aham (I am) kama (passion) raga (attachment) vivarjitam (devoid of) dharma-aviruddhah (not against the religious principles) bhutesu (in all beings) kamah (sex life) asmi (I am) bharata-rsabha (O lord of the Bharatas.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (291, 7.012, 'Gyan-Vigyan-Yoga', 291, 'ye caiva satvika bhava ', 'rajasas tamasas ch ye', 'matta eveti tan viddhi ', 'na tv aham tesu te mayi', '', '', 'ye (all those) ch (and) eva (certainly) sattvikah (in goodness) bhavah (states of being) rajasah (mode of passion) tamasah (mode of ignorance) ch (also) ye (although) mattah (from Me) eva (certainly) iti (thus) tan (those) viddhi (try to know) na (not) tu (but) aham (I) tesu (in those) te (they) mayi (unto Me.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (292, 7.013, 'Gyan-Vigyan-Yoga', 292, 'tribhir gunamayair bhavair ', 'ebhih sarvam idam jagat', 'mohitam nabhijanati ', 'mam ebhyah param avyayam', '', '', 'tribhih (three) guna-mayaih (by the three gunas) bhavaih (state of being) ebhih (all these) sarvam (the whole world) idam (in this world) jagat (universe) mohitam (deluded) na abhijanati (do not know) mam (Me) ebhyah (above these) param (the Supreme) avyayam (inexhaustible.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (293, 7.014, 'Gyan-Vigyan-Yoga', 293, 'daivi hy esa gunamayi ', 'mama maya duratyaya', 'mam eva ye prapadyante ', 'mayam etam taranti te', '', '', 'daivi (transcendental) hi (certainly) esa (this) guna-mayi (consisting of the three modes of material nature) mama (My) maya (energy) duratyaya (very difficult to overcome) mam (unto Me) eva (certainly) ye (those) prapadyante (surrender) mayam etam (this illusory energy) taranti (overcome) te (they.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (294, 7.015, 'Gyan-Vigyan-Yoga', 294, 'na mam duskrtino mudhah ', 'prapadyante naradhamah', 'mayayapahrtajnana asuram ', 'bhavam asritah', '', '', 'na (not) mam (unto Me) duskrtinah (miscreants) mudhah (foolish) prapadyante (surrender) nara-adhamah (lowest among mankind) mayaya (by the illusory energy) apahrta (stolen by illusion) jnanah (knowledge) asuram (demonic) bhavam (nature) asritah (accepting.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (295, 7.016, 'Gyan-Vigyan-Yoga', 295, 'caturvidha bhajante mam ', 'janah sukrtinorjuna', 'arto jijnasur artharthi ', 'jnani ch bharatarsabha', '', '', 'catuh-vidhah (four kinds of) bhajante (render services) mam (unto Me) janah (persons) su-krtinah (those who are pious) arjuna (O Arjuna) artah (the distressed) jijnasuh (the inquisitive) artha-arthi (one who desires material gain) jnani (one who knows things as they are) ch (also) bharata-rsabha (O great one amongst the descendants of Bharata.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (296, 7.017, 'Gyan-Vigyan-Yoga', 296, 'tesam jnani nityayukta ', 'ekabhaktir visisyate', 'priyo hi jnaninotyartham ', 'aham sa ch mama priyah', '', '', 'tesam (out of them) jnani (one in full knowledge) nitya-yuktah (always engaged) eka (only one) bhaktih (in devotional service) visisyate (especially) priyah (very dear) hi (certainly) jnaninah (person in knowledge) atyartham (highly) aham (I am) sah (he) ch (also) mama (Mine) priyah (dear.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (297, 7.018, 'Gyan-Vigyan-Yoga', 297, 'udarah sarva evaite ', 'jnani tv atmaiva me matam', 'asthitah sa hi yuktatma ', 'mam evanuttamam gatim', '', '', 'udarah (magnanimous) sarve (all) eva (certainly) ete (these) jnani (one who is in knowledge) tu (but) atma eva (just like Myself) me (My) matam (opinion) asthitah (situated) sah (he) hi (certainly) yukta-atma (engaged in devotional service) mam (unto Me) eva (certainly) anuttamam (the highest goal) gatim (destination.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (298, 7.019, 'Gyan-Vigyan-Yoga', 298, 'bahunam janmanam ante ', 'jnanavan mam prapadyate', 'vasudevah sarvam iti sa ', 'mahatma sudurlabhah', '', '', 'bahunam (many) janmanam (births) ante (after) jnana-van (he possessing knowledge) mam (unto Me) prapadyate (surrenders) vasudevah (cause of all causes) sarvam (all) iti (thus) sah (such) maha-atma (great soul) su-durlabhah (very rare.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (299, 7.020, 'Gyan-Vigyan-Yoga', 299, 'kamais tais tair hrtajnanah ', 'prapadyantenyadevatah', 'tam tam niyamam asthaya ', 'prakrtya niyatah svaya', '', '', 'kamaih (by desires) taih (by those) taih (by those) hrta (distorted) jnanah (knowledge) prapadyante (surrender) anya (other) devatah (demigods) tam (that) tam (that) niyamam (rules) asthaya (following) prakrtya (by nature) niyatah (controlled) svaya (by their own.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (300, 7.021, 'Gyan-Vigyan-Yoga', 300, 'yo yo yam yam tanum bhaktah sraddhayarcitum', 'icchati', 'tasya tasyacalam sraddham ', 'tam eva vidadhamy aham', '', '', 'yah (that) yah (that) yam (which) yam (which) tanum (form of the demigods) bhaktah (devotee) sraddhaya (with faith) arcitum (to worship) icchati (desires) tasya (of that) tasya (of that) acalam (steady) sraddham (faith) tam (him) eva (surely) vidadhami (give) aham (I.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (301, 7.022, 'Gyan-Vigyan-Yoga', 301, 'sa taya sraddhaya yuktas ', 'tasyaradhanam ihate', 'labhate ch tatah kaman ', 'mayaivah vihitan hi tan', '', '', 'sah (he) taya (with that) sraddhaya (with faith) yuktah (endowed) tasya (his) aradhanam (worship) ihate (seeks) labhate (obtains) ch (and) tatah (from which) kaman (desires) maya (by Me) eva (alone) vihitan (regulated) hi (for) tan (those.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (302, 7.023, 'Gyan-Vigyan-Yoga', 302, 'antavat tu phalam tesam ', 'tad bhavaty alpamedhasam', 'devan devayajo yanti ', 'madbhakta yanti mam api', '', '', 'anta-vat tu (limited and temporary) phalam (fruits) tesam (their) tat (that) bhavati (becomes) alpa-medhasam (of those of small intelligence) devan (demigods planets) deva-yajah (worshipers of demigods) yanti (achieve) mat (My) bhaktah (devotees) yanti (attain) mam (to Me) api (surely.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (303, 7.024, 'Gyan-Vigyan-Yoga', 303, 'avyaktam vyaktim apannam manyante mam', 'abuddhayah', 'param bhavam ajananto ', 'mamavyayam anuttamam', '', '', 'avyaktam (nonmanifested) vyaktim (personality) apannam (achieved) manyante (think) mam (unto Me) abuddhayah (less intelligent persons) param (supreme) bhavam (state of being) ajanantah (without knowing) mama (My) avyayam (imperishable) anuttamam (the finest.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (304, 7.025, 'Gyan-Vigyan-Yoga', 304, 'naham prakasah sarvasya ', 'yogamayasamavrtah', 'mudhoyam nabhijanati loko ', 'mam ajam avyayam', '', '', 'na (nor) aham (I) prakasah (manifest) sarvasya (to everyone) yoga-maya (internal potency) samavrtah (covered) mudhah (foolish) ayam (this) na (not) abhijanati (can understand) lokah (such less intelligent persons) mam (Me) ajam (unborn) avyayam (inexhaustible.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (305, 7.026, 'Gyan-Vigyan-Yoga', 305, 'vedaham samatitani ', 'vartamanani carjuna', 'bhavisyani ch bhutani ', 'mam tu veda na kascana', '', '', 'veda (know) aham (I) sama (equally) a titani (past) vartamanani (present) ch (and) arjuna (O Arjuna) bhavisyani (future) ch (also) bhutani (living entities) mam (Me) tu (but) veda (knows) na (not) kascana (anyone.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (306, 7.027, 'Gyan-Vigyan-Yoga', 306, 'icchadvesasamutthena ', 'dvandvamohena bharata', 'sarvabhutani sammoham ', 'sarge yanti paramtapa', '', '', 'iccha (desire) dvesa (hate) samutthena (born) dvandva (duality) mohena (overcome) bharata (O scion of Bharata) sarva (all) bhutani (living entities) sammoham (into delusion) sarge (in creation) yanti (go) parantapa (O conqueror of enemies.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (307, 7.028, 'Gyan-Vigyan-Yoga', 307, 'yesam tv antagatam papam ', 'jananam punyakarmanam', 'te dvandvamohanirmukta ', 'bhajante mam drdhavratah', '', '', 'yesam (whose) tu (but) anta-gatam (completely eradicated) papam (sin) jananam (of the persons) punya (pious) karmanam (previous activities) te (they) dvandva (duality) moha (delusion) nirmuktah (free from) bhajante (worship) mam (Me) drdha-vratah (with determination.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (308, 7.029, 'Gyan-Vigyan-Yoga', 308, 'jaramaranamoksaya mam ', 'asritya yatanti ye', 'te brahma tad viduh ', 'krtsnam adhyatmam karmacakhilam ', '', '', 'jara (old age) marana (death) moksaya (for the purpose of liberation) mam (unto Me) asritya (taking shelter of) yatanti (endeavor) ye (all those) te (such persons) brahma (Brahman) tat (actually that) viduh (they know) krtsnam (everything) adhyatmam (transcendental) karma (fruitive activities) ch (and) akhilam (entirely.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (309, 7.030, 'Gyan-Vigyan-Yoga', 309, 'sadhibhutadhidaivam mam ', 'sadhiyajnam ch ye viduh', 'prayanakalepi ch mam te ', 'vidur yuktacetasah', '', '', 'sa-adhibhuta (the governing principle of the material manifestation) adhidaivam (underlying all the demigods) mam (Me) sa-adhiyajnam (sustaining all sacrifices) ch (and) ye (those) viduh (know) prayana (of death) kale (at the time) api (even) ch (and) mam (Me) te (they) viduh (know) yukta-cetasah (with steadfast minds.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (310, 8.001, 'Brahma-Yoga', 310, 'arjuna uvach; अर्जुन उवाच', 'kim tad brahma kim adhyatmam kim karma', 'purusottama', 'adhibhutam ch kim proktam adhidaivam kim ucyate', '', '', 'arjunah uvach (Arjuna says) kim (what) tat (that) brahma (Brahman) kim (what) adhyatmam (the self) kim (what) karma (fruitive activities) purusa-uttama (O Supreme Person) adhibhutam (the material manifestation) ch (and) kim (what) proktam (is called) adhidaivam (the demigods) kim (what) ucyate (is called.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (311, 8.002, 'Brahma-Yoga', 311, 'adhiyajnah katham kotra ', 'dehesmin madhusudana', 'prayanakale ch katham ', 'jneyosi niyatatmabhih ', '', '', 'adhiyajnah (the Lord of sacrifice) katham (how) kah (who) atra (here) dehe (in the body) asmin (in this) madhusudana (O Madhusudana) prayana-kale (at the time of death) ch (and) katham (how) jneyah (be known) asi (You can) niyata-atmabhih (by the self-controlled.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (312, 8.003, 'Brahma-Yoga', 312, 'sri-bhagavan uvach; श्रीभगवानुवाच', 'aksaram brahma paramam ', 'svabhavodhyatmam ucyate', 'bhutabhavodbhavakaro visargah karmasamjnitah ', '', '', 'sri-bhagavan uvach (The Lord says) aksaram (indestructible) brahma (Brahman) paramam (transcendental) svabhavah (eternal nature) adhyatmam (the self) ucyate (is called) bhuta-bhava-udbhava-karah (action producing the material bodies of the living entities) visargah (creation) karma (fruitive activities) samjnitah (is called.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (313, 8.004, 'Brahma-Yoga', 313, 'adhibhutam ksaro bhavah ', 'purusas cadhidaivatam', 'adhiyajnoham evatra ', 'dehe dehabhrtam vara ', '', '', 'adhibhutam (the physical manifestation) ksarah (constantly changing) bhavah (nature) purusah (the universal form) ch (and) adhidaivatam (including all demigods like the sun and moon) adhiyajnah (the Supersoul) aham (I (Krishna)) eva (certainly) atra (in this) dehe (body) deha-bhrtam (of the embodied) vara (the Supreme.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (314, 8.005, 'Brahma-Yoga', 314, 'antakale ch mam eva smaran ', 'muktva kalevaram', 'yah prayati sa madbhavam ', 'yati nasty atra samsayah', '', '', 'anta-kale (at the end of life) ch (also) mam (unto Me) eva (certainly) smaran (remembering) muktva (quitting) kalevaram (the body) yah (he who) prayati (goes) sah (he) mat-bhavam (My nature) yati (achieves) na (not) asti (there is) atra (here) samsayah (doubt.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (315, 8.006, 'Brahma-Yoga', 315, 'yam yam vapi smaran bhavam ', 'tyajaty ante kalevaram', 'tam tam evaiti kaunteya ', 'sada tadbhavabhavitah ', '', '', 'yam yam (whatever) va (either) api (also) smaran (remembering) bhavam (nature) tyajati (give up) ante (at the end) kalevaram (this body) tam tam (similar) eva (certainly) eti (gets) kaunteya (O son of Kunti) sada (always) tat (that) bhava (state of being) bhavitah (remembering.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (316, 8.007, 'Brahma-Yoga', 316, 'tasmat sarvesu kalesu ', 'mam anusmara yudhya ch', 'mayy arpitamanobuddhir ', 'mam evaisyasy asamsayah', '', '', 'tasmat (therefore) sarvesu (always) kalesu (time) mam (unto Me) anusmara (go on remembering) yudhya (fight) ch (also) mayi (unto Me) arpita (surrender) manah (mind) buddhih (intellect) mam (unto Me) eva (surely) esyasi (will attain) asamsayah (beyond a doubt.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (317, 8.008, 'Brahma-Yoga', 317, 'abhyasayogayuktena cetasa ', 'nanyagamina', 'paramam purusam divyam ', 'yati parthanucintayan ', '', '', 'abhyasa (practice) yoga-yuktena (being engaged in meditation) cetasa (by the mind and intelligence) na anya-gamina (without their being deviated) paramam (the Supreme) purusam (Personality of Godhead) divyam (transcendental) yati (achieves) partha (O son of Prtha) anucintayan (constantly thinking of.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (318, 8.009, 'Brahma-Yoga', 318, 'kavim puranam anusasitaram', 'anor aniyamsam anusmared yah', 'sarvasya dhataram acintyarupam', 'adityavarnam tamasah parastat ', '', '', 'kavim (one who knows everything) puranam (the oldest) anusasitaram (the controller) anoh (of the atom) aniyamsam (smaller than) anusmaret (always thinking) yah (one who) sarvasya (of everything) dhataram (the maintainer) acintya (inconceivable) rupam (form) aditya-varnam (illuminated like the sun) tamasah (of the darkness) parastat (transcendental.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (319, 8.010, 'Brahma-Yoga', 319, 'prayanakale manasacalena', 'bhaktya yukto yogabalena caiva', 'bhruvor madhye pranam avesya samyak', 'sa tam param purusam upaiti divyam ', '', '', 'prayana-kale (at the time of death) manasa (by the mind) acalena (without being deviated) bhaktya (in full devotion) yuktah (engaged) yoga-balena (by the power of mystic yoga) ch (also) eva (certainly) bhruvoh (between the two eyebrows) madhye (in) pranam (the life air) avesya (establishing) samyak (completely) sah (he) tam (that) param (transcendental) purusam (Personality of Godhead) upaiti (achieves) divyam (in the spiritual kingdom.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (320, 8.011, 'Brahma-Yoga', 320, 'yad aksaram vedavido vadanti', 'visanti yad yatayo vitaragah', 'yad icchanto brahmacaryam caranti', 'tat te padam samgrahena pravaksye ', '', '', 'yat (that which) aksaram (inexhaustible) veda-vidah (a person conversant with the Vedas) vadanti (say) visanti (enters) yat (in which) yatayah (great sages) vita-ragah (in the renounced order of life) yat (that which) icchantah (desiring) brahmacaryam (celibacy) caranti (practices) tat (that) te (unto you) padam (situation) sangrahena (in summary) pravaksye (I shall explain.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (321, 8.012, 'Brahma-Yoga', 321, 'sarvadvarani samyamya ', 'mano hrdi nirudhya ch', 'murdhny adhayatmanah pranam asthito', 'yogadharanam ', '', '', 'sarva-dvarani (all the doors of the body) samyamya (controlling) manah (mind) hrdi (in the heart) nirudhya (confined) ch (also) murdhni (on the head) adhaya (fixed) atmanah (soul) pranam (the life air) asthitah (situated) yoga-dharanam (the yogic situation.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (322, 8.013, 'Brahma-Yoga', 322, 'om ity ekaksaram brahma ', 'vyaharan mam anusmaran', 'yah prayati tyajan deham sa ', 'yati paramam gatim ', '', '', 'om (the combination of letters om (omkara)) iti (thus) eka-aksaram (supreme, indestructible) brahma (absolute) vyaharan (vibrating) mam (Me (Krishna)) anusmaran (remembering) yah (anyone) prayati (leaves) tyajan (quitting) deham (this body) sah (he) yati (achieves) paramam (supreme) gatim (destination.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (323, 8.014, 'Brahma-Yoga', 323, 'ananyacetah satatam yo mam ', 'smarati nityasah', 'tasyaham sulabhah partha ', 'nityayuktasya yoginah ', '', '', 'ananya-cetah (without deviation) satatam (always) yah (anyone) mam (Me (Krishna)) smarati (remembers) nityasah (regularly) tasya (to him) aham (I am) su-labhah (very easy to achieve) partha (O son of Prtha) nitya (regularly) yuktasya (engaged) yoginah (of the devotee.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (324, 8.015, 'Brahma-Yoga', 324, 'mam upetya punarjanma ', 'duhkhalayam asasvatam', 'napnuvanti mahatmanah ', 'samsiddhim paramam gatah', '', '', 'mam (unto Me) upetya (achieving) punah (again) janma (birth) duhkha-alayam (a place of miseries) asasvatam (temporary) na (never) apnuvanti (attain) maha-atmanah (the great souls) samsiddhim (perfection) paramam (ultimate) gatah (achieved.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (325, 8.016, 'Brahma-Yoga', 325, 'a brahmabhuvanal lokah ', 'punaravartinorjuna', 'mam upetya tu kaunteya ', 'punarjanma na vidyate ', '', '', 'abrahma (up to the Brahmaloka planet) bhuvanat (from the planetary systems) lokah (planets) punah (again) avartinah (returning) arjuna (O Arjuna) mam (unto Me) upetya (arriving) tu (but) kaunteya (O son of Kunti) punah janma (rebirth) na (never) vidyate (takes to.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (326, 8.017, 'Brahma-Yoga', 326, 'sahasrayugaparyantam ahar ', 'yad brahmano viduh', 'ratrim yugasahasrantam ', 'te ahoratravido janah ', '', '', 'sahasra (thousand) yuga (millenniums) paryantam (including) ahah (day) yat (that) brahmanah (of Brahma) viduh (they know) ratrim (night) yuga (millenniums) sahasra-antam (similarly, at the end of one thousand) te (that) ahah-ratra (day and night) vidah (understand) janah (people.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (327, 8.018, 'Brahma-Yoga', 327, 'avyaktad vyaktayah sarvah ', 'prabhavanty aharagame', 'ratryagame praliyante ', 'tatraivavyaktasamjnake ', '', '', 'avyaktat (from the unmanifest) vyaktayah (living entities) sarvah (all) prabhavanti (come into being) ahah-agame (at the beginning of the day) ratri-agame (at the fall of night) praliyante (are annihilated) tatra (there) eva (certainly) avyakta (the unmanifest) samjnake (called.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (328, 8.019, 'Brahma-Yoga', 328, 'bhutagramah sa evayam ', 'bhutva bhutva praliyate', 'ratryagamevasah partha ', 'prabhavaty aharagame ', '', '', 'bhuta-gramah (the aggregate of all living entities) sah (they) eva (certainly) ayam (this) bhutva bhutva (taking birth) praliyate (annihilate) ratri (night) agame (on arrival) avasah (automatically) partha (O son of Prtha) prabhavati (manifest) ahah (during daytime) agame (on arrival.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (329, 8.020, 'Brahma-Yoga', 329, 'paras tasmat tu ', 'bhavonyovyaktovyaktat sanatanah', 'yah sa sarvesu bhutesu ', 'nasyatsu na vinasyati ', '', '', 'parah (transcendental) tasmat (from that) tu (but) bhavah (nature) anyah (another) avyaktah (unmanifest) avyaktat (from the unmanifest) sanatanah (eternal) yah (that) sah (which) sarvesu (all) bhutesu (manifestation) nasyatsu (being annihilated) na (never) vinasyati (annihilated.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (330, 8.021, 'Brahma-Yoga', 330, 'avyaktoksara ity uktas ', 'tam ahuh paramam gatim', 'yam prapya na nivartante ', 'tad dhama paramam mama', '', '', 'avyaktah (unmanifested) aksarah (infallible) iti (thus) uktah (says) tam (that which) ahuh (is known) paramam (ultimate) gatim (destination) yam (that which) prapya (gaining) na (never) nivartante (comes back) tat dhama (that abode) paramam (supreme) mama (Mine.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (331, 8.022, 'Brahma-Yoga', 331, 'purusah sa parah partha ', 'bhaktya labhyas tv ananyaya', 'yasyantahsthani bhutani ', 'yena sarvam idam tatam ', '', '', 'purusah (the Supreme Personality) sah (He) parah (the Supreme, than whom no one is greater) partha (O son of Prtha) bhaktya (by devotional service) labhyah (can be achieved) tu (but) ananyaya (unalloyed, undeviating devotion) yasya (whom) antah-sthani (within) bhutani (all of this material manifestation) yena (by whom) sarvam (all) idam (whatever we can see) tatam (distributed.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (332, 8.023, 'Brahma-Yoga', 332, 'yatra kale tv anavrttim ', 'avrttim caiva yoginah', 'prayata yanti tam kalam ', 'vaksyami bharatarsabha ', '', '', 'yatra (in that) kale (time) tu (but) anavrttim (no return) avrttim (return) ch (also) eva (certainly) yoginah (of different kinds of mystics) prayatah (one who goes) yanti (departs) tam (that) kalam (time) vaksyami (describing) bharatarsabha (O best of the Bharatas.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (333, 8.024, 'Brahma-Yoga', 333, 'agnir jotir ahah suklah ', 'sanmasa uttarayanam', 'tatra prayata gacchanti ', 'brahma brahmavido janah', '', '', 'agnih (fire) jyotih (light) ahah (day) suklah (white) sat-masah (six months) uttara-ayanam (when the sun passes on the northern side) tatra (there) prayatah (one who goes) gacchanti (passes away) brahma (to the Absolute) brahma-vidah (one who knows the Absolute) janah (person.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (334, 8.025, 'Brahma-Yoga', 334, 'dhumo ratris tatha Krishnah ', 'sanmasa daksinayanam', 'tatra candramasam jyotir ', 'yogi prapya nivartate ', '', '', 'dhumah (smoke) ratrih (night) tatha (also) Krishnah (the fortnight of the dark moon) sat-masah (the six months) daksina-ayanam (when the sun passes on the southern side) tatra (there) candra-masam (the moon planet) jyotih (light) yogi (the mystic) prapya (achieves) nivartate (comes back.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (335, 8.026, 'Brahma-Yoga', 335, 'suklakrsne gati hy ete ', 'jagatah sasvate mate', 'ekaya yaty anavrttim ', 'anyayavartate punah ', '', '', 'sukla (light) krsne (darkness) gati (passing away) hi (certainly) ete (all these) jagatah (of the material world) sasvate (of the Vedas) mate (in the opinion) ekaya (by one) yati (goes) anavrttim (no return) anyaya (by the other) avartate (comes back) punah (again.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (336, 8.027, 'Brahma-Yoga', 336, 'naite srti partha janan ', 'yogi muhyati kascana', 'tasmat sarvesu kalesu ', 'yogayukto bhavarjuna ', '', '', 'na (never) ete (all these) srti (different paths) partha (O son of Prtha) janan (even if they know) yogi (the devotees of the Lord) muhyati (bewildered) kascana (anyone) tasmat (therefore) sarvesu kalesu (always) yoga-yuktah (being engaged in Krishna consciousness) bhava (just become) arjuna (O Arjuna.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (337, 8.028, 'Brahma-Yoga', 337, 'vedesu yajnesu tapahsu caiva', 'danesu yat punyaphalam pradistam', 'atyeti tat sarvam idam viditva', 'yogi param sthanam upaiti cadyam', '', '', 'vedesu (in the study of the Vedas) yajnesu (in the performances of yajna, sacrifice) tapahsu (undergoing different types of austerities) ch (also) eva (certainly) danesu (in giving charities) yat (that which) punya-phalam (the result of pious work) pradistam (directed) atyeti (surpasses) tat (all those) sarvam idam (all those described above) viditva (knowing) yogi (the devotee) param (supreme) sthanam (abode) upaiti (achieved peace) ch (also) adyam (original.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (338, 9.001, 'RajaVidyaRajaGuhya-Yoga', 338, 'sri-bhagavan uvach; श्रीभगवानुवाच', 'idam tu te guhyatamam pravaksyamy anasuyave', 'jnanam vijnanasahitam yaj ', 'jnatva moksyasesubhat ', '', '', 'sri-bhagavan uvach (The Lord says) idam (this) tu (but) te (unto you) guhya-tamam (the most confidential) pravaksyami (I am speaking) anasuyave (to the nonenvious) jnanam (knowledge) vijnana (realized knowledge) sahitam (with) yat (which) jnatva (knowing) moksyase (be released) asubhat (from this miserable material existence.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (339, 9.002, 'RajaVidyaRajaGuhya-Yoga', 339, 'rajavidya rajaguhyam ', 'pavitram idam uttamam', 'pratyaksavagamam dharmyam ', 'susukham kartum avyayam ', '', '', 'raja-vidya (the king of education) raja-guhyam (the king of confidential knowledge) pavitram (the purest) idam (this) uttamam (transcendental) pratyaksa (directly experienced) avagamam (understood) dharmyam (the principle of religion) su-sukham (very happy) kartum (to execute) avyayam (everlasting.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (340, 9.003, 'RajaVidyaRajaGuhya-Yoga', 340, 'asraddadhanah purusa ', 'dharmasyasya paramtapa', 'aprapya mam nivartante ', 'mrtyusamsaravartmani ', '', '', 'asraddadhanah (those who are faithless) purusah (such persons) dharmasya (of this process of religion) asya (of it) parantapa (O killer of the enemies) aprapya (without obtaining) mam (Me) nivartante (come back) mrtyu (death) samsara (material existence) vartmani (on the path of.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (341, 9.004, 'RajaVidyaRajaGuhya-Yoga', 341, 'maya tatam idam sarvam ', 'jagad avyaktamurtina', 'matsthani sarvabhutani ', 'na caham tesv avasthitah ', '', '', 'maya (by Me) tatam (spread) idam (all these manifestations) sarvam (all) jagat (cosmic manifestation) avyakta-murtina (unmanifested form) mat-sthani (unto Me) sarva-bhutani (all living entities) na (not) ch (also) aham (I) tesu (in them) avasthitah (situated.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (342, 9.005, 'RajaVidyaRajaGuhya-Yoga', 342, 'na ch matsthani bhutani ', 'pasya me yogam aisvaram', 'bhutabhrn na ch bhutastho ', 'mamatma bhutabhavanah', '', '', 'na (never) ch (also) mat-sthani (situated in Me) bhutani (all creation) pasya (just see) me (My) yogam aisvaram (inconceivable mystic power) bhuta-bhrt (maintainer of all living entities) na (never) ch (also) bhuta-sthah (in the cosmic manifestation) mama (My) atma (Self) bhuta-bhavanah (is the source of all manifestations.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (343, 9.006, 'RajaVidyaRajaGuhya-Yoga', 343, 'yathakasasthito nityam vayuh sarvatrago mahan', 'tatha sarvani bhutani matsthanity upadharaya ', 'sarvabhutani kaunteya prakrtim yanti mamikam', 'kalpaksaye punas tani kalpadau visrjamy aham ', '', '', 'yatha (as much as) akasa-sthitah (situated in space) nityam (always) vayuh (wind) sarvatra-gah (blowing everywhere) mahan (great) tatha (similarly) sarvani (everything) bhutani (created beings) mat-sthani (situated in Me) iti (thus) upadharaya (try to understand.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (344, 9.007, 'RajaVidyaRajaGuhya-Yoga', 344, 'prakrtim svam avastabhya ', 'visrjami punah punah', 'bhutagramam imam krtsnam ', 'avasam prakrter vasat ', '', '', 'sarva-bhutani (all created entities) kaunteya (O son of Kunti) prakrtim (nature) yanti (enter) mamikam (unto Me) kalpa-ksaye (at the end of the millennium) punah (again) tani (all those) kalpa-adau (in the beginning of the millennium) visrjami (I create) aham (I.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (345, 9.008, 'RajaVidyaRajaGuhya-Yoga', 345, 'na ch mam tani karmani ', 'nibadhnanti dhanamjaya', 'udasinavad asinam ', 'asaktam tesu karmasu ', '', '', 'prakrtim (material nature) svam (of My personal Self) avastabhya (enter in) visrjami (create) punah punah (again, again) bhuta-gramam (all these cosmic manifestations) imam (this) krtsnam (total) avasam (automatically) prakrteh (by the force of nature) vasat (under obligation.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (346, 9.009, 'RajaVidyaRajaGuhya-Yoga', 346, 'mayadhyaksena prakrtih ', 'suyate sacaracaram', 'hetunanena kaunteya ', 'jagad viparivartate ', '', '', 'na (never) ch (also) mam (Me) tani (all those) karmani (activities) nibadhnanti (bind) dhananjaya (O conqueror of riches) udasina-vat (as neutral) asinam (situated) asaktam (without attraction) tesu (in them) karmasu (in activities.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (347, 9.010, 'RajaVidyaRajaGuhya-Yoga', 347, 'avajananti mam mudha ', 'manusim tanum asritam', 'param bhavam ajananto ', 'mama bhutamahesvaram ', '', '', 'maya (by Me) adhyaksena (by superintendence) prakrtih (material nature) suyate (manifests) sa (with) cara-acaram (the moving and the nonmoving) hetuna (for this reason) anena (this) kaunteya (O son of Kunti) jagat (the cosmic manifestation) viparivartate (is working.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (348, 9.011, 'RajaVidyaRajaGuhya-Yoga', 348, 'moghasa moghakarmano ', 'moghajnana vicetasah', 'raksasim asurim caiva ', 'prakrtim mohinim sritah ', '', '', 'avajananti (deride) mam (Me) mudhah (foolish men) manusim (in human form) tanum (body) asritam (assuming) param (transcendental) bhavam (nature) ajanantah (not knowing) mama (Mine) bhuta (everything that be) maha-isvaram (the supreme proprietor.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (349, 9.012, 'RajaVidyaRajaGuhya-Yoga', 349, 'mahatmanas tu mam partha ', 'daivim prakrtim asritah', 'bhajanty ananyamanaso ', 'jnatva bhutadim avyayam', '', '', 'mogha-asah (baffled hope) mogha-karmanah (baffled in fruitive activities) mogha-jnanah (baffled in knowledge) vicetasah (bewildered) raksasim (demonic) asurim (atheistic) ch (and) eva (certainly) prakrtim (nature) mohinim (bewildering) sritah (taking shelter of.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (350, 9.013, 'RajaVidyaRajaGuhya-Yoga', 350, 'satatam kirtayanto mam ', 'yatantas ch drdhavratah', 'namasyantas ch mam ', 'bhaktya nityayukta upasate ', '', '', 'maha-atmanah (the great souls) tu (but) mam (unto Me) partha (O son of Prtha) daivim (divine) prakrtim (nature) asritah (taken shelter of) bhajanti (render service) ananya-manasah (without deviation of the mind) jnatva (knowing) bhuta (creation) adim (original) avyayam (inexhaustible.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (351, 9.014, 'RajaVidyaRajaGuhya-Yoga', 351, 'jnanayajnena capy anye ', 'yajanto mam upasate', 'ekatvena prthaktvena ', 'bahudha visvatomukham ', '', '', 'satatam (always) kirtayantah (chanting) mam (Me) yatantah ch (fully endeavoring also) drdha-vratah (with determination) namasyantah ch (offering obeisances) mam (unto Me) bhaktya (in devotion) nitya-yuktah (perpetually engaged) upasate (worship.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (352, 9.015, 'RajaVidyaRajaGuhya-Yoga', 352, 'aham kratur aham yajnah ', 'svadhaham aham ausadham', 'mantro aham aham evajyam ', 'aham agnir aham hutam', '', '', 'jnana-yajnena (by cultivation of knowledge) ch (also) api (certainly) anye (others) yajantah (worshiping) mam (Me) upasate (worship) ekatvena (in oneness) prthaktvena (in duality) bahudha (diversity) visvatah-mukham (in the universal form.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (353, 9.016, 'RajaVidyaRajaGuhya-Yoga', 353, 'pitaham asya jagato ', 'mata dhata pitamahah', 'vedyam pavitram omkara ', 'rk sama yajur eva ch ', '', '', 'aham (I) kratuh (ritual) aham (I) yajnah (sacrifice) svadha (oblation) aham (I) aham (I) ausadham (healing herb) mantrah (transcendental chant) aham (I) aham (I) eva (certainly) ajyam (melted butter) aham (I) agnih (fire) aham (I) hutam (offering.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (354, 9.017, 'RajaVidyaRajaGuhya-Yoga', 354, 'gatir bharta prabhuh saksi ', 'nivasah saranam suhrt', 'prabhavah pralayah sthanam ', 'nidhanam bijam avyam', '', '', 'pita (father) aham (I) asya (of this) jagatah (of the universe) mata (mother) dhata (supporter) pitamahah (grandfather) vedyam (what is to be known) pavitram (that which purifies) om-kara (the syllable om) rk (the Rg Veda) sama (the Sama Veda) yajuh (the Yajur Veda) eva (certainly) ch (and.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (355, 9.018, 'RajaVidyaRajaGuhya-Yoga', 355, 'tapamy aham aham varsam ', 'nigrnhamy utsrjami ch', 'amrtam caiva mrtyus ch ', 'sad asac caham arjuna ', '', '', 'gatih (goal) bharta (sustainer) prabhuh (Lord) saksi (witness) nivasah (abode) saranam (refuge) su-hrt (most intimate friend) prabhavah (creation) pralayah (dissolution) sthanam (ground) nidhanam (resting place) bijam (seed) avyayam (imperishable.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (356, 9.019, 'RajaVidyaRajaGuhya-Yoga', 356, 'traividya mam somapah putapapa', 'yajnair istva svargatim prarthayante', 'te punyam asadya surendralokam', 'asnanti divyan divi devabhogan ', '', '', 'tapami (give heat) aham (I) aham (I) varsam (rain) nigrhnami (withhold) utsrjami (send forth) ch (and) amrtam (immortality) ch (and) eva (certainly) mrtyuh (death) ch (and) sat (being) asat (nonbeing) ch (and) aham (I) arjuna (O Arjuna.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (357, 9.020, 'RajaVidyaRajaGuhya-Yoga', 357, 'te tam bhuktva svargalokam visalam', 'ksine punye martyalokam visanti', 'evam trayidharmam anuprapanna', 'gatagatam kamakama labhante ', '', '', 'trai-vidyah (the knowers of the three Vedas) mam (unto Me) soma-pah (drinkers of soma juice) puta (purified) papah (sins) yajnaih (with sacrifices) istva (after worshiping) svah-gatim (passage to heaven) prarthayante (pray) te (they) punyam (virtue) asadya (enjoying) sura-indra (of Indra) lokam (the world) asnanti (enjoy) divyan (celestial) divi (in heaven) deva-bhogan (pleasures of the gods.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (358, 9.021, 'RajaVidyaRajaGuhya-Yoga', 358, 'ananyas cintayanto mam ', 'ye janah paryupasate', 'tesam nityabhiyuktanam ', 'yogaksemam vahamy aham', '', '', 'te (they) tam (that) bhuktva (enjoying) svarga-lokam (heaven) visalam (vast) ksine (being exhausted) punye (merits) martya-lokam (mortal earth) visanti (fall down) evam (thus) trayi (three Vedas) dharmam (doctrines) anuprapannah (following) gata-agatam (death and birth) kama-kamah (desiring sense enjoyments) labhante (attain.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (359, 9.022, 'RajaVidyaRajaGuhya-Yoga', 359, 'yepy anyadevatabhakta ', 'yajante sraddhayanvitah', 'tepi mam eva kaunteya ', 'yajanty avidhipurvakam', '', '', 'ananyah (no other) cintayantah (concentrating) mam (unto Me) ye (who) janah (persons) paryupasate (properly worship) tesam (their) nitya (always) abhiyuktanam (fixed in devotion) yoga-ksemam (requirements) vahami (carry) aham (I.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (360, 9.023, 'RajaVidyaRajaGuhya-Yoga', 360, 'aham hi sarvayajnanam ', 'bhokta ch prabhur eva ch', 'na tu mam abhijananti ', 'tattvenatas cyavanti te ', '', '', 'ye (those) api (also) anya (other) devata (demigods) bhaktah (devotees) yajante (worship) sraddhaya anvitah (with faith) te (they) api (also) mam (Me) eva (even) kaunteya (O son of Kunti) yajanti (sacrifice) avidhi-purvakam (in a wrong way.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (361, 9.024, 'RajaVidyaRajaGuhya-Yoga', 361, 'yanti devavrata devan ', 'pitrn yanti pitrvratah', 'bhutani yanti bhutejya ', 'yanti madyajinopi mam ', '', '', 'aham (I) hi (surely) sarva (of all) yajnanam (sacrifices) bhokta (enjoyer) ch (and) prabhuh (Lord) eva (also) ch (and) na (not) tu (but) mam (Me) abhijananti (know) tattvena (in reality) atah (therefore) cyavanti (fall down) te (they.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (362, 9.025, 'RajaVidyaRajaGuhya-Yoga', 362, 'patram puspam phalam ', 'toyam yo me bhaktya', 'prayacchati tad aham ', 'bhaktyupahrtam asnami prayatatmanah', '', '', 'yanti (achieve) deva-vratah (worshipers of demigods) devan (to demigods) pitrn (to ancestors) yanti (go) pitr-vratah (worshipers of the ancestors) bhutani (to ghosts and spirits) yanti (go) bhuta-ijyah (worshipers of ghosts and spirits) yanti (go) mat (My) yajinah (devotees) api (also) mam (unto Me.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (363, 9.026, 'RajaVidyaRajaGuhya-Yoga', 363, 'yat karosi yad asnasi ', 'yaj juhosi dadasi yat', 'yat tapasyasi kaunteya ', 'tat kurusva madarpanam ', '', '', 'patram (a leaf) puspam (a flower) phalam (a fruit) toyam (water) yah (whoever) me (unto Me) bhaktya (with devotion) prayacchati (offers) tat (that) aham (I) bhakti-upahrtam (offered in devotion) asnami (accept) prayata-atmanah (of one in pure consciousness.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (364, 9.027, 'RajaVidyaRajaGuhya-Yoga', 364, 'subhasubhaphalair evam ', 'moksyase karmabandhanaih', 'samnyasayogayuktatma ', 'vimukto mam upaisyasi ', '', '', 'yat (whatever) karosi (you do) yat (whatever) asnasi (you eat) yat (whatever) juhosi (you offer) dadasi (you give away) yat (whatever) yat (whatever) tapasyasi (austerities you perform) kaunteya (O son of Kunti) tat (that) kurusva (make) mat (unto Me) arpanam (offering.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (365, 9.028, 'RajaVidyaRajaGuhya-Yoga', 365, 'samoham sarvabhutesu na me ', 'dvesyosti na priyah', 'ye bhajanti tu mam bhaktya ', 'mayi te tesu capy aham', '', '', 'subha (good) asubha (evil) phalaih (results) evam (thus) moksyase (free) karma (action) bandhanaih (bondage) sannyasa (of renunciation) yoga (the yoga) yukta-atma (having the mind firmly set on) vimuktah (liberated) mam (to Me) upaisyasi (you will attain.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (366, 9.029, 'RajaVidyaRajaGuhya-Yoga', 366, 'api cet suduracaro ', 'bhajate mam ananyabhak', 'sadhur eva sa mantavyah ', 'samyag vyavasito hi sah', '', '', 'samah (equally disposed) aham (I) sarva-bhutesu (to all living entities) na (no one) me (Mine) dvesyah (hateful) asti (is) na (nor) priyah (dear) ye (those) bhajanti (render transcendental service) tu (yet) mam (unto Me) bhaktya (in devotion) mayi (unto Me) te (such persons) tesu (in them) ch (also) api (certainly) aham (I.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (367, 9.030, 'RajaVidyaRajaGuhya-Yoga', 367, 'ksipram bhavati ', 'dharmatma sasvacchantim', 'nigacchhati kaunteya pratijanihi ', 'na me bhaktah pranasyati', '', '', 'api (in spite of) cet (although) su-duracarah (one committing the most abominable actions) bhajate (engaged in devotional service) mam (unto Me) ananya-bhak (without deviation) sadhuh (saint) eva (certainly) sah (he) mantavyah (to be considered) samyak (completely) vyavasitah (situated) hi (certainly) sah (he.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (368, 9.031, 'RajaVidyaRajaGuhya-Yoga', 368, 'mam hi partha vyapasritya ', 'yepi syuh papayonayah', 'striyo vaisyas tatha ', 'sudras tepi yanti param gatim', '', '', 'ksipram (very soon) bhavati (becomes) dharma-atma (righteous) sasvat-santim (lasting peace) nigacchati (attains) kaunteya (O son of Kunti) pratijanihi (justly declare) na (never) me (Mine) bhaktah (devotees) pranasyati (perishes.', NULL, NULL, 'admin');
