INSERT INTO gita VALUES (370, 9.033, 'RajaVidyaRajaGuhya-Yoga', 370, 'manmana bhava madbhakto ', 'madyaji mam namaskuru', 'mam evaisyasi yuktvaivam ', 'atmanam matparayanah', '', '', 'kim (how much) punah (again) brahmanah (brahmanas) punyah (righteous) bhaktah (devotees) raja-rsayah (saintly kings) tatha (also) anityam (temporary) asukham (sorrowful) lokam (planet) imam (this) prapya (gaining) bhajasva (are engaged in loving service) mam (unto Me.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (371, 9.034, 'RajaVidyaRajaGuhya-Yoga', 371, 'manmana bhava madbhakto ', 'madyaji mam namaskuru', 'mam evaisyasi yuktvaivam ', 'atmanam matparayanah', '', '', 'mat-manah (always thinking of Me) bhava (become) mat (My) bhaktah (devotee) mat (My) yaji (worshiper) mam (unto Me) namaskuru (offer obeisances) mam (unto Me) eva (completely) esyasi (come) yuktva evam (being absorbed) atmanam (your soul) mat-parayanah (devoted to Me.', NULL, NULL, 'admin');
INSERT INTO gita VALUES (372, 10.001, 'Vibhuti-Yoga', 372, 'sri-bhagavan uvach; श्रीभगवानुवाच', 'bhuya eva mahabaho srnu me paramam vacah', 'yat teham priyamanaya ', 'vaksyami hitakamyaya ', '', '', 'sri-bhagavan uvach (The Lord says) bhuyah (again) eva (certainly) maha-baho (O mighty-armed) srnu (just hear) me (My) paramam (supreme) vacah (information) yat (that which) te (to you) aham (I) priyamanaya (thinking you dear to Me) vaksyami (say) hita-kamyaya (for your benefit).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (373, 10.002, 'Vibhuti-Yoga', 373, 'na me viduh suraganah ', 'prabhavam na maharsayah', 'aham adir hi devanam ', 'maharsinam ch sarvasah ', '', '', 'na (never) me (My) viduh (knows) sura-ganah (demigods) prabhavam (opulences) na (never) maha-rsayah (great sages) aham (I am) adih (the origin) hi (certainly) devanam (of the demigods) maha-rsinam (of the great sages) ch (also) sarvasah (in all respects).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (374, 10.003, 'Vibhuti-Yoga', 374, 'yo mam ajam anadim ch ', 'vetti lokamahesvaram', 'asammudhah sa martyesu ', 'sarvapapaih pramucyate', '', '', 'yah (anyone who) mam (unto Me) ajam (unborn) anadim (without beginning) ch (also) vetti (knows) loka (the planets) maha-isvaram (the supreme master) asammudhah (without doubt) sah (he) martyesu (among those subject to death) sarva-papaih (from all sinful reactions) pramucyate (is delivered).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (375, 10.004, 'Vibhuti-Yoga', 375, 'buddhir jnanam asammohah ', 'ksama satyam damah samah', 'sukham duhkham bhavobhavo ', 'bhayam cabhayam eva ch ', '', '', 'buddhih (intelligence) jnanam (knowledge) asammohah (freedom from doubt) ksama (forgiveness) satyam (truthfulness) damah (control of the senses) samah (control of the mind) sukham (happiness) duhkham (distress) bhavah (birth) abhavah (death) bhayam (fear) ch (and) abhayam (without fear) eva (also) ch (and)', NULL, NULL, 'admin');
INSERT INTO gita VALUES (376, 10.005, 'Vibhuti-Yoga', 376, 'ahimsa samata tustis tapo ', 'danam yasoyasah', 'bhavanti bhava bhutanam ', 'matta eva prthagvidhah', '', '', 'ahimsa (nonviolence) samata (equilibrium) tustih (satisfaction) tapah (penance) danam (charity) yasah (fame) ayasah (infamy) bhavanti (become) bhavah (natures) bhutanam (of living entities) mattah (from Me) eva (certainly) prthak-vidhah (differently arranged).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (377, 10.006, 'Vibhuti-Yoga', 377, 'maharsayah sapta purve ', 'catvaro manavas tatha', 'madbhava manasa jata ', 'yesam loka imah prajah ', '', '', 'maha-rsayah (the great sages) sapta (seven) purve (before) catvarah (four) manavah (Manus) tatha (also) mat-bhavah (born of Me) manasah (from the mind) jatah (born) yesam (of them) loke (the planets) imah (all this) prajah (population).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (378, 10.007, 'Vibhuti-Yoga', 378, 'etam vibhutim yogam ch ', 'mama yo vetti tattvatah', 'sovikampena yogena ', 'yujyate natra samsayah ', '', '', 'etam (all this) vibhutim (opulence) yogam ch (also mystic power) mama (of Mine) yah (anyone) vetti (knows) tattvatah (factual) sah (he) avikalpena (without division) yogena (in devotional service) yujyate (engaged) na (never) atra (here) samsayah (doubt).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (379, 10.008, 'Vibhuti-Yoga', 379, 'aham sarvasya prabhavo ', 'mattah sarvam pravartate', 'iti matva bhajante mam ', 'budha bhavasamanvitah ', '', '', 'aham (I) sarvasya (of all) prabhavah (source of generation) mattah (from Me) sarvam (everything) pravartate (emanates) iti (thus) matva (knowing) bhajante (becomes devoted) mam (unto Me) budhah (learned) bhava-samanvitah (with great attention).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (380, 10.009, 'Vibhuti-Yoga', 380, 'maccitta madgataprana ', 'bodhayantah parasparam', 'kathayantas ch mam ', 'nityam tusyanti ch ramanti ch', '', '', 'mat-cittah (minds fully engaged in Me) mat-gata-pranah (lives devoted to the service of Krishna) bodhayantah (preaching) parasparam (among themselves) kathayantah ch (talking also) mam (about Me) nityam (perpetually) tusyanti (are pleased) ch (also) ramanti (enjoy transcendental bliss) ch (also).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (381, 10.010, 'Vibhuti-Yoga', 381, 'tesam satatayuktanam ', 'bhajatam pritipurvakam', 'dadami buddhiyogam ', 'tam yena mam upayanti te', '', '', 'tesam (unto them) satata-yuktanam (always engaged) bhajatam (in devotional service) priti-purvakam (in loving ecstasy) dadami (I give) buddhi-yogam (real intelligence) tam (that) yena (by which) mam (unto Me) upayanti (come) te (they).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (382, 10.011, 'Vibhuti-Yoga', 382, 'tesam evanukampartham ', 'aham ajnanajam tamah', 'nasayamy atmabhavastho ', 'jnanadipena bhasvata', '', '', 'tesam (for them) eva (certainly) anukampa-artham (to show special mercy) aham (I) ajnana-jam (due to ignorance) tamah (darkness) nasayami (dispel) atma (within) bhavasthah (themselves) jnana (of knowledge) dipena (with the lamp) bhasvata (glowing).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (383, 10.012, 'Vibhuti-Yoga', 383, 'arjuna uvach; अर्जुन उवाच', 'param brahma param dhama ', 'pavitram paramam bhavan', 'purusam sasvatam divyam adidevam ajam vibhum', '', '', 'arjunah uvach (Arjuna says) param (supreme) brahma (truth) param (supreme) dhama (sustenance) pavitram (purest) paramam (supreme) bhavan (Yourself) purusam (personality) sasvatam (original) divyam (transcendental) adi-devam (original Lord) ajam (unborn) vibhum (greatest)', NULL, NULL, 'admin');
INSERT INTO gita VALUES (384, 10.013, 'Vibhuti-Yoga', 384, 'ahus tvam rsayah sarve ', 'devarsir naradas tatha', 'asito devalo vyasah ', 'svayam caiva bravisi me ', '', '', 'ahuh (say) tvam (unto You) rsayah (sages) sarve (all) deva-rsih (the sage among the demigods) naradah (Narada) tatha (also) asitah (Asita) devalah (Devala) vyasah (Vyasa) svayam (personally) ch (also) eva (certainly) bravisi (explaining) me (unto me).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (385, 10.014, 'Vibhuti-Yoga', 385, 'sarvam etad rtam manye ', 'yan mam vadasi kesava', 'na hi te bhagavan vyaktim ', 'vidur deva na danavah', '', '', 'sarvam (all) etat (these) rtam (truths) manye (accept) yat (which) mam (unto me) vadasi (You tell) kesava (O Krishna) na (never) hi (certainly) te (Your) bhagavan (O Personality of Godhead) vyaktim (revelation) viduh (can know) devah (the demigods) na (nor) danavah (the demons).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (386, 10.015, 'Vibhuti-Yoga', 386, 'svayam evatmanatmanam ', 'vettha tvam purusottama', 'bhutabhavana bhutesa ', 'devadeva jagatpate ', '', '', 'svayam (personality) eva (certainly) atmana (by Yourself) atmanam (Yourself) vettha (know) tvam (You) purusa-uttama (O greatest of all persons) bhuta-bhavana (O origin of everything) bhuta-isa (O Lord of everything) deva-deva (O Lord of all demigods) jagat-pate (O Lord of the entire universe).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (387, 10.016, 'Vibhuti-Yoga', 387, 'vaktum arhasy asesena ', 'divya hy atmavibhutayah', 'yabhir vibhutibhir lokan ', 'imams tvam vyapya tisthasi', '', '', 'vaktum (to say) arhasi (deserve) asesena (in detail) divyah (divine) hi (certainly) atma (Yourself) vibhutayah (opulences) yabhih (by which) vibhutibhih (opulences) lokan (all the planets) iman (these) tvam (You) vyapya (pervading) tisthasi (remain).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (388, 10.017, 'Vibhuti-Yoga', 388, 'katham vidyam aham ', 'yogims tvam sada paricintayan', 'kesu kesu ch bhavesu ', 'cintyosi bhagavan maya ', '', '', 'katham (how) vidyam aham (shall I know) yogin (O supreme mystic) tvam (You) sada (always) paricintayan (thinking) kesu (in which) kesu (in which) ch (also) bhavesu (nature) cintyah asi (You are remembered) bhagavan (O Supreme) maya (by me).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (389, 10.018, 'Vibhuti-Yoga', 389, 'vistarenatmano yogam ', 'vibhutim ch janardana', 'bhuyah kathaya trptir hi ', 'srnvato nasti memrtam ', '', '', 'vistarena (in description) atmanah (of Yourself) yogam (mystic power) vibhutim (opulences) ch (also) jana-ardana (O killer of the atheists) bhuyah (again) kathaya (describe) trptih (satisfaction) hi (certainly) srnvatah (hearing) na asti (there is not) me (my) amrtam (nectar).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (390, 10.019, 'Vibhuti-Yoga', 390, 'sri-bhagavan uvach; श्रीभगवानुवाच', 'hanta te kathayisyami ', 'divya hy atmavibhutayah', 'pradhanyatah kurusrestha nasty anto vistarasya me', '', '', 'sri-bhagavan uvach (The Lord says) hanta (yes) te (unto you) kathayisyami (I shall speak) divyah (divine) hi (certainly) atma-vibhutayah (personal opulences) pradhanyatah (principally) kuru-srestha (O best of the Kurus) na asti (there is no) antah (limit) vistarasya (to the extent) me (My).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (391, 10.020, 'Vibhuti-Yoga', 391, 'aham atma gudakesa ', 'sarvabhutasayasthitah', 'aham adis ch madhyam ch ', 'bhutanam anta eva ch', '', '', 'aham (I) atma (soul) gudakesa (O Arjuna) sarva-bhuta (all living entities) asaya-sthitah (situated within) aham (I am) adih (origin) ch (also) madhyam (middle) ch (also) bhutanam (all living entities) antah (end) eva (certainly) ch (and).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (392, 10.021, 'Vibhuti-Yoga', 392, 'adityanam aham visnur ', 'jyotisam ravir amsuman', 'maricir marutam asmi ', 'naksatranam aham sasi ', '', '', 'adityanam (of the Adityas) aham (I am) visnuh (the Supreme Lord) jyotisam (of all luminaries) ravih (the sun) amsu-man (radiant) maricih (Marici) marutam (of the Maruts) asmi (I am) naksatranam (of stars) aham (I am) sasi (the moon).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (393, 10.022, 'Vibhuti-Yoga', 393, 'vedanam samavedosmi ', 'devanam asmi vasavah', 'indriyanam manas casmi ', 'bhutanam asmi cetana ', '', '', 'vedanam (of all the Vedas) sama-vedah (the Sama Veda) asmi (I am) devanam (of all the demigods) asmi (I am) vasavah (heavenly king) indriyanam (of all the senses) manah (the mind) ch (also) asmi (I am) bhutanam (of all living entities) asmi (I am) cetana (the living force).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (394, 10.023, 'Vibhuti-Yoga', 394, 'rudranam samkaras casmi ', 'vitteso yaksaraksasam', 'vasunam pavakas casmi ', 'meruh sikharinam aham ', '', '', 'rudranam (of all the Rudras) sankarah (Lord Siva) ch (also) asmi (I am) vitta-isah (the lord of the treasury of the demigods) yaksa-raksasam (of the Yaksas and Raksasas) vasunam (of the Vasus) pavakah (fire) ch (also) asmi (I am) meruh (Meru) sikharinam (of all mountains) aham (I am).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (395, 10.024, 'Vibhuti-Yoga', 395, 'purodhasam ch mukhyam ', 'mam viddhi partha brhaspatim', 'senaninam aham skandah ', 'sarasam asmi sagarah ', '', '', 'purodhasam (of all priests) ch (also) mukhyam (chief) mam (Me) viddhi (understand) partha (O son of Prtha) brhaspatim (Brhaspati) senaninam (of all commanders) aham (I am) skandah (Kartikeya) sarasam (of all reservoirs of water) asmi (I am) sagarah (the ocean).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (396, 10.025, 'Vibhuti-Yoga', 396, 'maharsinam bhrgur aham ', 'giram asmy ekam aksaram', 'yajnanam japayajnosmi ', 'sthavaranam himalayah ', '', '', 'maha-rsinam (among the great sages) bhrguh (Bhrgu) aham (I am) giram (of vibrations) asmi (I am) ekam aksaram (pranava) yajnanam (of sacrifices) japa-yajnah (chanting) asmi (I am) sthavaranam (of immovable things) himalayah (the Himalayan mountains).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (397, 10.026, 'Vibhuti-Yoga', 397, 'asvatthah sarvavrksanam ', 'devarsinam ch naradah', 'gandharvanam citrarathah ', 'siddhanam kapilo munih', '', '', 'asvatthah (the banyan tree) sarva-vrksanam (of all trees) deva-rsinam (of all the sages amongst the demigods) ch (and) naradah (Narada) gandharvanam (the citizens of the Gandharva planet) citrarathah (Citraratha) siddhanam (of all those who are perfected) kapilah munih (Kapila Muni).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (398, 10.027, 'Vibhuti-Yoga', 398, 'uccaihsravasam asvanam ', 'viddhi mam amrtodbhavam', 'airavatam gajendranam ', 'naranam ch naradhipam ', '', '', 'uccaihsravasam (Uccaihsrava) asvanam (among horses) viddhi (know) mam (Me) amrta-udbhavam (produced from the churning of the ocean) airavatam (Airavata) gaja-indranam (of elephants) naranam (among human beings) ch (and) nara-adhipam (the king).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (399, 10.028, 'Vibhuti-Yoga', 399, 'ayudhanam aham vajram ', 'dhenunam asmi kamadhuk', 'prajanas casmi kandarpah ', 'sarpanam asmi vasukih', '', '', 'ayudhanam (of all weapons) aham (I am) vajram (the thunderbolt) dhenunam (of cows) asmi (I am) kama-dhuk (the surabhi cows) prajanah (for begetting children) ch (and) asmi (I am) kandarpah (Cupid) sarpanam (of all snakes) asmi (I am) vasukih (Vasuki).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (400, 10.029, 'Vibhuti-Yoga', 400, 'anantas casmi naganam ', 'varuno yadasam aham', 'pitrnam aryama casmi ', 'yamah samyamatam aham', '', '', 'anantah (Ananta) ch (also) asmi (I am) naganam (of all serpents) varunah (the demigod controlling the water) yadasam (of all aquatics) aham (I am) pitrnam (of the ancestors) aryama (Aryama) ch (also) asmi (I am) yamah (the controller of death) samyamatam (of all regulators) aham (I am).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (401, 10.030, 'Vibhuti-Yoga', 401, 'pralhadas casmi daityanam ', 'kalah kalayatam aham', 'mrganam ch mrgendroham ', 'vainateyas ch paksinam', '', '', 'prahladah (Prahlada) ch (also) asmi (I am) daityanam (of the demons) kalah (time) kalayatam (of subduers) aham (I am) mrganam (of animals) ch (and) mrga-indrah (the lion) aham (I am) vainateyah (Garuda) ch (also) paksinam (of birds).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (402, 10.031, 'Vibhuti-Yoga', 402, 'pavanah pavatam asmi ', 'ramah sastrabhrtam aham', 'jhasanam makaras casmi ', 'strotasam asmi jahnavi ', '', '', 'pavanah (the wind) pavatam (of all that purifies) asmi (I am) ramah (Rama) sastra-bhrtam (of the carriers of weapons) aham (I am) jhasanam (of all aquatics) makarah (shark) ch asmi (I am also) srotasam (of flowing rivers) asmi (I am) jahnavi (the River Ganges).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (403, 10.032, 'Vibhuti-Yoga', 403, 'sarganam adir antas ch ', 'madhyam caivaham arjuna', 'adhyatmavidya vidyanam ', 'vadah pravadatam aham', '', '', 'sarganam (of all creations) adih (beginning) antah (end) ch (and) madhyam (middle) ch (also) eva (certainly) aham (I am) arjuna (O Arjuna) adhyatma-vidya (spiritual knowledge) vidyanam (of all education) vadah (natural conclusion) pravadatam (of arguments) aham (I am).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (404, 10.033, 'Vibhuti-Yoga', 404, 'aksaranam akarosmi ', 'dvandvah samasikasya ch', 'aham evaksayah kalo ', 'dhataham visvatomukhah ', '', '', 'aksaranam (of letters) akarah (the first) asmi (I am) dvandvah (dual) samasikasya (compounds) ch (and) aham (I am) eva (certainly) aksayah (eternal) kalah (time) dhata (creator) aham (I am) visvatah-mukhah (Brahma).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (405, 10.034, 'Vibhuti-Yoga', 405, 'mrtyuh sarvaharas caham ', 'udbhavas ch bhavisyatam', 'kirtih srir vak ch narinam ', 'smrtir medha dhrtih ksama', '', '', 'mrtyuh (death) sarva-harah (all-devouring) ch (also) aham (I am) udbhavah (generation) ch (also) bhavisyatam (of the future) kirtih (fame) srih vak (beautiful speech) ch (also) narinam (of women) smrtih (memory) medha (intelligence) dhrtih (faithfulness) ksama (patience).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (406, 10.035, 'Vibhuti-Yoga', 406, 'brhatsama tatha samnam ', 'gayatri chandasam aham', 'masanam margasirsoham ', 'rtunam kusumakarah ', '', '', 'brhat-sama (the Brhat-sama) tatha (also) samnam (of the Sama Veda songs) gayatri (the Gayatri hymns) chandasam (of all poetry) aham (I am) masanam (of months) marga-sirsah (the month of November-December) aham (I am) rtunam (of all seasons) kusuma-akarah (spring).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (407, 10.036, 'Vibhuti-Yoga', 407, 'dyutam chalayatam asmi ', 'tejas tejasvinam aham', 'jayosmi vyavasayosmi ', 'sattvam sattvavatam aham', '', '', 'dyutam (gambling) chalayatam (of all cheats) asmi (I am) tejah (splendid) tejasvinam (of everything splendid) aham (I am) jayah (victory) asmi (I am) vyavasayah (adventure) asmi (I am) sattvam (strength) sattva-vatam (of all the strong) aham (I am).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (408, 10.037, 'Vibhuti-Yoga', 408, 'vrsninam vasudevosmi ', 'pandavanam dhanamjayah', 'muninam apy aham vyasah ', 'kavinam usana kavih ', '', '', 'vrsninam (of the descendants of Vrsni) vasudevah (Krishna in Dvaraka) asmi (I am) pandavanam (of the Pandavas) dhananjayah (Arjuna) muninam (of the sages) api (also) aham (I am) vyasah (Vyasa, the compiler of all Vedic literature) kavinam (of all great thinkers) usana (Usana) kavih (the thinker).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (409, 10.038, 'Vibhuti-Yoga', 409, 'dando damayatam asmi ', 'nitir asmi jigisatam', 'maunam caivasmi ', 'guhyanam jnanam jnanavatam aham ', '', '', 'dandah (punishment) damayatam (of all separation) asmi (I am) nitih (morality) asmi (I am) jigisatam (of the victorious) maunam (silence) ch (and) eva (also) asmi (I am) guhyanam (of secrets) jnanam (knowledge) jnana-vatam (of the wise) aham (I am).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (410, 10.039, 'Vibhuti-Yoga', 410, 'yac capi sarvabhutanam ', 'bijam tad aham arjuna', 'na tad asti vina yat ', 'syan maya bhutam caracaram', '', '', 'yat (whatever) ch (also) api (may be) sarva-bhutanam (of all creations) bijam (the seed) tat (that) aham (I am) arjuna (O Arjuna) na (not) tat (that) asti (there is) vina (without) yat (that) syat (exists) maya (by Me) bhutam (created) cara-acaram (moving and unmoving).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (411, 10.040, 'Vibhuti-Yoga', 411, 'nantosti mama divyanam ', 'vibhutinam paramtapa', 'esa tuddesatah prokto ', 'vibhuter vistaro maya ', '', '', 'na (nor) antah (a limit) asti (is there) mama (My) divyanam (divine) vibhutinam (opulences) parantapa (O conqueror of the enemies) esah (all this) tu (that) uddesatah (examples) proktah (spoken) vibhuteh (opulences) vistarah (expanded) maya (by Me).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (412, 10.041, 'Vibhuti-Yoga', 412, 'yad yad vibhutimat sattvam ', 'srimad urjitam eva va', 'tat tad evavagaccha tvam ', 'mama tejomsasambhavam', '', '', 'yat yat (whatever) vibhuti (opulences) mat (having) sattvam (existence) sri-mat (beautiful) urjitam (glorious) eva (certainly) va (or) tat tat (all those) eva (certainly) avagaccha (you must know) tvam (you) mama (My) tejah (splendor) amsa (partly) sambhavam (born of).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (413, 10.042, 'Vibhuti-Yoga', 413, 'athava bahunaitena kim ', 'jnatena tavarjuna', 'vistabhyaham idam krtsnam ', 'ekamsena sthito jagat', '', '', 'atha va (or) bahuna (many) etena (by this kind) kim (what) jnatena (knowing) tava (you) arjuna (O Arjuna) vistabhya (entire) aham (I) idam (this) krtsnam (of all manifestations) eka (one) amsena (part) sthitah (situated) jagat (in the universe).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (414, 11.001, 'VisvaRupa Darsana-Yoga', 414, 'arjuna uvach; अर्जुन उवाच', 'madanugrahaya paramam', 'guhyam adhyatmasamjnitam', 'yat tvayoktam vacas tena ', 'mohoyam vigato mama ', '', 'arjunah uvach (Arjuna says) mat-anugrahaya (just show me favor) paramam (supreme) guhyam (confidential) adhyatma (spiritual) samjnitam (in the matter of) yat (what) tvaya (by You) uktam (says) vacah (words) tena (by that) mohah (illusion) ayam (this) vigatah (is educated) mama (my).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (415, 11.002, 'VisvaRupa Darsana-Yoga', 415, 'bhavapyayau hi bhutanam ', 'srutau vistaraso maya', 'tvattah kamalapatraksa ', 'mahatmyam api cavyayam', '', '', 'bhava (appearance) apyayau (disappearance) hi (certainly) bhutanam (of all living entities) srutau (have heard) vistarasah (detail) maya (by me) tvattah (from You) kamala-patra-aksa (O lotus-eyed one) mahatmyam (glories) api (also) ch (and) avyayam (inexhaustible).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (416, 11.003, 'VisvaRupa Darsana-Yoga', 416, 'evam etad yathattha tvam ', 'atmanam paramesvara', 'drastum icchami te rupam ', 'aisvaram purusottama ', '', '', 'evam (that) etat (this) yatha (as it is) attha (have spoken) tvam (You) atmanam (the soul) parama-isvara (the Supreme Lord) drastum (to see) icchami (I wish) te (You) rupam (form) aisvaram (divine) purusa-uttama (O best of personalities).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (417, 11.004, 'VisvaRupa Darsana-Yoga', 417, 'manyase yadi tac chakyam ', 'maya drastum iti prabho', 'yogesvara tato me ', 'tvam darsayatmanam avyayam ', '', '', 'manyase (if You think) yadi (if) tat (that) sakyam (able to see) maya (by me) drastum (to see) iti (thus) prabho (O Lord) yoga-isvara (O Lord of all mystic power) tatah (then) me (unto me) tvam (You) darsaya (show) atmanam (Yourself) avyayam (eternal).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (418, 11.005, 'VisvaRupa Darsana-Yoga', 418, 'sri-bhagavan uvach; श्रीभगवानुवाच', 'pasya me partha rupani ', 'satasotha sahastrasah', 'nanavidhani divyani ', 'nanavarnakrtini ch ', '', 'sri-bhagavan uvach (The Lord says) pasya (just see) me (Mine) partha (O son of Prtha) rupani (forms) satasah (hundreds) atha (also) sahasrasah (thousands) nana-vidhani (variegated) divyani (divine) nana (variegated) varna (colors) akrtini (forms) ch (also).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (419, 11.006, 'VisvaRupa Darsana-Yoga', 419, 'pasyadityan vasun rudran ', 'asvinau marutas tatha', 'bahuny adrstapurvani ', 'pasyascaryani bharata ', '', '', 'pasya (see) adityan (the twelve sons of Aditi) vasun (the eight Vasus) rudran (the eleven forms of Rudra) asvinau (the two Asvinis) marutah (the forty-nine Maruts (demigods of the wind)) tatha (also) bahuni (many) adrsta (that you have not head or seen) purvani (before) pasya (there see) ascaryani (all the wonderful) bharata (O best of the Bharatas).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (420, 11.007, 'VisvaRupa Darsana-Yoga', 420, 'ihaikastham jagat krtsnam ', 'pasyadya sacaracaram', 'mama dehe gudakesa yac ', 'canyad drastum icchasi ', '', '', 'iha (in this) eka-stham (in one) jagat (the universe) krtsnam (completely) pasya (see) adya (immediately) sa (with) cara (moving) acaram (not moving) mama (My) dehe (in this body) gudakesa (O Arjuna) yat (that) ch (also) anyat (other) drastum (to see) icchasi (you like).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (421, 11.008, 'VisvaRupa Darsana-Yoga', 421, 'na tu mam sakyase drastum ', 'anenaiva svacaksusa', 'divyam dadami te caksuh ', 'pasya me yogam aisvaram ', '', '', 'na (never) tu (but) mam (Me) sakyase (able) drastum (to see) anena (by this) eva (certainly) sva-caksusa (with your own eyes) divyam (divine) dadami (I give) te (you) caksuh (eyes) pasya (see) me (My) yogam aisvaram (inconceivable mystic power).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (422, 11.009, 'VisvaRupa Darsana-Yoga', 422, 'sanjaya uvach; संजय उवाच', 'evam uktva tato rajan ', 'mahayogesvaro harih', 'darsayam asa parthaya ', 'paramam rupam aisvaram ', '', 'sanjayah uvach (Sanjaya says) evam (thus) uktva (saying) tatah (thereafter) rajan (O King) maha-yoga-isvarah (the most powerful mystic) harih (the Supreme Personality of Godhead, Krishna) darsayam asa (showed) parthaya (unto Arjuna) paramam (divine) rupam (universal form) aisvaram (opulences).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (423, 11.010, 'VisvaRupa Darsana-Yoga', 423, 'anekavaktranayanam ', 'anekadbhutadarsanam', 'anekadivyabharanam ', 'divyanekodyatayudham ', '', '', 'aneka (various) vaktra (mouths) nayanam (eyes) aneka (various) adbhuta (wonderful) darsanam (sights) aneka (many) divya (divine) abharanam (ornaments) divya (divine) aneka (various) udyata (uplifted) ayudham (weapons) ', NULL, NULL, 'admin');
INSERT INTO gita VALUES (424, 11.011, 'VisvaRupa Darsana-Yoga', 424, 'divyamalyambaradharam ', 'divyagandhanulepanam', 'sarvascaryamayam devam ', 'anantam visvatomukham', '', '', 'divya (divine) malya (garlands) ambara dharam (covered with the dresses) divya (divine) gandha (fragrance) anulepanam (smeared) sarva (all) ascarya-mayam (wonderful) devam (shining) anantam (unlimited) visvatah-mukham (all-pervading).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (425, 11.012, 'VisvaRupa Darsana-Yoga', 425, 'divi suryasahastrasya ', 'bhaved yugapad utthita', 'yadi bhah sadrsi sa syad ', 'bhasas tasya mahatmanah', '', '', 'divi (in the sky) surya (sun) sahasrasya (of many thousands) bhavet (there were) yugapat (simultaneously) utthita (present) yadi (if) bhah (light) sadrsi (like that) sa (that) syat (may be) bhasah (effulgence) tasya (there is) maha-atmanah (of the great Lord).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (426, 11.013, 'VisvaRupa Darsana-Yoga', 426, 'tatraikastham jagat krtsnam ', 'pravibhaktam anekadha', 'apasyad devadevasya ', 'sarire pandavas tada ', '', '', 'tatra (there) eka-stham (one place) jagat (universe) krtsnam (completely) pravibhaktam (divided in) anekadha (many kinds) apasyat (could see) deva-devasya (of the Supreme Personality of Godhead) sarire (in the universal form) pandavah (Arjuna) tada (at that time).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (427, 11.014, 'VisvaRupa Darsana-Yoga', 427, 'tatah sa vismayavisto ', 'hrstaroma dhanamjayah', 'pranamya sirasa devam ', 'krtanjalir abhasata ', '', '', 'tatah (thereafter) sah (he) vismaya-avistah (being overwhelmed with wonder) hrsta-roma (with his bodily hairs standing on end due to his great ecstasy) dhananjayah (Arjuna) pranamya (offering obeisances) sirasa (with the head) devam (to the Supreme Personality of Godhead) krta-anjalih (with folded hands) abhasata (began to say).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (428, 11.015, 'VisvaRupa Darsana-Yoga', 428, 'arjuna uvach; अर्जुन उवाच', 'pasyami devams tava deva dehe', 'sarvams tatha bhutavisesasamghan', 'brahmanam isam kamalasanastham', 'rsims ch sarvan uragams ch divyan ', '', 'arjunah uvach (Arjuna says) pasyami (I see) devan (all the demigods) tava (Your) deva (O Lord) dehe (in the body) sarvan (all) tatha (also) bhuta (living entities) visesa-sanghan (specifically assembled) brahmanam (Lord Brahma) isam (Lord Siva) kamala-asana-stham (sitting on the lotus flower) rsin (great sages) ch (also) sarvan (all) uragan (serpents) ch (also) divyan (divine).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (429, 11.016, 'VisvaRupa Darsana-Yoga', 429, 'anekabahudaravaktranetram', 'pasyami tvam sarvatonantarupam', 'nantam na madhyam na punas tavadim', 'pasyami visvesvara visvarupa ', '', '', 'aneka (many) bahu (arms) udara (bellies) vaktra (mouths) netram (eyes) pasyami (I see) tvam (unto You) sarvatah (from all sides) ananta-rupam (unlimited form) na antam (there is no end) na madhyam (there is no middle) na punah (nor again) tava (Your) adim (beginning) pasyami (I see) visva-isvara (O Lord of the universe) visva-rupa (in the form of the universe).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (430, 11.017, 'VisvaRupa Darsana-Yoga', 430, 'kiritinam gadinam cakrinam ch', 'tejorasim sarvato diptimantam', 'pasyami tvam durniriksyam samantad', 'diptanalarkadyutim aprameyam ', '', '', 'kiritinam (with helmets) gadinam (with maces) cakrinam (with discs) ch (and) tejah-rasim (effulgence) sarvatah (all sides) dipti-mantam (glowing) pasyami (I see) tvam (You) durniriksyam (difficult to see) samantat (spreading) dipta-anala (blazing fire) arka (sun) dyutim (sunshine) aprameyam (immeasurable).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (431, 11.018, 'VisvaRupa Darsana-Yoga', 431, 'tvam aksaram paramam veditavyam', 'tvam asya visvasya param nidhanam', 'tvam avyayah sasvatadharmagopta', 'sanatanas tvam puruso mato me ', '', '', 'tvam (You) aksaram (inexhaustible) paramam (supreme) veditavyam (to be understood) tvam (You) asya (of this) visvasya (of the universe) param (supreme) nidhanam (basis) tvam (You are) avyayah (inexhaustible) sasvata-dharma-gopta (maintainer of the eternal religion) sanatanah (eternal) tvam (You) purusah (Supreme Personality) matah me (is my opinion).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (432, 11.019, 'VisvaRupa Darsana-Yoga', 432, 'anadimadhyantam anantaviryam', 'anantabahum sasisuryanetram', 'pasyami tvam diptahutasavaktram', 'svatejasa visvam idam tapantam ', '', '', 'anadi (without beginning) madhya (without middle) antam (without end) ananta (unlimited) viryam (glorious) ananta (unlimited) bahum (arms) sasi (moon) surya (sun) netram (eyes) pasyami (I see) tvam (You) dipta (blazing) hutasa-vaktram (fire coming out of Your mouth) sva-tejasa (by Your) visvam (this universe) idam (this) tapantam (heating).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (433, 11.020, 'VisvaRupa Darsana-Yoga', 433, 'dyavaprthivyor idam antaram hi', 'vyaptam tvayaikena disas ch sarvah', 'drstvadbhutam rupam ugram tavedam', 'lokatrayam pravyathitam mahatman ', '', '', 'dyau (in outer space) a-prthivyoh (of the earth) idam (this) antaram (in between) hi (certainly) vyaptam (pervaded) tvaya (by You) ekena (by one) disah (directions) ch (and) sarvah (all) drstva (by seeing) adbhutam (wonderful) rupam (form) ugram (terrible) tava (Your) idam (this) loka (planetary system) trayam (three) pravyathitam (perturbed) maha-atman (O great one).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (434, 11.021, 'VisvaRupa Darsana-Yoga', 434, 'ami hi tvam surasamgha visanti', 'kecid bhitah pranjalayo grnanti', 'svastity uktva maharsisiddhasamghah', 'stuvanti tvam stutibhih puskalabhih ', '', '', 'ami (all those) hi (certainly) tvam (unto You) sura-sanghah (groups of demigods) visanti (entering) kecit (some of them) bhitah (out of fear) pranjalayah (with folded hands) grnanti (offering prayers unto) svasti (all peace) iti (thus) uktva (speaking like that) maha-rsi (great sages) siddha-sanghah (perfect sages) stuvanti (singing hymns) tvam (unto You) stutibhih (with prayers) puskalabhih (Vedic hymns).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (435, 11.022, 'VisvaRupa Darsana-Yoga', 435, 'rudraditya vasavo ye ch sadhya', 'visvesvinau marutas cosmapas ch', 'gandharvayaksasurasiddhasamgha', 'viksante tvam vismitas caiva sarve ', '', '', 'rudra (manifestations of Lord Siva) adityah (the Adityas) vasavah (the Vasus) ye (all those) ch (and) sadhyah (the Sadhyas) visve (the Visvedevas) asvinau (the Asvini-kumaras) marutah (the Maruts) ch (and) usma-pah (the forefathers) ch (and) gandharva (of the Gandharvas) yaksa (the Yaksas) asura-siddha (the demons and the perfected demigods) sanghah (assemblies) viksante (are seeing) tvam (You) vismitah (in wonder) ch (also) eva (certainly) sarve (all).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (436, 11.023, 'VisvaRupa Darsana-Yoga', 436, 'rupam mahat te bahuvaktranetram', 'mahabaho bahubahurupadam', 'bahudaram bahudanstrakaralam', 'drstva lokah pravyathitas tathaham ', '', '', 'rupam (form) mahat (very great) te (of You) bahu (many) vaktra (faces) netram (eyes) maha-baho (O mighty-armed one) bahu (many) bahu (arms) uru (thighs) padam (legs) bahu-udaram (many bellies) bahu-damstra (many teeth) karalam (horrible) drstva (seeing) lokah (all the planets) pravyathitah (perturbed) tatha (similarly) aham (I).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (437, 11.024, 'VisvaRupa Darsana-Yoga', 437, 'nabhahsprsam diptam anekavarnam', 'vyattananam diptavisalanetram', 'drstva hi tvam pravyathitantaratma', 'dhrtim na vindami samam ch visno ', '', '', 'nabhah-sprsam (touching the sky) diptam (glowing) aneka (many) varnam (color) vyatta (open) ananam (mouth) dipta (glowing) visala (very great) netram (eyes) drstva (by seeing) hi (certainly) tvam (You) pravyathita (perturbed) antah (within) atma (soul) dhrtim (steadiness) na (no) vindami (and have) samam (mental tranquility) ch (also) visno (O Lord Visnu).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (438, 11.025, 'VisvaRupa Darsana-Yoga', 438, 'damstrakaralani ch te mukhani', 'drstvaiva kalanalasamnibhani', 'diso na jane na labhe ch sarma', 'prasida devesa jagannivasa ', '', '', 'damstra (teeth) karalani (like that) ch (also) te (Your) mukhani (faces) drstva (seeing) eva (thus) kala-anala (the fire of death) sannibhani (as if blazing) disah (directions) na jane (do not know) na labhe (nor obtain) ch sarma (and grace) prasida (be pleased) deva-isa (O Lord of all lords) jagat-nivasa (refuge of the worlds).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (439, 11.026, 'VisvaRupa Darsana-Yoga', 439, 'ami ch tvam dhrtarastrasya putrah', 'sarve sahaivavanipalasamghaih', 'bhismo dronah sutaputras tathasau', 'sahasmadiyair api yodhamukhyaih ', '', '', 'ami (all those) ch (also) tvam (You) dhrtarastrasya (of Dhrtarastra) putrah (sons) sarve (all) saha eva (along with) avani-pala (warrior kings) sanghaih (the groups) bhismah (Bhismadeva) dronah (Dronacarya) suta-putrah (Karna) tatha (also) asau (that) saha (with) asmadiyaih (our) api (also) yodha-mukhyaih (chief among the warriors)', NULL, NULL, 'admin');
INSERT INTO gita VALUES (440, 11.027, 'VisvaRupa Darsana-Yoga', 440, 'vaktrani te tvaramana visanti', 'damstrakaralani bhayanakani', 'kecid vilagna dasanantaresu', 'samdrsyante curnitair uttamangaih ', '', '', 'vaktrani (mouths) te (Your) tvaramanah (fearful) visanti (entering) damstra (teeth) karalani (terrible) bhayanakani (very fearful) kecit (some of them) vilagnah (being attacked) dasana-antaresu (between the teeth) sandrsyante (being seen) curnitaih (smashed) uttama-angaih (by the head).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (441, 11.028, 'VisvaRupa Darsana-Yoga', 441, 'yatha nadinam bahavombuvegah', 'samudram evabhimukha dravanti', 'tatha tavami naralokavira', 'visanti vaktrany abhivijvalanti ', '', '', 'yatha (as) nadinam (of the rivers) bahavah (many) ambu-vegah (waves of the waters) samudram (ocean) eva (certainly) abhimukhah (towards) dravanti (gliding) tatha (similarly) tava (Your) ami (all those) nara-loka-virah (the kings of human society) visanti (entering) vaktrani (into the mouths) abhivijvalanti (blazing).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (442, 11.029, 'VisvaRupa Darsana-Yoga', 442, 'yatha pradiptam jvalanam patanga', 'visanti nasaya samrddhavegah', 'tathaiva nasaya visanti lokas', 'tavapi vaktrani samrddhavegah ', '', '', 'yatha (as) pradiptam (blazing) jvalanam (fire) patangah (moths) visanti (enters) nasaya (destruction) samrddha (full) vegah (speed) tatha eva (similarly) nasaya (for destruction) visanti (entering) lokah (all people) tava (unto You) api (also) vaktrani (in the mouths) samrddha-vegah (with full speed).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (443, 11.030, 'VisvaRupa Darsana-Yoga', 443, 'lelihyase grasamanah samantal', 'lokan samagran vadanair jvaladbhih', 'tejobhir apurya jagat samagram', 'bhasas tavograh pratapanti visno ', '', '', 'lelihyase (licking) grasamanah (devouring) samantat (from all directions) lokan (people) samagran (completely) vadanaih (by the mouth) jvaladbhih (with blazing) tejobhih (by effulgence) apurya (covering) jagat (the universe) samagram (all) bhasah (illuminating) tava (Your) ugrah (terrible) pratapanti (scorching) visno (O all-pervading Lord).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (444, 11.031, 'VisvaRupa Darsana-Yoga', 444, 'akhyahi me ko bhavan ugrarupo', 'namostu te devavara prasida', 'vijnatum icchami bhavantam adyam', 'na hi prajanami tava pravrttim ', '', '', 'akhyahi (please explain) me (unto me) kah (who) bhavan (You) ugra-rupah (fierce form) namah astu (obeisances) te (unto You) deva-vara (the great one amongst the demigods) prasida (be gracious) vijnatum (just to know) icchami (I wish) bhavantam (You) adyam (the original) na (never) hi (certainly) prajanami (do I know) tava (Your) pravrttim (mission).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (445, 11.032, 'VisvaRupa Darsana-Yoga', 445, 'sri-bhagavan uvach; श्रीभगवानुवाच', 'kalosmi lokaksayakrt pravrddho', 'lokan samahartum iha pravrttah', 'rtepi tvam na bhavisyanti sarve', 'yevasthitah pratyanikesu yodhah ', '', 'sri-bhagavan uvach (the Personality of Godhead says) kalah (time) asmi (I am) loka (the worlds) ksaya-krt (destroyer) pravrddhah (to engage) lokan (all people) samahartum (to destroy) iha (in this world) pravrttah (to engage) rte api (without even) tvam (you) na (never) bhavisyanti (will be) sarve (all) ye (who) avasthitah (situated) prati-anikesu (on the opposite side) yodhah (the soldiers).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (446, 11.033, 'VisvaRupa Darsana-Yoga', 446, 'tasmat tvam uttistha yaso labhasva', 'jitva satrun bhunksva rajyam samrddham', 'mayaivaite nihatah purvam eva', 'nimittamatram bhava savyasacin ', '', '', 'tasmat (therefore) tvam (you) uttistha (get up) yasah (fame) labhasva (gain) jitva (conquering) satrun (enemies) bhunksva (enjoy) rajyam (kingdom) samrddham (flourishing) maya (by Me) eva (certainly) ete (all these) nihatah (already killed) purvam eva (by previous arrangement) nimitta-matram (just the cause) bhava (become) savya-sacin (O Savyasaci).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (447, 11.034, 'VisvaRupa Darsana-Yoga', 447, 'dronam ch bhismam ch jayadratham ch', 'karnam tathanyan api yodhaviran', 'maya hatams tvam jahi ma vyathistha', 'yudhyasva jetasi rane sapatnan ', '', '', 'dronam ch (also Drona) bhismam ch (also Bhisma) jayadratham ch (also Jayadratha) karnam (also Karna) tatha (also) anyan (others) api (certainly) yodha-viran (great warriors) maya (by Me) hatan (already killed) tvam (you) jahi (become victorious) ma (never) vyathisthah (be disturbed) yudhyasva (just fight) jeta asi (just conquer) rane (in the fight) sapatnan (enemies).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (448, 11.035, 'VisvaRupa Darsana-Yoga', 448, 'sanjaya uvach; संजय उवाच', 'etac chrutva vacanam kesavasya', 'krtanjalir vepamanah kiriti', 'namaskrtva bhuya evaha Krishnam', 'sagadgadam bhitabhitah pranamya ', '', 'sanjayah uvach (Sanjaya says) etat (thus) srutva (hearing) vacanam (speech) kesavasya (of Krishna) krta-anjalih (with folded hands) vepamanah (trembling) kiriti (Arjuna) namaskrtva (offering obeisances) bhuyah (again) eva (also) aha Krishnam (says unto Krishna) sa-gadgadam (faltering) bhita-bhitah (fearful) pranamya (offering obeisances).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (449, 11.036, 'VisvaRupa Darsana-Yoga', 449, 'arjuna uvach; अर्जुन उवाच', 'sthane hrsikesa tava prakirtya', 'jagat prahrsyaty anurajyate ch', 'raksamsi bhitani diso dravanti', 'sarve namasyanti ch siddhasamghah ', '', 'arjunah uvach (Arjuna says) sthane (rightly) hrsika-isa (O master of all senses) tava (Your) prakirtya (glories) jagat (the entire world) prahrsyati (rejoicing) anurajyate (becoming attached) ch (and) raksamsi (the demons) bhitani (out of fear) disah (directions) dravanti (fleeing) sarve (all) namasyanti (offering respect) ch (also) siddha-sanghah (the perfect human beings).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (450, 11.037, 'VisvaRupa Darsana-Yoga', 450, 'kasmac ch te na nameran mahatman', 'gariyase brahmanopy adikartre', 'ananta devesa jagannivasa', 'tvam aksaram sad asat tatparam yat ', '', '', 'kasmat (why) ch (also) te (unto You) na (not) nameran (offer proper obeisances) maha-atman (O great one) gariyase (You are better than) brahmanah (Brahma) api (although) adi-kartre (the supreme creator) ananta (unlimited) deva-isa (God of the gods) jagat-nivasa (O refuge of the universe) tvam (You are) aksaram (imperishable) sat-asat (cause and effect) tat param (transcendental) yat (because).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (451, 11.038, 'VisvaRupa Darsana-Yoga', 451, 'tvam adidevah purusah puranas', 'tvam asya visvasya param nidhanam', 'vettasi vedyam ch param ch dhama', 'tvaya tatam visvam anantarupa ', '', '', 'tvam (You) adi-devah (the original Supreme God) purusah (personality) puranah (old) tvam (You) asya (this) visvasya (universe) param (transcendental) nidhanam (refuge) vetta (knower) asi (You are) vedyam ch (and the knowable) param ch (and transcendental) dhama (refuge) tvaya (by You) tatam (pervaded) visvam (universe) ananta-rupa (unlimited form).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (452, 11.039, 'VisvaRupa Darsana-Yoga', 452, 'vayur yamognir varunah sasankah', 'prajapatis tvam prapitamahas ch', 'namo namas testu sahastrakrtvah', 'punas ch bhuyopi namo namas te ', '', '', 'vayuh (air) yamah (controller) agnih (fire) varunah (water) sasa-ankah (moon) prajapatih (Brahma) tvam (You) prapitamahah (grandfather) ch (also) namah (offering respects) namah te (again my respects unto You) astu (are being) sahasra-krtvah (a thousand times) punah ch (and again) bhuyah (again) api (also) namah (offer my respects) namah te (offering my respects unto You).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (453, 11.040, 'VisvaRupa Darsana-Yoga', 453, 'namah purastad atha prsthatas te', 'namostu te sarvata eva sarva', 'anantaviryamitavikramas tvam', 'sarvam samapnosi tatosi sarvah ', '', '', 'namah (offering obeisances) purastat (from the front) atha (also) prsthatah (from behind) te (You) namah astu (offer my respects) te (unto You) sarvatah (from all sides) eva sarva (because You are everything) ananta-virya (unlimited potency) amita-vikramah (unlimited force) tvam (You) sarvam (everything) samapnosi (cover) tatah asi (therefore You are) sarvah (everything).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (454, 11.041, 'VisvaRupa Darsana-Yoga', 454, 'sakheti matva prasabham yad uktam', 'he Krishna he yadava he sakheti', 'ajanata mahimanam tavedam', 'maya pramadat pranayena vapi ', '', '', 'sakha (friend) iti (thus) matva (thinking) prasabham (temporary) yat (whatever) uktam (says) he Krishna (O Krishna) he yadava (O Yadava) he sakhe iti (O my dear friend) ajanata (without knowing) mahimanam (glories) tava (Your) idam (this) maya (by me) pramadat (out of foolishness) pranayena (out of love) va api (either)', NULL, NULL, 'admin');
INSERT INTO gita VALUES (455, 11.042, 'VisvaRupa Darsana-Yoga', 455, 'yac cavahasartham asatkrtosi', 'viharasayyasanabhojanesu', 'ekothavapy acyuta tatsamaksam', 'tat ksamaye tvam aham aprameyam ', '', '', 'yat (whatever) ch (also) avahasa-artham (for joking) asat-krtah (dishonor) asi (have been done) vihara (in relaxation) sayya (in joking) asana (in a resting place) bhojanesu (or while eating together) ekah (alone) atha va (or) api (others) acyuta (O infallible one) tat-samaksam (as Your competitor) tat (all those) ksamaye (excuse) tvam (from You) aham (I) aprameyam (immeasurable).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (456, 11.043, 'VisvaRupa Darsana-Yoga', 456, 'pitasi lokasya caracarasya', 'tvam asya pujyas ch gurur gariyan', 'na tvatsamosty abhyadhikah kutonyo', 'lokatrayepy apratimaprabhava ', '', '', 'pita (father) asi (You are) lokasya (of all the world) cara (moving) acarasya (nonmoving) tvam (You are) asya (of this) pujyah (worshipable) ch (also) guruh (master) gariyan (glorious) na (never) tvat-samah (equal to You) asti (there is) abhyadhikah (greater) kutah (how is it possible) anyah (other) loka-traye (in three planetary systems) api (also) apratima (immeasurable) prabhava (power).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (457, 11.044, 'VisvaRupa Darsana-Yoga', 457, 'tasmat pranamya pranidhaya kayam', 'prasadaye tvam aham isam idyam', 'piteva putrasya sakheva sakhyuh', 'priyah priyayarhasi deva sodhum ', '', '', 'tasmat (therefore) pranamya (after offering obeisances) pranidhaya (laying down) kayam (body) prasadaye (to beg mercy) tvam (unto You) aham (I) isam (unto the Supreme Lord) idyam (who is worshipable) pita iva (like a father) putrasya (of a son) sakha iva (like a friend) sakhyuh (of a friend) priyah (lover) priyayah (of the dearmost) arhasi (You should) deva (my Lord) sodhum (tolerate).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (458, 11.045, 'VisvaRupa Darsana-Yoga', 458, 'adrstapurvam hrsitosmi drstva', 'bhayena ch pravyathitam mano me', 'tad eva me darsaya deva rupam', 'prasida devesa jagannivasa ', '', '', 'adrsta-purvam (never seen before) hrsitah (gladdened) asmi (I am) drstva (by seeing) bhayena (out of fear) ch (also) pravyathitam (perturbed) manah (mind) me (my) tat (therefore) eva (certainly) me (unto me) darsaya (show) deva (O Lord) rupam (the form) prasida (just be gracious) deva-isa (O Lord of lords) jagat-nivasa (the refuge of the universe).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (459, 11.046, 'VisvaRupa Darsana-Yoga', 459, 'kiritinam gadinam cakrahastam', 'icchami tvam drastum aham tathaiva', 'tenaiva rupena caturbhujena', 'sahastrabaho bhava visvamurte ', '', '', 'kiritinam (with helmet) gadinam (with club) cakra-hastam (disc in hand) icchami (I wish) tvam (You) drastum (to see) aham (I) tatha eva (in that position) tena eva (by that) rupena (with form) catuh-bhujena (four-handed) sahasra-baho (O thousand-handed one) bhava (just become) visva-murte (O universal form).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (460, 11.047, 'VisvaRupa Darsana-Yoga', 460, 'sri-bhagavan uvach; श्रीभगवानुवाच', 'maya prasannena tavarjunedam', 'rupam param darsitam atmayogat', 'tejomayam visvam anantam adyam', 'yan me tvadanyena na drstapurvam ', '', 'sri-bhagavan uvach (The Lord says) maya (by Me) prasannena (happily) tava (unto you) arjuna (O Arjuna) idam (this) rupam (form) param (transcendental) darsitam (shown) atma-yogat (by My internal potency) tejah-mayam (full of effulgence) visvam (the entire universe) anantam (unlimited) adyam (original) yat me (that which is Mine) tvat anyena (besides you) na drsta-purvam (no one has previously seen).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (461, 11.048, 'VisvaRupa Darsana-Yoga', 461, 'na veda yajnadhyayanair na danaih', 'na ch kriyabhir na tapobhir ugraih', 'evamrupah sakya aham nrloke', 'drastum tvadanyena kurupravira ', '', '', 'na (never) veda (Vedic study) yajna (sacrifice) adhyayanaih (study) na danaih (by charity) na (never) ch (also) kriyabhih (by pious activities) na tapobhih (by serious penances) ugraih (severe) evam (thus) rupah (form) sakyah (can be seen) aham (I) nr-loke (in this material world) drastum (to see) tvat (you) anyena (by another) kuru-pravira (O best among the Kuru warriors).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (462, 11.049, 'VisvaRupa Darsana-Yoga', 462, 'ma te vyatha ma ch vimudhabhavo', 'drstva rupam ghoram idrn mamedam', 'vyapetabhih pritamanah punas tvam', 'tad eva me rupam idam prapasya ', '', '', 'ma (let it not be) te (unto you) vyatha (trouble) ma (let it not be) ch (also) vimudha-bhavah (bewilderment) drstva (by seeing) rupam (form) ghoram (horrible) idrk (like this) mama (My) idam (as it is) vyapeta-bhih (just become free from all fear) prita-manah (be pleased in mind) punah (again) tvam (you) tat (that) eva (thus) me (My) rupam (form) idam (this) prapasya (just see).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (463, 11.050, 'VisvaRupa Darsana-Yoga', 463, 'sanjaya uvach; संजय उवाच', 'ity arjunam vasudevas tathoktva', 'svakam rupam darsayam asa bhuyah', 'asvasayam asa ch bhitam enam', 'bhutva punah saumyavapur mahatma ', '', 'sanjayah uvach (Sanjaya says) iti (thus) arjunam (unto Arjuna) vasudevah (Krishna) tatha (that way) uktva (saying) svakam (His own) rupam (form) darsayam asa (showed) bhuyah (again) asvasayam asa (also convinced him) ch (also) bhitam (fearful) enam (him) bhutva punah (becoming again) saumya-vapuh (beautiful form) maha-atma (the great one).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (464, 11.051, 'VisvaRupa Darsana-Yoga', 464, 'arjuna uvach; अर्जुन उवाच', 'drstvedam manusam rupam tava ', 'saumyam janardana', 'idanim asmi samvrttah sacetah ', 'prakrtim gatah ', '', 'arjunah uvach (Arjuna says) drstva (seeing) idam (this) manusam (human being) rupam (form) tava (Your) saumyam (very beautiful) janardana (O chastiser of the enemies) idanim (just now) asmi (I am) samvrttah (settled) sa-cetah (in my consciousness) prakrtim (my own) gatah (I am).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (465, 11.052, 'VisvaRupa Darsana-Yoga', 465, 'sri-bhagavan uvach; श्रीभगवानुवाच', 'sudurdarsam idam rupam ', 'drstvan asi yan mama', 'deva apy asya rupasya ', 'nityam darsanakanksinah ', '', 'sri-bhagavan uvach (The Lord says) su-durdarsam (very difficult to be seen) idam (this) rupam (form) drstavan asi (as you have seen) yat (which) mama (of Mine) devah (the demigods) api asya (also this) rupasya (form) nityam (eternally) darsana-kanksinah (always aspire to see).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (466, 11.053, 'VisvaRupa Darsana-Yoga', 466, 'naham vedair na tapasa ', 'na danena na cejyaya', 'sakya evamvidho drastum ', 'drstavan asi mam yatha', '', '', 'na (never) aham (I) vedaih (by study of the Vedas) na (never) tapasa (by serious penances) na (never) danena (by charity) na (never) ch (also) ijyaya (by worship) sakyah (it is possible) evam-vidhah (like this) drastum (to see) drstavan (seeing) asi (you are) mam (Me) yatha (as).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (467, 11.054, 'VisvaRupa Darsana-Yoga', 467, 'bhaktya tv ananyaya sakya ', 'aham evamvidhorjuna', 'jnatum drastum ch tattvena ', 'pravestum ch paramtapa', '', '', 'bhaktya (by devotional service) tu (but) ananyaya (without being mixed with fruitive activities or speculative knowledge) sakyah (possible) aham (I) evam-vidhah (like this) arjuna (O Arjuna) jnatum (to know) drastum (to see) ch (and) tattvena (in fact) pravestum (and to enter into) ch (also) parantapa (O mighty-armed one).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (468, 11.055, 'VisvaRupa Darsana-Yoga', 468, 'matkarmakrn matparamo ', 'madbhaktah sangavarjitah', 'nirvairah sarvabhutesu yah ', 'sa mam eti pandava ', '', '', 'mat-karma-krt (engaged in doing My work) mat-paramah (considering Me the Supreme) mat-bhaktah (engaged in My devotional service) sanga-varjitah (freed from the contamination of previous activities and mental speculation) nirvairah (without an enemy) sarva-bhutesu (to every living entity yah (one who) sah (he) mam (unto Me) eti (comes) pandava (O son of Pandu).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (469, 12.001, 'BhaktiYoga', 469, 'arjuna uvach; अर्जुन उवाच', 'evam satatayukta ye ', 'bhaktas tvam paryupasate', 'ye capy aksaram avyaktam ', 'tesam ke yogavittamah', '', 'arjunah uvach (Arjuna says) evam (thus) satata (always) yuktah (engaged) ye (those) bhaktah (devotees) tvam (unto You) paryupasate (properly worship) ye (those) ch (also) api (again) aksaram (beyond the senses) avyaktam (unmanifested) tesam (of them) ke (who) yoga-vit-tamah (the most perfect).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (470, 12.002, 'BhaktiYoga', 470, 'sri-bhagavan uvach; श्रीभगवानुवाच', 'mayy avesya mano ye mam ', 'nityayukta upasate', 'sraddhaya parayopetah te ', 'me yuktatama matah ', '', 'sri-bhagavan uvach (The Lord says) mayi (unto Me) avesya (fixing) manah (mind) ye (one who) mam (unto Me) nitya (always) yuktah (engaged) upasate (worships) sraddhaya (with faith) paraya (transcendental) upetah (engages) te (they) me (Me) yukta-tamah (most perfect) matah (I consider).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (471, 12.003, 'BhaktiYoga', 471, 'ye tv aksaram anirdesyam ', 'avyaktam paryupasate', 'sarvatragam acintyam ch ', 'kutastham acalam dhruvam', '', '', 'ye (those) tu (but) aksaram (which is beyond the perception of the senses) anirdesyam (indefinite) avyaktam (unmanifested) paryupasate (completely engages) sarvatra-gam (all-pervading) acintyam (inconceivable) ch (also) kuta-stham (in the center) acalam (immovable) dhruvam (fixed)', NULL, NULL, 'admin');
INSERT INTO gita VALUES (472, 12.004, 'BhaktiYoga', 472, 'samniyamyendriyagramam ', 'sarvatra samabuddhayah', 'te prapnuvanti mam eva ', 'sarvabhutahite ratah ', '', '', 'sanniyamya (controlling) indriya-gramam (all the senses) sarvatra (everywhere) sama-buddhayah (equally disposed) te (they) prapnuvanti (achieve) mam (unto Me) eva (certainly) sarva-bhuta-hite (all living entities welfare) ratah (engaged).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (473, 12.005, 'BhaktiYoga', 473, 'klesodhikataras tesam ', 'avyaktasaktacetasam ', 'avyakta hi gatir duhkham ', 'dehavadbhir avapyate ', '', '', 'klesah (trouble) adhika-tarah (more troublesome) tesam (of them) avyakta (to unmanifested) asakta (being attached) cetasam (of those whose minds) avyakta (toward the unmanifested) hi (certainly) gatih duhkham (progress is troublesome) deha-vadbhih (of the embodiments) avapyate (achieve).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (474, 12.006, 'BhaktiYoga', 474, 'ye tu sarvani karmani mayi ', 'samnyasya matparah', 'ananyenaiva yogena mam ', 'dhyayanta upasate ', '', '', 'ye (one who) tu (but) sarvani (everything) karmani (activities) mayi (unto Me) sannyasya (giving up) mat-parah (being attached to Me) ananyena (without division) eva (certainly) yogena (by practice of such bhakti-yoga) mam (unto Me) dhyayantah (meditating) upasate (worship)', NULL, NULL, 'admin');
INSERT INTO gita VALUES (475, 12.007, 'BhaktiYoga', 475, 'tesam aham samuddharta ', 'mrtyusamsarasagarat', 'bhavami na cirat partha ', 'mayy avesitacetasam ', '', '', 'tesam (of them) aham (I) samuddharta (deliverer) mrtyu (death) samsara (material existence) sagarat (from the ocean) bhavami (become) na cirat (not a long time) partha (O son of Prtha) mayi (unto Me) avesita (fixed) cetasam (of those whose minds are like that).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (476, 12.008, 'BhaktiYoga', 476, 'mayy eva mana adhatsva mayi ', 'buddhim nivesaya', 'nivasisyasi mayy eva ata ', 'urdhvam na samsayah ', '', '', 'mayi (unto Me) eva (certainly) manah (mind) adhatsva (fix) mayi (upon Me) buddhim (intelligence) nivesaya (apply) nivasisyasi (you live) mayi (unto Me) eva (certainly) atah urdhvam (thereafter) na (never) samsayah (doubt).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (477, 12.009, 'BhaktiYoga', 477, 'atha cittam samadhatum na ', 'saknosi mayi sthiram', 'abhyasayogena tato mam ', 'ichaptum dhanamjaya ', '', '', 'atha (if, therefore) cittam (mind) samadhatum (fixing) na (not) saknosi (able) mayi (upon Me) sthiram (fixed) abhyasa (practice) yogena (by devotional service) tatah (therefore) mam (Me) iccha (desire) aptum (to get) dhanam-jaya (O Arjuna).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (478, 12.010, 'BhaktiYoga', 478, 'abhyasepy asamarthosi ', 'matkarmaparamo bhava', 'madartham api karmani ', 'kurvan siddhim avapsyasi', '', '', 'abhyase (in practice of) api (even) asamarthah (unable) asi (you are) mat-karma (my work) paramah (supreme) bhava (you become) mat-artham (for my sake) api (even though) karmani (work) kurvan (performing) siddhim (perfection) avapsyasi (achieve).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (479, 12.011, 'BhaktiYoga', 479, 'athaitad apy asaktosi ', 'kartum madyogam asritah', 'sarvakarmaphalatyagam tatah ', 'kuru yatatmavan ', '', '', 'atha (even though) etat (this) api (also) asaktah (unable) asi (you are) kartum (to perform) mat (unto Me) yogam (devotional service) asritah (refuge) sarva-karma (all activities) phala (result) tyagam (renunciation) tatah (therefore) kuru (do) yata-atma-van (self-situated).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (480, 12.012, 'BhaktiYoga', 480, 'sreyo hi jnanam abhyasaj ', 'jnanad dhyanam visisyate', 'dhyanat karmaphalatyagas ', 'tyagac chantir anantaram', '', '', 'sreyah (better) hi (certainly) jnanam (knowledge) abhyasat (by practice) jnanat (better than knowledge) dhyanam (meditation) visisyate (especially considered) dhyanat (from meditation) karma-phala-tyagah (renunciation of the results of fruitive action) tyagat (by such renunciation) santih (peace) anantaram (thereafter).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (481, 12.013, 'BhaktiYoga', 481, 'advesta sarvabhutanam ', 'maitrah karuna eva ch', 'nirmamo nirahamkarah ', 'samaduhkhasukhah ksami', '', '', 'advesta (nonenvious) sarva-bhutanam (for all living entities) maitrah (friendly) karunah (kindly) eva (certainly) ch (also) nirmamah (with no sense of proprietorship) nirahankarah (without false ego) sama (equally) duhkha (distress) sukhah (happiness) ksami (forgiving)', NULL, NULL, 'admin');
INSERT INTO gita VALUES (482, 12.014, 'BhaktiYoga', 482, 'samtustah satatam yogi ', 'yatatma drdhaniscayah', 'mayy arpitamanobuddhir ', 'yo madbhaktah sa me priyah ', '', '', 'santustah (satisfied) satatam (always) yogi (engaged in devotion) yata-atma (self-controlled) drdha-niscayah (with determination) mayi (upon Me) arpita (engaged) manah (mind) buddhih (intelligence) yah (one who) mat-bhaktah (my devotee) sah me priyah (he is dear to Me).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (483, 12.015, 'BhaktiYoga', 483, 'yasman nodvijate loko ', 'lokan nodvijate ch yah', 'harsamarsabhayodvegair', 'mukto yah sa ch me priyah', '', '', 'yasmat (from whom) na (never) udvijate (agitates) lokah (persons) lokat (persons) na (never) udvijate (disturbed) ch (also) yah (anyone) harsa (happiness) amarsa (distress) bhaya (fear) udvegaih (with anxiety) muktah (freed) yah (who) sah (anyone) ch (also) me (mine) priyah (very dear).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (484, 12.016, 'BhaktiYoga', 484, 'anapeksah sucir daksa ', 'udasino gatavyathah', 'sarvarambhaparityagi yo ', 'madbhaktah sa me priyah', '', '', 'anapeksah (neutral) sucih (pure) daksah (expert) udasinah (free from care) gata-vyathah (freed from all distress) sarva-arambha (all endeavors) parityagi (renouncer) yah (anyone) mat-bhaktah (my devotee) sah (he) me (Me) priyah (very dear).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (485, 12.017, 'BhaktiYoga', 485, 'yo na hrsyati na dvesti ', 'na socati na kanksati', 'subhasubhaparityagi ', 'bhaktiman yah sa me priyah', '', '', 'yah (one who) na (never) hrsyati (takes pleasure) na (never) dvesti (grieves) na (never) socati (laments) na (never) kanksati (desires) subha (auspicious) asubha (inauspicious) parityagi (renouncer) bhakti-man (devotee) yah (one who) sah (he is) me (mine) priyah (dear).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (486, 12.018, 'BhaktiYoga', 486, 'samah satrau ch mitre ', 'ch tatha manapamanayoh', 'sitosnasukhaduhkhesu ', 'samah sangavivarjitah ', '', '', 'samah (equal) satrau (to the enemy) ch (also) mitre (to friends) ch (also) tatha (so) mana (honor) apamanayoh (dishonor) sita (cold) usna (heat) sukha (happiness) duhkhesu (distress) samah (equipoised) sanga-vivarjitah (free from all association)', NULL, NULL, 'admin');
INSERT INTO gita VALUES (487, 12.019, 'BhaktiYoga', 487, 'tulyanindastutir mauni ', 'samtusto yena kenacit', 'aniketah sthiramatir ', 'bhaktiman me priyo narah ', '', '', 'tulya (equal) ninda (defamation) stutih (repute) mauni (silent) santustah (satisfied) yena (somehow) kena (or other) cit (if) aniketah (having no residence) sthira (fixed) matih (determination) bhakti-man (engaged in devotion) me (my) priyah (dear) narah (a man).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (488, 12.020, 'BhaktiYoga', 488, 'ye tu dharmyamrtam idam ', 'yathoktam paryupasate', 'sraddadhana matparama ', 'bhaktas tetiva me priyah', '', '', 'ye (one who) tu (but) dharma (of religion) amrtam (nectar) idam (this) yatha (as) uktam (says) paryupasate (completely engage) sraddadhanah (with faith) mat-paramah (taking the Supreme Lord as everything) bhaktah (devotees) te (such persons) ativa (very, very) me (Me) priyah (dear).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (489, 13.001, 'Kshetra-Kshetrajna-Vibhaga-Yoga', 489, 'arjuna uvach; अर्जुन उवाच', 'prakrtim purusam caiva ', 'ksetram ksetrajnam eva ch', 'etad veditum icchami jnanam ', 'jneyam ch kesava ', '', 'arjunah uvach (Arjuna says) prakrtim (nature) purusam (the enjoyer) ch (also) eva (certainly) ksetram (body) ksetra-jnam (knower of the body) eva (certainly) ch (also) etat (all this) veditum (to understand) icchami (I wish) jnanam (knowledge) jneyam (the object of knowledge) ch (also) kesava (O Krishna)', NULL, NULL, 'admin');
INSERT INTO gita VALUES (490, 13.002, 'Kshetra-Kshetrajna-Vibhaga-Yoga', 490, 'sri-bhagavan uvach; श्रीभगवानुवाच', 'idam sariram kaunteya ', 'ksetram ity abhidhiyate', 'etad yo vetti tam prahuh ', 'ksetrajna iti tadvidah ', '', 'sri-bhagavan uvach (the Personality of Godhead says) idam (this) sariram (body) kaunteya (O son of Kunti) ksetram (the field) iti (thus) abhidhiyate (is called) etat (this) yah (anyone) vetti (knows) tam (he) prahuh (is called) ksetra-jnah (knower of the body) iti (thus) tat-vidah (one who knows).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (491, 13.003, 'Kshetra-Kshetrajna-Vibhaga-Yoga', 491, 'ksetrajnam capi mam ', 'viddhi sarvaksetresu bharata', 'ksetraksetrajnayor jnanam ', 'yat taj jnanam matammama ', '', '', 'ksetra-jnam (the knower) ch (also) api (certainly) mam (Me) viddhi (know) sarva (all) ksetresu (in bodily fields) bharata (O son of Bharata) ksetra (field of activities (the body)) ksetra-jnayoh (the knower of the field) jnanam (knowledge) yat (that which is taught) tat (that) jnanam (knowledge) matam (opinion) mama (that).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (492, 13.004, 'Kshetra-Kshetrajna-Vibhaga-Yoga', 492, 'tat ksetram yac ch yadrk', 'ch yadvikari yatas ch yat', 'sa ch yo yatprabhavas ', 'ch tat samasena me srnu ', '', '', 'tat (that) ksetram (field of activities) yat (as) ch (also) yadrk (as it is) ch (also) yat (what is) vikari (changes) yatah (from which) ch (also) yat (which) sah (he) ch (also) yah (one) yat (which) prabhavah ch (influence also) tat (that) samasena (in summary) me (from Me) srnu (understand).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (493, 13.005, 'Kshetra-Kshetrajna-Vibhaga-Yoga', 493, 'rsibhir bahudha gitam ', 'chandobhir vividhaih prthak', 'brahmasutrapadais caiva ', 'hetumadbhir viniscitaih ', '', '', 'rsibhih (by the wise sages) bahudha (in many ways) gitam (described) chandobhih (Vedic hymns) vividhaih (in various) prthak (variously) brahma-sutra (the Vedanta) padaih (aphorisms) ch (also) eva (certainly) hetu-madbhih (with cause and effect) viniscitaih (ascertain).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (494, 13.006, 'Kshetra-Kshetrajna-Vibhaga-Yoga', 494, 'mahabhutany ahamkaro ', 'buddhir avyaktam eva ch', 'indriyani dasaikam ch ', 'panca cendriyagocarah ', '', '', 'maha-bhutani (great elements) ahankarah (false ego) buddhih (intelligence) avyaktam (the unmanifested) eva (certainly) ch (also) indriyani (senses) dasa-ekam (eleven) ch (also) panca (five) ch (also) indriya-go-carah (objects of the senses)', NULL, NULL, 'admin');
INSERT INTO gita VALUES (495, 13.007, 'Kshetra-Kshetrajna-Vibhaga-Yoga', 495, 'iccha dvesah sukham ', 'duhkham samghatas cetana', 'dhrtih etat ksetram samasena ', 'savikaram udahrtam ', '', '', 'iccha (desire) dvesah (hatred) sukham (happiness) duhkham (distress) sanghatah (the aggregate) cetana (living symptoms) dhrtih (conviction) etat (all this) ksetram (the field of activities) samasena (in summary) sa-vikaram (interaction) udahrtam (exemplified).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (496, 13.008, 'Kshetra-Kshetrajna-Vibhaga-Yoga', 496, 'amanitvam adambhitvam ', 'ahimsa ksantir arjavam', 'acaryopasanam saucam ', 'sthairyam atmavinigrahah', '', '', 'amanitvam (humility) adambhitvam (pridelessness) ahimsa (nonviolence) ksantih (tolerance) arjavam (simplicity) acarya-upasanam (approaching a bona fide spiritual master) saucam (cleanliness) sthairyam (steadfastness) atma-vinigrahah (control)', NULL, NULL, 'admin');
INSERT INTO gita VALUES (497, 13.009, 'Kshetra-Kshetrajna-Vibhaga-Yoga', 497, 'indriyarthesu vairagyam ', 'anahamkara eva ch', 'janmamrtyujaravyadhiduhkhadosanudarsanam ', '', '', '', 'indriya-arthesu (in the matter of the senses) vairagyam (renunciation) anahankarah (being without false egoism) eva (certainly) ch (also) janma (birth) mrtyu (death) jara (old age) vyadhi (disease) duhkha (distress) dosa (fault) anudarsanam (observing)', NULL, NULL, 'admin');
INSERT INTO gita VALUES (498, 13.010, 'Kshetra-Kshetrajna-Vibhaga-Yoga', 498, 'asaktir anabhisvangah ', 'putradaragrhadisu', 'nityam ch ', 'samacittatvam istanistopapattisu ', '', '', 'asaktih (without attachment) anabhisvangah (without association) putra (sons) dara (wife) grha-adisu (home, etc.) nityam (eternal) ch (also) sama-cittatvam (equilibrium) ista (desirable) anista (undesirable) upapattisu (having obtained)', NULL, NULL, 'admin');
INSERT INTO gita VALUES (499, 13.011, 'Kshetra-Kshetrajna-Vibhaga-Yoga', 499, 'mayi cananyayogena ', 'bhaktir avyabhicarini', 'viviktadesasevitvam ', 'aratir janasamsadi ', '', '', 'mayi (unto Me) ch (also) ananya-yogena (by devotional service) bhaktih (devotion) avyabhicarini (constant, unalloyed) vivikta (solitary) desa (place) sevitvam (aspiring) aratih (without attachment) jana (to people in general) samsadi (mass)', NULL, NULL, 'admin');
INSERT INTO gita VALUES (500, 13.012, 'Kshetra-Kshetrajna-Vibhaga-Yoga', 500, 'adhyatmajnananityatvam ', 'tattvajnanarthadarsanam', 'etaj jnanam iti proktam ', 'ajnanam yad atonyatha ', '', '', 'adhyatma (pertaining to the self) jnana (knowledge) nityatvam (eternity) tattva-jnana (knowledge of the truth) artha (the object) darsanam (philosophy) etat (all this) jnanam (knowledge) iti (thus) proktam (declared) ajnanam (ignorance) yat (that which) atah (from this) anyatha (others).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (501, 13.013, 'Kshetra-Kshetrajna-Vibhaga-Yoga', 501, 'jneyam yat tat pravaksyami ', 'yaj jnatvamrtam asnute', 'anadimat param brahma na ', 'sat tan nasad ucyate', '', '', 'jneyam (knowable) yat (that) tat (which) pravaksyami (I shall now explain) yat (which) jnatva (knowing) amrtam (nectar) asnute (taste) anadi (beginningless) mat-param (subordinate to Me) brahma (spirit) na (neither) sat (cause) tat (that) na (nor) asat (effect) ucyate (is called).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (502, 13.014, 'Kshetra-Kshetrajna-Vibhaga-Yoga', 502, 'sarvatah panipadam tat ', 'sarvatoksisiromukham', 'sarvatah srutimal loke ', 'sarvam avrtya tisthati ', '', '', 'sarvatah (everywhere) pani (hands) padam (legs) tat (that) sarvatah (everywhere) aksi (eyes) sirah (head) mukham (face) sarvatah (everywhere) sruti-mat (hearing) loke (in the world) sarvam (everything) avrtya (covering) tisthati (exists).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (503, 13.015, 'Kshetra-Kshetrajna-Vibhaga-Yoga', 503, 'sarvendriyagunabhasam ', 'sarvendriyavivarjitam', 'asaktam sarvabhrc caiva ', 'nirgunam gunabhoktr ch', '', '', 'sarva (all) indriya (senses) guna (qualities) abhasam (original source) sarva (all) indriya (senses) vivarjitam (being without) asaktam (without attachment) sarva-bhrt (maintainer of everyone) ch (also) eva (certainly) nirgunam (without material qualities) guna-bhoktr (simultaneously master of the gunas) ch (also).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (504, 13.016, 'Kshetra-Kshetrajna-Vibhaga-Yoga', 504, 'bahir antas ch bhutanam ', 'acaram caram eva ch', 'suksmatvat tad avijneyam ', 'durastham cantike ch tat', '', '', 'bahir (outside) antah (inside) ch (also) bhutanam (of all living entities) acaram (not moving) caram (moving) eva (also) ch (and) suksmatvat (on account of being subtle) tat (that) avijneyam (unknowable) durastham (far away) ch (also) antike (near) ch (and) tat (that).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (505, 13.017, 'Kshetra-Kshetrajna-Vibhaga-Yoga', 505, 'avibhaktam ch bhutesu ', 'vibhaktam iva ch sthitam', 'bhutabhartr ch taj jneyam ', 'grasisnu prabhavisnu ch', '', '', 'avibhaktam (without division) ch (also) bhutesu (in every living being) vibhaktam (divided) iva (as if) ch (also) sthitam (situated) bhuta-bhartr (maintainer of all living entities) ch (also) tat (that) jneyam (to be understood) grasisnu (devours) prabhavisnu (develops) ch (also).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (506, 13.018, 'Kshetra-Kshetrajna-Vibhaga-Yoga', 506, 'jyotisam api taj jyotis ', 'tamasah param ucyate', 'jnanam jneyam jnanagamyam ', 'hrdi sarvasya visthitam', '', '', 'jyotisam (in all luminous objects) api (also) tat (that) jyotih (source of light) tamasah (of the darkness) param (beyond) ucyate (is says) jnanam (knowledge) jneyam (to be known) jnana-gamyam (to be approached by knowledge) hrdi (in the heart) sarvasya (of everyone) visthitam (situated).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (507, 13.019, 'Kshetra-Kshetrajna-Vibhaga-Yoga', 507, 'iti ksetram tatha jnanam ', 'jneyam coktam sanasatah', 'madbhakta etad vijnaya ', 'madbhavayopapadyate ', '', '', 'iti (thus) ksetram (the field of activities (the body)) tatha (also) jnanam (knowledge) jneyam (the knowable) ch (also) uktam (described) samasatah (in summary) mat-bhaktah (my devotee) etat (all this) vijnaya (after understanding) mat-bhavaya (my nature) upapadyate (attains).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (508, 13.020, 'Kshetra-Kshetrajna-Vibhaga-Yoga', 508, 'prakrtim purusam caiva ', 'viddhy anadi ubhav api', 'vikarans ch gunams caiva ', 'viddhi prakrtisambhavan', '', '', 'prakrtim (material nature) purusam (living entities) ch (also) eva (certainly) viddhi (must know) anadi (without beginning) ubhau (both) api (also) vikaran (transformations) ch (also) gunan (three modes of nature) ch (also) eva (certainly) viddhi (know) prakrti (material nature) sambhavan (produced of).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (509, 13.021, 'Kshetra-Kshetrajna-Vibhaga-Yoga', 509, 'karya karana kartrtve ', 'hetuh prakrtir ucyate', 'purusah sukhaduhkhanam ', 'bhoktrtve hetur ucyate', '', '', 'karya (effect) karana (cause) kartrtve (in the matter of creation) hetuh (instrument) prakrtih (material nature) ucyate (is says to be) purusah (the living entity) sukha (of happiness) duhkhanam (and distress) bhoktrtve (in enjoyment) hetuh (the instrument) ucyate (is says to be).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (510, 13.022, 'Kshetra-Kshetrajna-Vibhaga-Yoga', 510, 'purusah prakrtistho hi ', 'bhunkte prakrtijan gunan', 'karanam gunasangosya ', 'sadasadyonijanmasu ', '', '', 'purusah (the living entity) prakrti-sthah (being situated in the material energy) hi (certainly) bhunkte (enjoys) prakrti-jan (produced by the material nature) gunan (modes of nature) karanam (cause) guna-sangah (association with the modes of nature) asya (of the living entity) sat-asat (good and bad) yoni (species of life) janmasu (birth).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (511, 13.023, 'Kshetra-Kshetrajna-Vibhaga-Yoga', 511, 'upadrastanumanta ch bharta ', 'bhokta mahesvarah', 'paramatmeti capyukto ', 'dehesmin purusah parah ', '', '', 'upadrasta (overseer) anumanta (permitter) ch (also) bharta (master) bhokta (supreme enjoyer) maha-isvarah (the Supreme Lord) parama-atma (Supersoul) iti (also) ch (and) api uktah (is says) dehe (in this body) asmin (this) purusah (enjoyer) parah (transcendental).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (512, 13.024, 'Kshetra-Kshetrajna-Vibhaga-Yoga', 512, 'ya evam vetti purusam ', 'prakrtim ch gunaih saha', 'sarvatha vartamanopi ', 'na sa bhuyobhijayate ', '', '', 'yah (anyone) evam (thus) vetti (understands) purusam (the living entity) prakrtim (material nature) ch (and) gunaih (modes of material nature) saha (with) sarvatha (by all means) vartamanah (situated) api (in spite of) na (never) sah (he) bhuyah (again) abhijayate (takes his birth).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (513, 13.025, 'Kshetra-Kshetrajna-Vibhaga-Yoga', 513, 'dhyanenatmani pasyanti ', 'kecid atmanam atmana', 'anye samkhyena yogena ', 'karmayogena capare ', '', '', 'dhyanena (by meditation) atmani (self) pasyanti (see) kecit (one) atmanam (Supersoul) atmana (by the mind) anye (others) sankhyena (by philosophical discussion) yogena (by the yoga system) karma-yogena (by activities without fruitive desire) ch (also) apare (others).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (514, 13.026, 'Kshetra-Kshetrajna-Vibhaga-Yoga', 514, 'anye tv evam ajanantah ', 'srutvanyebhya upasate', 'tepi catitaranty eva ', 'mrtyum srutiparayanah ', '', '', 'anye (others) tu (but) evam (thus) ajanantah (without spiritual knowledge) srutva (by hearing) anyebhyah (from others) upasate (begin to worship) te (they) api (also) ch (and) atitaranti (transcend) eva (certainly) mrtyum (the path of death) sruti-parayanah (inclined to the process of hearing).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (515, 13.027, 'Kshetra-Kshetrajna-Vibhaga-Yoga', 515, 'yavat samjayate kimcit ', 'sattvam sthavarajangamam', 'ksetraksetrajnasamyogat ', 'tad viddhi bharatarsabha', '', '', 'yavat (whatever) sanjayate (takes place) kincit (anything) sattvam (existence) sthavara (not moving) jangamam (moving) ksetra (the body) ksetra-jna (knower of the body) samyogat (union between) tat viddhi (you must know it) bharata-rsabha (O chief of the Bharatas).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (516, 13.028, 'Kshetra-Kshetrajna-Vibhaga-Yoga', 516, 'samam sarvesu bhutesu ', 'tisthantam paramesvaram', 'vinasyatsv avinasyantam yah ', 'pasyati sa pasyati ', '', '', 'samam (equally) sarvesu (in all) bhutesu (living entities) tisthan-tam (residing) parama-isvaram (the Supersoul) vinasyatsu (in the destructible) avinasyantam (not destroyed) yah (anyone) pasyati (sees) sah (he) pasyati (actually sees).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (517, 13.029, 'Kshetra-Kshetrajna-Vibhaga-Yoga', 517, 'samam pasyan hi sarvatra ', 'samavasthitam isvaram', 'na hinasty atmanatmanam ', 'tato yati param gatim', '', '', 'samam (equally) pasyan (seeing) hi (certainly) sarvatra (everywhere) samavasthitam (equally situated) isvaram (Supersoul) na (does not) hinasti (degrade) atmana (by the mind) atmanam (the soul) tatah yati (then reaches) param (the transcendental) gatim (destination).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (518, 13.030, 'Kshetra-Kshetrajna-Vibhaga-Yoga', 518, 'prakrtyaiva ch karmani ', 'kriyamanani sarvasah', 'yah pasyati tathatmanam ', 'akartaram sa pasyati ', '', '', 'prakrtya (by material nature) eva (certainly) ch (also) karmani (activities) kriyamanani (engaged in performing) sarvasah (in all respects) yah (anyone who) pasyati (sees) tatha (also) atmanam (himself) akartaram (non-doer) sah (he) pasyati (sees perfectly).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (519, 13.031, 'Kshetra-Kshetrajna-Vibhaga-Yoga', 519, 'yada bhutaprthagbhavam ', 'ekastham anupasyati', 'tata eva ch vistaram ', 'brahma sampadyate tada ', '', '', 'yada (when) bhuta (living entities) prthak-bhavam (separated identities) eka-stham (situated in one) anupasyati (tries to see through authority) tatah eva (thereafter) ch (also) vistaram (expanded) brahma (the Absolute) sampadyate (attains) tada (at that time).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (520, 13.032, 'Kshetra-Kshetrajna-Vibhaga-Yoga', 520, 'anaditvan nirgunatvat ', 'paramatmayam avyayah', 'sarirasthopi kaunteya na ', 'karoti na lipyate ', '', '', 'anaditvat (due to eternity) nirgunatvat (due to transcendental) parama (beyond material nature) atma (spirit) ayam (this) avyayah (inexhaustible) sarira-sthah api (though dwelling in the body) kaunteya (O son of Kunti) na karoti (never does anything) na lipyate (nor is he entangled).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (521, 13.033, 'Kshetra-Kshetrajna-Vibhaga-Yoga', 521, 'yatha sarvagatam sauksmyad ', 'akasam nopalipyate', 'sarvatravasthito dehe ', 'tathatma nopalipyate ', '', '', 'yatha (as) sarva-gatam (all-pervading) sauksmyat (due to being subtle) akasam (the sky) na (never) upalipyate (mixes) sarvatra (everywhere) avasthitah (situated) dehe (in the body) tatha (such) atma (the self) na (never) upalipyate (mixes).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (522, 13.034, 'Kshetra-Kshetrajna-Vibhaga-Yoga', 522, 'yatha prakasayaty ekah ', 'krtsnam lokam imam ravih', 'ksetram ksetri tatha ', 'krtsnam prakasayati bharata', '', '', 'yatha (as) prakasayati (illuminates) ekah (one) krtsnam (the whole) lokam (universe) imam (this) ravih (the sun) ksetram (this body) ksetri (the soul) tatha (similarly) krtsnam (all) prakasayati (illuminates) bharata (O son of Bharata).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (523, 13.035, 'Kshetra-Kshetrajna-Vibhaga-Yoga', 523, 'ksetraksetrajnayor evam ', 'antaram jnanacaksusa', 'bhutaprakrtimoksam ch ye ', 'vidur yanti te param ', '', '', 'ksetra (body) ksetra-jnayoh (of the proprietor of the body) evam (that) antaram (difference) jnana-caksusa (by vision of knowledge) bhuta (living entity) prakrti (material nature) moksam (liberation) ch (also) ye (one who) viduh (knows) yanti (approaches) te (they) param (Supreme).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (524, 14.001, 'Gunatraya Vibhaga-Yoga', 524, 'sri-bhagavan uvach; श्रीभगवानुवाच', 'param bhuyah pravaksyami ', 'jnananam jnanamuttamam', 'yaj jnatva munayah sarve ', 'param siddhim ito gatah', '', 'sri-bhagavan uvach (The Lord says) param (transcendental) bhuyah (again) pravaksyami (I shall speak) jnananam (of all knowledge) jnanam (knowledge) uttamam (the supreme) yat (which) jnatva (knowing) munayah (the sages) sarve (all) param (transcendental) siddhim (perfection) itah (from this world) gatah (attained).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (525, 14.002, 'Gunatraya Vibhaga-Yoga', 525, 'idam jnanam upasritya ', 'mama sadharmyam agatah', 'sargepi nopajayante ', 'pralaye na vyathanti ch ', '', '', 'idam (this) jnanam (knowledge) upasritya (taking shelter of) mama (my) sadharmyam (nature) agatah (attained) sarge api (even in the creation) na (never) upajayante (comes in) pralaye (in the annihilation) na (nor) vyathanti (disturbed) ch (also).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (526, 14.003, 'Gunatraya Vibhaga-Yoga', 526, 'mama yonir mahad brahma ', 'tasmin garbham dadhamyaham', 'sambhavah sarvabhutanam ', 'tato bhavati bharata ', '', '', 'mama (my) yonih (source of birth) mahat (the total material existence) brahma (supreme) tasmin (in that) garbham (pregnancy) dadhami (create) aham (I) sambhavah (possibility) sarva-bhutanam (of all living entities) tatah (thereafter) bhavati (becomes) bharata (O son of Bharata).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (527, 14.004, 'Gunatraya Vibhaga-Yoga', 527, 'sarvayonisu kaunteya ', 'murtayah sambhavanti yah', 'tasam brahma mahad ', 'yonir aham bijapradah pita ', '', '', 'sarva-yonisu (in all species of life) kaunteya (O son of Kunti) murtayah (forms) sambhavanti (as they appear) yah (which) tasam (all of them) brahma (supreme) mahat yonih (the source of birth in the material substance) aham (myself) bija-pradah (seed-giving) pita (father).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (528, 14.005, 'Gunatraya Vibhaga-Yoga', 528, 'sattvam rajas tama iti ', 'gunah prakrtisambhavah', 'nibadhnanti mahabaho ', 'dehe dehinam avyayam ', '', '', 'sattvam (mode of goodness) rajah (mode of passion) tamah (mode of ignorance) iti (thus) gunah (qualities) prakrti (material nature) sambhavah (produced of) nibadhnanti (does condition) maha-baho (O mighty-armed one) dehe (in this body) dehinam (the living entity) avyayam (eternal).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (529, 14.006, 'Gunatraya Vibhaga-Yoga', 529, 'tatra sattvam nirmalatvat ', 'prakasakam anamayam', 'sukhasangena badhnati ', 'jnanasangena canagha ', '', '', 'tatra (thereafter) sattvam (mode of goodness) nirmalatvat (being purest in the material world) prakasakam (illuminating) anamayam (without any sinful reaction) sukha (happiness) sangena (association) badhnati (conditions) jnana (knowledge) sangena (association) ch (also) anagha (O sinless one).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (530, 14.007, 'Gunatraya Vibhaga-Yoga', 530, 'rajo ragatmakam viddhi ', 'trsnasangasamudbhavam', 'tan nibadhnati kaunteya ', 'karmasangena dehinam ', '', '', 'rajah (the mode of passion) raga-atmakam (born of desire or lust) viddhi (know) trsna (with hankering) sanga (association) samudbhavam (produced of) tat (that) nibadhnati (binds) kaunteya (O son of Kunti) karma-sangena (by association with fruitive activity) dehinam (the embodied).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (531, 14.008, 'Gunatraya Vibhaga-Yoga', 531, 'tamas tv ajnanajam viddhi ', 'mohanam sarvadehinam', 'pramadalasyanidrabhis tan ', 'nibadhnati bharata ', '', '', 'tamah (mode of ignorance) tu (but) ajnana-jam (products of ignorance) viddhi (knowing) mohanam (delusion) sarva-dehinam (of all embodied beings) pramada (madness) alasya (indolence) nidrabhih (sleep) tat (that) nibadhnati (binds) bharata (O son of Bharata).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (532, 14.009, 'Gunatraya Vibhaga-Yoga', 532, 'sattvam sukhe samjayati ', 'rajah karmani bharata', 'jnanam avrtya tu tamah ', 'pramade samjayaty uta ', '', '', 'sattvam (mode of goodness) sukhe (in happiness) sanjayati (develops) rajah (mode of passion) karmani (fruits of activities) bharata (O son of Bharata) jnanam (knowledge) avrtya (covering) tu (but) tamah (the mode of ignorance) pramade (in madness) sanjayati (develops) uta (it is says).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (533, 14.010, 'Gunatraya Vibhaga-Yoga', 533, 'rajas tamas cabhibhuya ', 'sattvam bhavati bharata', 'rajah sattvam tamas caiva ', 'tamah sattvam rajas tatha', '', '', 'rajah (mode of passion) tamah (mode of ignorance) ch (also) abhibhuya (also surpassing) sattvam (mode of goodness) bhavati (becomes prominent) bharata (O son of Bharata) rajah (mode of passion) sattvam (mode of goodness) tamah (mode of ignorance) ch (also) eva (like that) tamah (mode of ignorance) sattvam (mode of goodness) rajah (mode of passion) tatha (as in this).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (534, 14.011, 'Gunatraya Vibhaga-Yoga', 534, 'sarvadvaresu dehesmin ', 'prakasa upajayate', 'jnanam yada tada vidyad ', 'vivrddham sattvam ity uta', '', '', 'sarva-dvaresu (all the gates) dehe asmin (in this body) prakasah (quality of illumination) upajayate (develops) jnanam (knowledge) yada (when) tada (at that time) vidyat (must know) vivrddham (increased) sattvam (the mode of goodness) iti (thus) uta (says).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (535, 14.012, 'Gunatraya Vibhaga-Yoga', 535, 'lobhah pravrttir arambhah ', 'karmanam asamah sprha', 'rajasy etani jayante ', 'vivrddhe bharatarsabha ', '', '', 'lobhah (greed) pravrttih (hankering) arambhah (endeavor) karmanam (of activities) asamah (uncontrollable) sprha (desire) rajasi (in the mode of passion) etani (all this) jayante (develop) vivrddhe (when there is excess) bharata-rsabha (O chief of the descendants of Bharata).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (536, 14.013, 'Gunatraya Vibhaga-Yoga', 536, 'aprakasopravrttis ch ', 'pramado moha eva ch', 'tamasy etani jayante ', 'vivrddhe kurunandana ', '', '', 'aprakasah (darkness) apravrttih (inactivity) ch (and) pramadah (madness) mohah (illusion) eva (certainly) ch (also) tamasi (of the mode of ignorance) etani (these) jayante (are manifested) vivrddhe (is developed) kuru-nandana (O son of Kuru).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (537, 14.014, 'Gunatraya Vibhaga-Yoga', 537, 'yada sattve pravrddhe tu ', 'pralayam yati dehabhrt', 'tadottamavidam lokan ', 'amalan pratipadyate ', '', '', 'yada (when) sattve (mode of goodness) pravrddhe (in development) tu (but) pralayam (dissolution) yati (goes) deha-bhrt (embodied) tada (at that time) uttama-vidam (of the great sages) lokan (the planets) amalan (pure) pratipadyate (attains).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (538, 14.015, 'Gunatraya Vibhaga-Yoga', 538, 'rajasi pralayam gatva ', 'karmasangisu jayate', 'tatha pralinas tamasi ', 'mudhayonisu jayate ', '', '', 'rajasi (in passion) pralayam (dissolution) gatva (attaining) karma-sangisu (in the association of fruitive activities) jayate (takes birth) tatha (thereafter) pralinah (being dissolved) tamasi (in ignorance) mudha (animal) yonisu (species) jayate (take birth).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (539, 14.016, 'Gunatraya Vibhaga-Yoga', 539, 'karmanah sukrtasyahuh ', 'sattvikam nirmalam phalam', 'rajasas tu phalam duhkham ', 'ajnanam tamasah phalam', '', '', 'karmanah (of work) su-krtasya (in the mode of goodness) ahuh (says) sattvikam (mode of goodness) nirmalam (purified) phalam (result) rajasah (of the mode of passion) tu (but) phalam (result) duhkham (misery) ajnanam (nonsense) tamasah (of the mode of ignorance) phalam (result).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (540, 14.017, 'Gunatraya Vibhaga-Yoga', 540, 'sattvat samjayate ', 'jnanam rajaso lobha eva ch', 'pramadamohau tamaso ', 'bhavatojnanam eva ch ', '', '', 'sattvat (from the mode of goodness) sanjayate (develops) jnanam (knowledge) rajasah (from the mode of passion) lobhah (greed) eva (certainly) ch (also) pramada (madness) mohau (illusion) tamasah (from the mode of ignorance) bhavatah (develops) ajnanam (nonsense) eva (certainly) ch (also).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (541, 14.018, 'Gunatraya Vibhaga-Yoga', 541, 'urdhvam gacchanti sattvastha ', 'madhye tisthantirajasah', 'jaghanyagunavrttistha adho ', 'gacchhanti tamasah ', '', '', 'urdhvam (upwards) gacchanti (go) sattva-sthah (one who is situated in the mode of goodness) madhye (in the middle) tisthanti (dwell) rajasah (those who are situated in the mode of passion) jaghanya (abominable) guna (quality) vrtti-sthah (occupation) adhah (down) gacchanti (go) tamasah (people in the mode of ignorance).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (542, 14.019, 'Gunatraya Vibhaga-Yoga', 542, 'nanyam gunebhyah kartaram ', 'yada drastanupasyati', 'gunebhyas ch param vetti ', 'madbhavam sodhigacchhati', '', '', 'na (never) anyam (other than) gunebhyah (from the qualities) kartaram (the performer) yada (when) drasta anupasyati (he who sees properly) gunebhyah ch (from the modes of nature) param (transcendental) vetti (know) mat-bhavam (my spiritual nature) sah (he) adhigacchati (is promoted).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (543, 14.020, 'Gunatraya Vibhaga-Yoga', 543, 'gunan etan atitya trin ', 'dehi dehasamudbhavan', 'janmamrtyujaraduhkhair ', 'vimuktomrtam asnute ', '', '', 'gunan (qualities) etan (all these) atitya (transcending) trin (three) dehi (embodied) deha (body) samudbhavan (produced of) janma (birth) mrtyu (death) jara (old age) duhkhaih (distresses) vimuktah (being freed from) amrtam (nectar) asnute (enjoys).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (544, 14.021, 'Gunatraya Vibhaga-Yoga', 544, 'arjuna uvach; अर्जुन उवाच', 'kair lingais trin gunan ', 'etan atito bhavati prabho', 'kimacarah katham caitams ', 'trin gunan ativartate ', '', 'arjunah uvach (Arjuna says) kaih (by which) lingaih (symptoms) trin (three) gunan (qualities) etan (all this) atitah (having transcended) bhavati (become) prabho (my Lord) kim (what) acarah (behavior) katham (what) ch (also) etan (these) trin (three) gunan (qualities) ativartate (transcend).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (545, 14.022, 'Gunatraya Vibhaga-Yoga', 545, 'sri-bhagavan uvach; श्रीभगवानुवाच', 'prakasam ch pravrttim ch ', 'moham eva ch pandava', 'ta dvesti sampravrttani ', 'na nivrttani kanksati ', '', 'sri-bhagavan uvach (The Lord says) prakasam ch (and illumination) pravrttim ch (and attachment) moham (illusion) eva ch (also) pandava (O son of Pandu) na dvesti (does not hate) sampravrttani (although developed) na nivrttani (nor stop development) kanksati (desires)', NULL, NULL, 'admin');
INSERT INTO gita VALUES (546, 14.023, 'Gunatraya Vibhaga-Yoga', 546, 'udasinavad asino gunair ', 'yo na vicalyate', 'guna vartanta ity eva ', 'yovatisthati nengate ', '', '', 'udasina-vat (as if neutral) asinah (situated) gunaih (by the qualities) yah (one who) na (never) vicalyate (is agitated) gunah (the qualities) vartante (is situated) iti evam (knowing thus) yah (one who) avatisthati (remains) na (never) ingate (flickering)', NULL, NULL, 'admin');
INSERT INTO gita VALUES (547, 14.024, 'Gunatraya Vibhaga-Yoga', 547, 'samaduhkhasukhah svasthah ', 'samalostasmakancanah', 'tulyapriyapriyo dhiras ', 'tulyanindatmasamstutih ', '', '', 'sama (equal) duhkha (in distress) sukhah (in happiness) sva-sthah (being situated himself) sama (equally) losta (a lump of earth) asma (stone) kancanah (gold) tulya (equally disposed) priya (dear) apriyah (undesirable) dhirah (steady) tulya (equally) ninda (in defamation) atma-samstutih (in praise of himself)', NULL, NULL, 'admin');
INSERT INTO gita VALUES (548, 14.025, 'Gunatraya Vibhaga-Yoga', 548, 'manapamanayos tulyas ', 'tulyo mitraripaksayoh', 'sarvarambhaparityagi ', 'gunatitah sa ucyate ', '', '', 'mana (in honor) apamanayoh (dishonor) tulyah (equally) tulyah (equally) mitra (friend) ari (enemy) paksayoh (in parties) sarva (all) arambha (endeavor) parityagi (renouncer) guna-atitah (transcendental to the material modes of nature) sah (he) ucyate (is says to be).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (549, 14.026, 'Gunatraya Vibhaga-Yoga', 549, 'mam ch yovyabhicarena ', 'bhaktiyogena sevate', 'sa gunan samatityaitan ', 'brahmabhuyaya kalpate ', '', '', 'mam (unto Me) ch (also) yah (person) avyabhicarena (without fail) bhakti-yogena (by devotional service) sevate (renders service) sah (he) gunan (all the modes of material nature) samatitya (transcending) etan (all this) brahma-bhuyaya (to be elevated to the Brahman platform) kalpate (is considered).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (550, 14.027, 'Gunatraya Vibhaga-Yoga', 550, 'brahmano hi pratisthaham ', 'amrtasyavyayasya ch', 'sasvatasya ch dharmasya ', 'sukhasyaikantikasya ch ', '', '', 'brahmanah (of the impersonal brahmajyoti) hi (certainly) pratistha (the rest) aham (I am) amrtasya (of the immortal) avyayasya (of the imperishable) ch (also) sasvatasya (of the eternal) ch (and) dharmasya (of the constitutional position) sukhasya (happiness) aikantikasya (ultimate) ch (also).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (551, 15.001, 'Purusottam-Yoga', 551, 'sri-bhagavan uvach; श्रीभगवानुवाच', 'urdhvamulam adhahsakham ', 'asvattham prahur avyam', 'chandamsi yasya parnani ', 'yas tam veda sa vedavit ', '', 'sri-bhagavan uvach (The Lord says) urdhva-mulam (with roots above) adhah (downwards) sakham (branches) asvattham (banyan tree) prahuh (says) avyayam (eternal) chandamsi (Vedic hymns) yasya (of which) parnani (the leaves) yah (anyone) tam (that) veda (knows) sah (he) veda-vit (the knower of the Vedas).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (552, 15.002, 'Purusottam-Yoga', 552, 'adhas cordhvam prasrtastasya sakha', 'gunapravrddha visayapravalah', 'adhas ch mulany anusamtatani', 'karmanubandhini manusyaloke ', '', '', 'adhah (downward) ch (and) urdhvam (upward) prasrtah (extended) tasya (its) sakhah (branches) guna (modes of material nature) pravrddhah (developed) visaya (sense objects) pravalah (twigs) adhah (downward) ch (and) mulani (roots) anusantatani (extended) karma (according to work) anubandhini (bound) manusya-loke (in the world of human society).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (553, 15.003, 'Purusottam-Yoga', 553, 'na rupam asyeha tathopalabhyate', 'nanto na cadir na ch sampratistha', 'asvattham enam suvirudhamulam', 'asangasastrena drdhena chittva ', '', '', 'na (not) rupam (form) asya (of this tree) iha (in this) tatha (also) upalabhyate (can be perceived) na (never) antah (end) na (never) ch (also) adih (beginning) na (never) ch (also) sampratistha (the foundation) asvattham (banyan tree) enam (this) su-virudha (strongly) mulam (rooted) asanga-sastrena (by the weapon of detachment) drdhena (strong) chittva (by cutting)', NULL, NULL, 'admin');
INSERT INTO gita VALUES (554, 15.004, 'Purusottam-Yoga', 554, 'tatah padam tatparimargitavyam', 'yasmin gata na nivartanti bhuyah', 'tameva cadyam purusam prapadye', 'yatah pravrttih prasrta purani ', '', '', 'tatah (thereafter) padam (situation) tat (that) parimargitavyam (has to be searched out) yasmin (where) gatah (going) na (never) nivartanti (comes back) bhuyah (again) tam (to Him) eva (certainly) ch (also) adyam (original) purusam (the Personality of Godhead) prapadye (surrender) yatah (from whom) pravrttih (beginning) prasrta (extended) purani (very old).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (555, 15.005, 'Purusottam-Yoga', 555, 'nirmanamoha jitasangadosa', 'adhyatmanitya vinivrttakamah', 'dvandvair vimuktah sukhaduhkhasamjnaih', 'gacchhanty amudhah padam avyayam tat ', '', '', 'nih (without) mana (respect) mohah (illusion) jita (having conquered) sanga (association) dosah (faulty) adhyatma (spiritual) nityah (eternity) vinivrtta (associated) kamah (lust) dvandvaih (with duality) vimuktah (liberated) sukha-duhkha (happiness and distress) samjnaih (named) gacchanti (attains) amudhah (unbewildered) padam (situation) avyayam (eternal) tat (that).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (556, 15.006, 'Purusottam-Yoga', 556, 'na tad bhasayate suryo na ', 'sasanko na pavakah', 'yad gatva na nivartante ', 'tad dhama paramam mama', '', '', 'na (not) tat (that) bhasayate (illuminates) suryah (the sun) na (nor) sasankah (the moon) na (nor) pavakah (fire, electricity) yat (where) gatva (going) na (never) nivartante (comes back) tat dhama (that abode) paramam (supreme) mama (my).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (557, 15.007, 'Purusottam-Yoga', 557, 'mamaivamso jivaloke ', 'jivabhutah sanatanah', 'manahsasthanindriyani ', 'prakrtisthani karsati ', '', '', 'mama (my) eva (certainly) amsah (fragmental particles) jiva-loke (world of conditional life) jiva-bhutah (the conditioned living entity) sanatanah (eternal) manah (mind) sasthani (six) indriyani (senses) prakrti (material nature) sthani (situated) karsati (struggling hard).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (558, 15.008, 'Purusottam-Yoga', 558, 'sariram yad avapnoti ', 'yac capy utkramatisvarah', 'grhitvaitani samyati ', 'vayur gandhan ivasayat ', '', '', 'sariram (body) yat (as much as) avapnoti (gets) yat (that which) ch (also) api (virtually) utkramati (gives up) isvarah (the lord of the body) grhitva (taking) etani (all these) samyati (goes away) vayuh (air) gandhan (smell) iva (like) asayat (from the flower).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (559, 15.009, 'Purusottam-Yoga', 559, 'srotram caksuh sparsanam ', 'ch rasanam ghranam eva ch', 'adhisthaya manas cayam ', 'visayan upasevate ', '', '', 'srotram (ears) caksuh (eyes) sparsanam (touch) ch (also) rasanam (tongue) ghranam (smelling power) eva (also) ch (and) adhisthaya (being situated) manah (mind) ch (also) ayam (this) visayan (sense objects) upasevate (enjoys).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (560, 15.010, 'Purusottam-Yoga', 560, 'utkramantam sthitam vapi ', 'bhunjanam va gunanvitam', 'vimudha nanupasyanti ', 'pasyanti jnanacaksusah ', '', '', 'utkramantam (quitting the body) sthitam (situated in the body) va api (either) bhunjanam (enjoying) va (or) guna-anvitam (under the spell of the modes of material nature) vimudhah (foolish persons) na (never) anupasyanti (can see) pasyanti (one can see) jnana-caksusah (one who has the eyes of knowledge).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (561, 15.011, 'Purusottam-Yoga', 561, 'yatanto yoginas cainam ', 'pasyanty atmany avasthitam', 'yatantopy akrtatmano ', 'nainam pasyanty acetasah ', '', '', 'yatantah (endeavoring) yoginah (transcendentalists) ch (also) enam (this) pasyanti (can see) atmani (in the self) avasthitam (situated) yatantah (although endeavoring) api (although) akrta-atmanah (without self-realization) na (does not) enam (this) pasyanti (can see) acetasah (undeveloped mind).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (562, 15.012, 'Purusottam-Yoga', 562, 'yad adityagatam tejo ', 'jagad bhasayatekhilam', 'yac candramasi yac cagnau ', 'tat tejo viddhi mamakam', '', '', 'yat (that which) aditya-gatam (in the sunshine) tejah (splendor) jagat (the whole world) bhasayate (illuminates) akhilam (entirely) yat (that which) candramasi (in the moon) yat (that which) ch (also) agnau (in the fire) tat (that) tejah (splendor) viddhi (understand) mamakam (from Me).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (563, 15.013, 'Purusottam-Yoga', 563, 'gam avisya ch bhutani ', 'dharayamy aham ojasa', 'pusnami causadhih ', 'sarvah somo bhutva rasatmakah', '', '', 'gam (the planets) avisya (entering) ch (also) bhutani (living entities) dharayami (sustaining) aham (I) ojasa (by my energy) pusnami (nourishing) ch (and) ausadhih (all vegetables) sarvah (all) somah (the moon) bhutva (becoming) rasa-atmakah (supplying the juice).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (564, 15.014, 'Purusottam-Yoga', 564, 'aham vaisvanaro bhutva ', 'praninam deham asritah', 'pranapanasamayuktah ', 'pacamy annam caturvidham', '', '', 'aham (I) vaisvanarah (by my plenary portion as the digesting fire) bhutva (becoming) praninam (of all living entities) deham (body) asritah (situated) prana (outgoing air) apana (down-going air) samayuktah (keep balance) pacami (digest) annam (foodstuff) catuh-vidham (four kinds of).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (565, 15.015, 'Purusottam-Yoga', 565, 'sarvasya caham hrdi samnivisto', 'mattah smrtir jnanam apohanam ch', 'vedais ch sarvair aham eva vedyo', 'vedantakrd vedavid eva caham ', '', '', 'sarvasya (of all living beings) ch (and) aham (I) hrdi (in the heart) sannivistah (being situated) mattah (from Me) smrtih (remembrance) jnanam (knowledge) apohanam ch (and forgetfulness) vedaih (by the Vedas) ch (also) sarvaih (all) aham (I am) eva (certainly) vedyah (knowable) vedanta-krt (the compiler of the Vedanta) veda-vit (the knower of the Vedas) eva (certainly) ch (and) aham (I).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (566, 15.016, 'Purusottam-Yoga', 566, 'dvav imau purusau loke ', 'ksaras caksara eva ch', 'ksarah sarvani bhutani ', 'kutasthoksara ucyate ', '', '', 'dvau (two) imau (in this (world)) purusau (living entities) loke (in the world) ksarah (fallible) ch (and) aksarah (infallible) eva (certainly) ch (and) ksarah (the fallible) sarvani (all) bhutani (living entities) kuta-sthah (in oneness) aksarah (infallible) ucyate (is says).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (567, 15.017, 'Purusottam-Yoga', 567, 'uttamah purusas tv ', 'anyah paramatmety udahrtah', 'yo lokatrayam avisya ', 'bibharty avyaya isvarah ', '', '', 'uttamah (the best) purusah (personality) tu (but) anyah (another) parama (the supreme) atma (self) iti (thus) udahrtah (is says) yah (one who) loka (of the universe) trayam (the three divisions) avisya (entering) bibharti (maintaining) avyayah (inexhaustible) isvarah (the Lord).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (568, 15.018, 'Purusottam-Yoga', 568, 'yasmat ksaram atitoham ', 'aksarad api cottamah', 'atosmi loke vede ch ', 'prathitah purusottamah ', '', '', 'yasmat (because) ksaram (the fallible) atitah (transcendental) aham (I) aksarat (from the infallible) api (better than that) ch (and) uttamah (the best) atah (therefore) asmi (I am) loke (in the world) vede (in the Vedic literature) ch (and) prathitah (celebrated) purusa-uttamah (as the Supreme Personality).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (569, 15.019, 'Purusottam-Yoga', 569, 'yo mam evam asammudho ', 'janati purusottamam', 'sa sarvavid bhajati ', 'mam sarvabhavena bharata ', '', '', 'yah (anyone) mam (unto Me) evam (certainly) asammudhah (without a doubt) janati (knows) purusa-uttamam (the Supreme Personality of Godhead) sah (he) sarva-vit (knower of everything) bhajati (renders devotional service) mam (unto Me) sarva-bhavena (in all respects) bharata (O son of Bharata).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (570, 15.020, 'Purusottam-Yoga', 570, 'iti guhyatamam sastram ', 'idam uktam mayanagha', 'etat buddhva buddhiman ', 'syat krtakrtyas ch bharata', '', '', 'iti (thus) guhya-tamam (the most confidential) sastram (revealed scripture) idam (this) uktam (disclosed) maya (by Me) anagha (O sinless one) etat (this) buddhva (understanding) buddhi-man (intelligent) syat (one becomes) krta-krtyah (the most perfect) ch (and) bharata (O son of Bharata).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (571, 16.001, 'Daivasura Sampad Vibhaga-Yoga', 571, 'sri-bhagavan uvach; श्रीभगवानुवाच', 'abhayam sattvasamsuddhir ', 'jnanayogavyavasthitih', 'danam damas ch yajnas ch ', 'svadhyayas tapa arjavam', '', 'sri-bhagavan uvach (The Lord says) abhayam (fearlessness) sattva-samsuddhih (purification of ones existence) jnana (knowledge) yoga (of linking up) vyavasthitih (the situation) danam (charity) damah ch (and controlling the mind) yajnah ch (and performance of sacrifice) svadhyayah (study of Vedic literature) tapah (austerity) arjavam (simplicity)', NULL, NULL, 'admin');
INSERT INTO gita VALUES (572, 16.002, 'Daivasura Sampad Vibhaga-Yoga', 572, 'ahimsa satyam akrodhas ', 'tyagah santir apaisunam', 'daya bhutesv aloluptvam ', 'mardavam hrir acapalam', '', '', 'ahimsa (nonviolence) satyam (truthfulness) akrodhah (freedom from anger) tyagah (renunciation) santih (tranquility) apaisunam (aversion to fault-finding) daya (mercy) bhutesu (towards all living entities) aloluptvam (freedom from greed) mardavam (gentleness) hrih (modesty) acapalam (determination)', NULL, NULL, 'admin');
INSERT INTO gita VALUES (573, 16.003, 'Daivasura Sampad Vibhaga-Yoga', 573, 'tejah ksama dhrtih saucam ', 'adroho natimanita', 'bhavanti sampadam daivim ', 'abhijatasya bharata ', '', '', 'tejah (vigor) ksama (forgiveness) dhrtih (fortitude) saucam (cleanliness) adrohah (freedom from envy) na (not) ati-manita (expectation of honor) bhavanti (become) sampadam (qualities) daivim (transcendental) abhijatasya (of one who is born of) bharata (O son of Bharata).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (574, 16.004, 'Daivasura Sampad Vibhaga-Yoga', 574, 'dambho darpobhimanas ch ', 'krodhah parusyam eva ch', 'ajnanam cabhijatasya ', 'partha sampadam asurim ', '', '', 'dambhah (pride) darpah (arrogance) abhimanah (conceit) ch (and) krodhah (anger) parusyam (harshness) eva (certainly) ch (and) ajnanam (ignorance) ch (and) abhijatasya (one who is born of) partha (O son of Prtha) sampadam (nature) asurim (demoniac).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (575, 16.005, 'Daivasura Sampad Vibhaga-Yoga', 575, 'daivi sampad vimoksaya ', 'nibandhayasuri mata', 'ma sucah sampadam daivim ', 'abhijatosi pandava ', '', '', 'daivi (transcendental) sampat (nature) vimoksaya (meant for liberation) nibandhaya (for bondage) asuri (demoniac qualities) mata (it is considered) ma (do not) sucah (worry) sampadam (nature) daivim (transcendental) abhijatah (born) asi (you are) pandava (O son of Pandu).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (576, 16.006, 'Daivasura Sampad Vibhaga-Yoga', 576, 'dvau bhutasargau lokesmin ', 'daiva asura eva ch', 'daivo vistarasah prokta ', 'asuram partha me srnu ', '', '', 'dvau (two) bhuta-sargau (created living beings) loke (in the world) asmin (this) daivah (godly) asurah (demoniac) eva (certainly) ch (and) daivah (divine) vistarasah (at great length) proktah (says) asuram (demoniac) partha (O son of Prtha) me (from Me) srnu (just hear).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (577, 16.007, 'Daivasura Sampad Vibhaga-Yoga', 577, 'pravrttim ch nivrttim ch ', 'jana na vidur asurah', 'na saucam napi cacaro na ', 'satyam tesu vidyate ', '', '', 'pravrttim (proper action) ch (also) nivrttim (improper action) ch (and) janah (persons) na (never) viduh (know) asurah (demoniac qualities) na (never) saucam (cleanliness) na (nor) api (also) ch (and) acarah (behavior) na (never) satyam (truth) tesu (in them) vidyate (there is).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (578, 16.008, 'Daivasura Sampad Vibhaga-Yoga', 578, 'asatyam apratistham te ', 'jagad ahur anisvaram', 'aparasparasambhutam kim ', 'anyat kamahaitukam ', '', '', 'asatyam (unreal) apratistham (without foundation) te (they) jagat (the cosmic manifestation) ahuh (is says) anisvaram (with no controller) aparaspara (by mutual lust) sambhutam (cause) kim anyat (there is no other cause) kama-haitukam (it is due to lust only).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (579, 16.009, 'Daivasura Sampad Vibhaga-Yoga', 579, 'etam drstim avastabhya ', 'nastatmanolpabuddhayah', 'prabhavanty ugrakarmanah ', 'ksayaya jagatohitah ', '', '', 'etam (thus) drstim (vision) avastabhya (accepting) nasta (lost) atmanah (self) alpa-buddhayah (less intelligent) prabhavanti (flourish) ugra-karmanah (in painful activities) ksayaya (for destruction) jagatah (of the world) ahitah (unbeneficial).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (580, 16.010, 'Daivasura Sampad Vibhaga-Yoga', 580, 'kamam asritya duspuram ', 'dambhamanamadanvitah', 'mohad grhitvasadgrahan ', 'pravartantesucivratah ', '', '', 'kamam (lust) asritya (taking shelter of) duspuram (insatiable) dambha (pride) mana (false prestige) mada-anvitah (absorbed in conceit) mohat (by illusion) grhitva (taking) asat (nonpermanent) grahan (things) pravartante (flourish) asuci (unclean) vratah (avowed).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (581, 16.011, 'Daivasura Sampad Vibhaga-Yoga', 581, 'cintam aparimeyam ch ', 'pralayantam upasritah', 'kamopabhogaparama etavad ', 'iti niscitah ', '', '', 'cintam (fears and anxieties) aparimeyam (unmeasurable) ch (and) pralaya-antam (unto the point of death) upasritah (having taken shelter of them) kama-upabhoga (sense gratification) paramah (the highest goal of life) etavat (thus) iti (in this way) niscitah (ascertained)', NULL, NULL, 'admin');
INSERT INTO gita VALUES (582, 16.012, 'Daivasura Sampad Vibhaga-Yoga', 582, 'asapasasatair baddhah ', 'kamakrodhaparayanah', 'ihante kamabhogartham ', 'anyayenarthasamcayan ', '', '', 'asa-pasa (entanglements in the network of hope) sataih (by hundreds) baddhah (being bound) kama (lust) krodha (anger) parayanah (always situated in that mentality) ihante (desire) kama (lust) bhoga (sense enjoyment) artham (for that purpose) anyayena (illegally) artha (wealth) sancayan (accumulate).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (583, 16.013, 'Daivasura Sampad Vibhaga-Yoga', 583, 'idam adya maya labdham imam ', 'prapsye manoratham', 'idam astidam api me ', 'bhavisyati punar dhanam ', '', '', 'idam (this) adya (today) maya (by me) labdham (gained) imam (this) prapsye (I shall gain) manah-ratham (according to my desires) idam (this) asti (there is) idam (this) api (also) me (mine) bhavisyati (will increase in the future) punah (again) dhanam (wealth)', NULL, NULL, 'admin');
INSERT INTO gita VALUES (584, 16.014, 'Daivasura Sampad Vibhaga-Yoga', 584, 'asau maya hatah satrur ', 'hanisye caparan api', 'isvaro.aham aham bhogi ', 'siddhoham balavan sukhi', '', '', 'asau (that) maya (by me) hatah (has been killed) satruh (enemy) hanisye (I shall kill) ch (also) aparan (others) api (certainly) isvarah (the lord) aham (I am) aham (I am) bhogi (the enjoyer) siddhah (perfect) aham (I am) bala-van (powerful) sukhi (happy)', NULL, NULL, 'admin');
INSERT INTO gita VALUES (585, 16.015, 'Daivasura Sampad Vibhaga-Yoga', 585, 'adhyobhijanavan asmi ', 'konyosti sadrso maya', 'yaksye dasyami modisya ', 'ity ajnanavimohitah ', '', '', 'adhyah (wealthy) abhijana-van (surrounded by aristocratic relatives) asmi (I am) kah (who else) anyah (other) asti (there is) sadrsah (like) maya (me) yaksye (I shall sacrifice) dasyami (I shall give charity) modisye (I shall rejoice) iti (thus) ajnana (by ignorance) vimohitah (deluded by).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (586, 16.016, 'Daivasura Sampad Vibhaga-Yoga', 586, 'anekacittavibhranta ', 'mohajalasamavrtah', 'prasaktah kamabhogesu ', 'patanti narakesucau ', '', '', 'aneka (numerous) citta vibhrantah (perplexed by anxieties) moha (of illusions) jala (by a network) samavrtah (surrounded) prasaktah (attached) kama (lust) bhogesu (sense gratification) patanti (glides down) narake (into hell) asucau (unclean).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (587, 16.017, 'Daivasura Sampad Vibhaga-Yoga', 587, 'atmasambhavitah stabdha ', 'dhanamanamadanvitah', 'yajante namayajnais te ', 'dambhenavidhipurvakam ', '', '', 'atma-sambhavitah (self-complacent) stabdhah (impudent) dhana-mana (wealth and false prestige) mada anvitah (absorbed in pride) yajante (perform sacrifices) nama (in name only) yajnaih (with such a sacrifice) te (they) dambhena (out of pride) avidhi-purvakam (without following any rules and regulations).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (588, 16.018, 'Daivasura Sampad Vibhaga-Yoga', 588, 'ahamkaram balam darpam ', 'kamam krodham ch samsritah', 'mam atmaparadehesu ', 'pradvisantobhyasuyakah ', '', '', 'ahankaram (false ego) balam (strength) darpam (pride) kamam (lust) krodham (anger) ch (also) samsritah (having taken shelter of) mam (Me) atma (ones own) para dehesu (in other bodies) pradvisantah (blasphemes) abhyasuyakah (envious).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (589, 16.019, 'Daivasura Sampad Vibhaga-Yoga', 589, 'tan aham dvisatah kruran ', 'samsaresu naradhaman', 'ksipamy ajasram asubhan ', 'asurisv eva yonisu ', '', '', 'tan (those) aham (I) dvisatah (envious) kruran (mischievous) samsaresu (into the ocean of material existence) nara-adhaman (the lowest of mankind) ksipami (put) ajasram (innumerable) asubhan (inauspicious) asurisu (demoniac) eva (certainly) yonisu (in the wombs).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (590, 16.020, 'Daivasura Sampad Vibhaga-Yoga', 590, 'asurim yonim apanna mudha ', 'janmanijanmani', 'mam aprapyaiva kaunteya ', 'tato yanty adhamam gatim', '', '', 'asurim (demoniac) yonim (species) apannah (gaining) mudhah (the foolish) janmani janmani (in birth after birth) mam (unto Me) aprapya (without achieving) eva (certainly) kaunteya (O son of Kunti) tatah (thereafter) yanti (goes) adhamam (condemned) gatim (destination).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (591, 16.021, 'Daivasura Sampad Vibhaga-Yoga', 591, 'trividham narakasyedam ', 'dvaram nasanam atmanah', 'kamah krodhas tatha ', 'lobhas tasmad etat trayam tyajet ', '', '', 'tri-vidham (three kinds of) narakasya (hellish) idam (this) dvaram (gate) nasanam (destructive) atmanah (of the self) kamah (lust) krodhah (anger) tatha (as well as) lobhah (greed) tasmat (therefore) etat (these) trayam (three) tyajet (must give up).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (592, 16.022, 'Daivasura Sampad Vibhaga-Yoga', 592, 'etair vimuktah kaunteya ', 'tamodvarais tribhir narah', 'acaraty atmanah sreyas ', 'tato yati param gatim ', '', '', 'etaih (by these) vimuktah (being liberated) kaunteya (O son of Kunti) tamah-dvaraih (the gates of ignorance) tribhih (three kinds of) narah (a person) acarati (performs) atmanah (self) sreyah (benediction) tatah (thereafter) yati (goes) param (supreme) gatim (destination).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (593, 16.023, 'Daivasura Sampad Vibhaga-Yoga', 593, 'yah sastravidhim utsrjya ', 'vartate kamakaratah', 'na sa siddhim avapnoti na ', 'sukham na param gatim', '', '', 'yah (anyone) sastra-vidhim (the regulations of the scriptures) utsrjya (giving up) vartate (remains) kama-karatah (acting whimsically in lust) na (never) sah (he) siddhim (perfection) avapnoti (achieves) na (never) sukham (happiness) na (never) param (the supreme) gatim (perfectional stage).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (594, 16.024, 'Daivasura Sampad Vibhaga-Yoga', 594, 'tasmac chastram pramanam te', 'karyakaryavyavasthitau', 'jnatva sastravidhanoktam ', 'karma kartum iharhasi', '', '', 'tasmat (therefore) sastram (scriptures) pramanam (evidence) te (your) karya (duty) akarya (forbidden activities) vyavasthitau (in determining) jnatva (knowing) sastra (of scripture) vidhana (regulations) uktam (as declared) karma (work) kartum (to do) iha arhasi (you should do it).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (595, 17.001, 'Sraddhatraya Vibhaga-Yoga', 595, 'arjuna uvach; अर्जुन उवाच', 'ye sastravidhim utsrjya ', 'yajante sraddhayanvitah', 'tesam nistha tu ka Krishna ', 'sattvam aho rajas tamah ', '', 'arjunah uvach (Arjuna says) ye (those) sastra-vidhim (the regulations of scripture) utsrjya (giving up) yajante (worship) sraddhaya (full faith) anvitah (possessed of) tesam (of them) nistha (faith) tu (but) ka (what is that) Krishna (O Krishna) sattvam (in goodness) aho (says) rajah (in passion) tamah (in ignorance).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (596, 17.002, 'Sraddhatraya Vibhaga-Yoga', 596, 'sri-bhagavan uvach; श्रीभगवानुवाच', 'trividha bhavati ', 'sraddha dehinam sa svabhavaja', 'sattviki rajasi ', 'caiva tamasi ceti tam srnu ', '', 'sri-bhagavan uvach (The Lord says) tri-vidha (three kinds) bhavati (become) sraddha (faith) dehinam (of the embodied) sa (that) sva-bhava-ja (according to his mode of material nature) sattviki (mode of goodness) rajasi (mode of passion) ch (also) eva (certainly) tamasi (mode of ignorance) ch (and) iti (thus) tam (that) srnu (hear from Me).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (597, 17.003, 'Sraddhatraya Vibhaga-Yoga', 597, 'sattvanurupa sarvasya ', 'sraddha bhavati bharata', 'sraddhamayoyam puruso ', 'yo yacchraddhah sa eva sah', '', '', 'sattva-anurupa (according to the existence) sarvasya (of everyone) sraddha (faith) bhavati (becomes) bharata (O son of Bharata) sraddha (faith) mayah (full) ayam (this) purusah (living entity) yah (anyone) yat (that) sraddhah (faith) sah (that) eva (certainly) sah (he).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (598, 17.004, 'Sraddhatraya Vibhaga-Yoga', 598, 'yajante sattvika devan ', 'yaksaraksamsi rajasah', 'pretan bhutaganans canye ', 'yajante tamasa janah ', '', '', 'yajante (worship) sattvikah (those who are in the mode of goodness) devan (demigods) yaksa-raksamsi rajasah (those who are in the mode of passion worship demons) pretan (dead spirits) bhuta-ganan (ghosts) ch anye (and others) yajante (worship) tamasah (in the mode of ignorance) janah (people).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (599, 17.005, 'Sraddhatraya Vibhaga-Yoga', 599, 'asastravihitam ghoram ', 'tapyante ye tapo janah', 'dambhahamkarasamyuktah ', 'kamaragabalanvitah ', '', '', 'asastra (not mentioned in the scriptures) vihitam (directed) ghoram (harmful to others) tapyante (undergo penances) ye (those) tapah (austerities) janah (persons) dambha (pride) ahankara (egoism) samyuktah (engaged) kama (lust) raga (attachment) bala (force) anvitah (impelled by) ', NULL, NULL, 'admin');
INSERT INTO gita VALUES (600, 17.006, 'Sraddhatraya Vibhaga-Yoga', 600, 'karsayantah sarirastham ', 'bhutagramam acetasah', 'mam caivantahsarirastham ', 'tan viddhy asuraniscayan', '', '', 'karsayantah (tormenting) sarira-stham (situated within the body) bhuta-gramam (combination of material elements) acetasah (by such a misled mentality) mam (to Me) ch (also) eva (certainly) antah (within) sarira-stham (situated in the body) tan (them) viddhi (understand) asura (demons) niscayan (certainly).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (601, 17.007, 'Sraddhatraya Vibhaga-Yoga', 601, 'aharas tv api sarvasya ', 'trividho bhavati priyah', 'yajnas tapas tatha danam ', 'tesam bhedam imam srnu', '', '', 'aharah (eating) tu (certainly) api (also) sarvasya (of everyone) tri-vidhah (three kinds) bhavati (there are) priyah (dear) yajnah (sacrifice) tapah (austerity) tatha (also) danam (charity) tesam (of them) bhedam (differences) imam (thus) srnu (hear).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (602, 17.008, 'Sraddhatraya Vibhaga-Yoga', 602, 'ayuhsattvabalarogyasukhapritivivardhanah', 'rasyah snigdhah sthira ', 'hrdya aharah sattvikapriyah', '', '', '', 'ayuh (duration of life) sattva (existence) bala (strength) arogya (health) sukha (happiness) priti (satisfaction) vivardhanah (increasing) rasyah (juicy) snigdhah (fatty) sthirah (enduring) hrdyah (pleasing to the heart) aharah (food) sattvika (goodness) priyah (palatable).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (603, 17.009, 'Sraddhatraya Vibhaga-Yoga', 603, 'katvamlalavanatyusnatiksnaruksavidahinah', 'ahara rajasasyesta ', 'duhkhasokamayapradah ', '', '', '', 'katu (bitter) amla (sour) lavana (salty) ati-usna (very hot) tiksna (pungent) ruksa (dry) vidahinah (burning) aharah (food) rajasasya (in the mode of passion) istah (palatable) duhkha (distress) soka (misery) amaya pradah (causing disease).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (604, 17.010, 'Sraddhatraya Vibhaga-Yoga', 604, 'yatayamam gatarasam puti ', 'paryusitam ch yat', 'ucchistam api camedhyam ', 'bhojanam tamasapriyam', '', '', 'yata-yamam (food cooked three hours before being eaten) gata-rasam (tasteless) puti (bad smelling) paryusitam (decomposed) ch (also) yat (that which) ucchistam (remnants of food eaten by others) api (also) ch (and) amedhyam (untouchable) bhojanam (eating) tamasa (in the mode of darkness) priyam (dear).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (605, 17.011, 'Sraddhatraya Vibhaga-Yoga', 605, 'aphalanksibhir yajno ', 'vidhidrsto ya ijyate', 'yastavyam eveti manah ', 'samadhaya sa sattvikah ', '', '', 'aphala-akanksibhih (devoid of desire for result) yajnah (sacrifice) vidhi (accordingly) distah (direction) yah (anyone) ijyate (performs) yastavyam (must be performed) eva (certainly) iti (thus) manah (mind) samadhaya (fixed in) sah (he) sattvikah (is in the mode of goodness).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (606, 17.012, 'Sraddhatraya Vibhaga-Yoga', 606, 'abhisamdhaya tu phalam ', 'dambhartham api caiva yat', 'ijyate bharatasrestha ', 'tam yajnam viddhi rajasam ', '', '', 'abhisandhaya (desiring) tu (but) phalam (the result) dambha (pride) artham (for the sake of) api (also) ch (and) eva (certainly) yat (that which) ijyate (worship) bharata-srestha (O chief of the Bharatas) tam (that) yajnam (sacrifice) viddhi (know) rajasam (in the mode of passion).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (607, 17.013, 'Sraddhatraya Vibhaga-Yoga', 607, 'vidhihinam asrstannam ', 'mantrahinam adaksinam', 'sraddhavirahitam yajnam ', 'tamasam paricaksate ', '', '', 'vidhi-hinam (without scriptural direction) asrsta-annam (without distribution of prasadam) mantra-hinam (with no chanting of the Vedic hymns) adaksinam (with no remunerations to the priests) sraddha (faith) virahitam (without) yajnam (sacrifice) tamasam (in the mode of ignorance) paricaksate (is to be considered).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (608, 17.014, 'Sraddhatraya Vibhaga-Yoga', 608, 'devadvijaguruprajnapujanam ', 'saucam arjavam', 'brahmacaryam ahimsa ch ', 'sariram tapa ucyate ', '', '', 'deva (the Supreme Lord) dvija (the brahmana) guru (the spiritual master) prajna (worshipable personalities) pujanam (worship) saucam (cleanliness) arjavam (simplicity) brahmacaryam (celibacy) ahimsa (nonviolence) ch (also) sariram (pertaining to the body) tapah (austerity) ucyate (is says to be).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (609, 17.015, 'Sraddhatraya Vibhaga-Yoga', 609, 'anudvegakaram vakyam ', 'satyam priyahitam ch yat', 'svadhyayabhyasanam caiva ', 'vanmayam tapa ucyate', '', '', 'anudvega (not agitating) karam (producing) vakyam (words) satyam (truthful) priya (dear) hitam (beneficial) ch (also) yat (which) svadhyaya (Vedic study) abhyasanam (practice) ch (also) eva (certainly) van-mayam (of the voice) tapah (austerity) ucyate (is says to be).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (610, 17.016, 'Sraddhatraya Vibhaga-Yoga', 610, 'manahprasadah saumyatvam ', 'maunam atmavinigrahah', 'bhavasamsuddhir ity ', 'etat tapo manasam ucyate ', '', '', 'manah-prasadah (satisfaction of the mind) saumyatvam (without duplicity towards others) maunam (gravity) atma (self) vinigrahah (control) bhava (nature) samsuddhih (purification) iti (thus) etat (that is) tapah (austerity) manasam (of the mind) ucyate (is says to be).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (611, 17.017, 'Sraddhatraya Vibhaga-Yoga', 611, 'sraddhaya paraya taptam ', 'tapas tat trividham naraih', 'aphalakanksibhir yuktaih ', 'sattvikam paricaksate ', '', '', 'sraddhaya (with faith) paraya (transcendental) taptam (execution) tapah (austerity) tat (that) tri-vidham (three kinds) naraih (by men) aphala-akanksibhih (without desires for fruits) yuktaih (engaged) sattvikam (in the mode of goodness) paricaksate (is called).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (612, 17.018, 'Sraddhatraya Vibhaga-Yoga', 612, 'satkaramanapujartham tapo ', 'dambhena caiva yat', 'kriyate tad iha proktam ', 'rajasam calam adhruvam', '', '', 'sat-kara (respect) mana (honor) puja-artham (for worship) tapah (austerity) dambhena (pride) ch (also) eva (certainly) yat (which is) kriyate (performed) tat (that) iha (in this world) proktam (is says) rajasam (in the mode of passion) calam (flickering) adhruvam (temporary).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (613, 17.019, 'Sraddhatraya Vibhaga-Yoga', 613, 'mudhagrahenatmano yat ', 'pidaya kriyate tapah', 'parasyotsadanartham va ', 'tat tamasam udahrtam ', '', '', 'mudha (foolish) grahena (with endeavor) atmanah (of ones own self) yat (which) pidaya (by torture) kriyate (is performed) tapah (penance) parasya (to others) utsadana-artham (causing annihilation) va (or) tat (that) tamasam (in the mode of darkness) udahrtam (is says to be).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (614, 17.020, 'Sraddhatraya Vibhaga-Yoga', 614, 'datavyam iti yad danam ', 'diyatenupakarine', 'dese kale ch patre ch ', 'tad danam sattvikam smrtam', '', '', 'datavyam (worth giving) iti (thus) yat (that which) danam (charity) diyate (given) anupakarine (to any person irrespective of doing good) dese (in place) kale (in time) ch (also) patre (suitable person) ch (and) tat (that) danam (charity) sattvikam (in the mode of goodness) smrtam (consider).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (615, 17.021, 'Sraddhatraya Vibhaga-Yoga', 615, 'yat tu prattyupakarartham ', 'phalam uddisya va punah', 'diyate ch pariklistam tad ', 'danam rajasam smrtam', '', '', 'yat (that which) tu (but) prati-upakara-artham (for the sake of getting some return) phalam (result) uddisya (desiring) va (or) punah (again) diyate (is given in charity) ch (also) pariklistam (grudgingly) tat (that) danam (charity) rajasam (in the mode of passion) smrtam (is understood to be).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (616, 17.022, 'Sraddhatraya Vibhaga-Yoga', 616, 'adesakale yad danam ', 'apatrebhyas ch diyate', 'asatkrtam avajnatam tat ', 'tamasam udahrtam ', '', '', 'adesa (unpurified place) kale (unpurified time) yat (that which) danam (charity) upatrebhyah (to unworthy persons) ch (also) diyate (is given) asat-krtam (without respect) avajnatam (without proper attention) tat (that) tamasam (in the mode of darkness) udahrtam (is says to be).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (617, 17.023, 'Sraddhatraya Vibhaga-Yoga', 617, 'om tat sad iti nirdeso ', 'brahmanas trividhah smrtah', 'brahmanas tena vedas ch ', 'yajnas ch vihitah pura ', '', '', 'om (indication of the Supreme) tat (that) sat (eternal) iti (that) nirdesah (indication) brahmanah (of the Supreme) tri-vidhah (three kinds) smrtah (consider) brahmanah (the brahmanas) tena (therefore) vedah (the Vedic literature) ch (also) yajnah (sacrifice) ch (also) vihitah (used) pura (formerly).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (618, 17.024, 'Sraddhatraya Vibhaga-Yoga', 618, 'tasmad om ity udahrtya ', 'yajnadanatapahkriyah', 'pravartante vidhanoktah ', 'satatam brahmavadinam', '', '', 'tasmat (therefore) om (beginning with om) iti (thus) udahrtya (indicating) yajna (sacrifice) dana (charity) tapah (penance) kriyah (performances) pravartante (begin) vidhana-uktah (according to scriptural regulation) satatam (always) brahma-vadinam (of the transcendentalists).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (619, 17.025, 'Sraddhatraya Vibhaga-Yoga', 619, 'tad ity anabhisamdhaya ', 'phalam yajnatapahkriyah', 'danakriyas ch vividhah ', 'kriyante moksakanksibhih', '', '', 'tat (that) iti (they) anabhisandhaya (without fruitive result) phalam (result of sacrifice) yajna (sacrifice) tapah (penance) kriyah (activities) dana (charity) kriyah (activities) ch (also) vividhah (varieties) kriyante (done) moksa-kanksibhih (those who actually desire liberation).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (620, 17.026, 'Sraddhatraya Vibhaga-Yoga', 620, 'sadbhave sadhubhave ch ', 'sad ity etat prayujyate', 'prasaste karmani tatha ', 'sacchabdah partha yujyate', '', '', 'sat-bhave (in the sense of the nature of the Supreme) sadhu-bhave (in the sense of the nature of devotion) ch (also) sat (the supreme) iti (thus) etat (this) prayujyate (is used) prasaste (bona fide) karmani (activities) tatha (also) sat-sabdah (the sound sat) partha (O son of Prtha) yujyate (is used) ', NULL, NULL, 'admin');
INSERT INTO gita VALUES (621, 17.027, 'Sraddhatraya Vibhaga-Yoga', 621, 'yajne tapasi dane ch ', 'sthitih sad iti cocyate', 'karma caiva tadarthiyam ', 'sad ity evabhidhiyate ', '', '', 'yajne (sacrifice) tapasi (in penance) dane (charity) ch (also) sthitih (situated) sat (the Supreme) iti (thus) ch (and) ucyate (pronounced) karma (work) ch (also) eva (certainly) tat (that) arthiyam (are meant) sat (Supreme) iti (thus) eva (certainly) abhidhiyate (is practiced).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (622, 17.028, 'Sraddhatraya Vibhaga-Yoga', 622, 'asraddhaya hutam dattam ', 'tapas taptam krtam ch yat', 'asad ity ucyate partha na ', 'ch tat prepya no iha ', '', '', 'asraddhaya (without faith) hutam (offered in sacrifice) dattam (given) tapah (penance) taptam (executed) krtam (performed) ch (also) yat (that which) asat (false) iti (thus) ucyate (is says to be) partha (O son of Prtha) na (never) ch (also) tat (that) pretya (after death) no (nor) iha (in this life).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (623, 18.001, 'Moksa-Sanyasa-Yoga', 623, 'arjuna uvach; अर्जुन उवाच', 'samnyasasya mahabaho ', 'tattvam icchami veditum', 'tyagasya ch hrsikesa ', 'prthak kesinisudana ', '', 'arjunah uvach (Arjuna says) sannyasasya (renunciation) maha-baho (O mighty-armed one) tattvam (truth) icchami (I wish) veditum (to understand) tyagasya (of renunciation) ch (also) hrsikesa (O master of the senses) prthak (differently) kesi-nisudana (O killer of the Kesi demon).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (624, 18.002, 'Moksa-Sanyasa-Yoga', 624, 'sri-bhagavan uvach; श्रीभगवानुवाच', 'kamyanam karmanam nyasam', 'samnyasam kavayo', 'viduh sarvakarmaphalatyagam ', 'prahus tyagam vicaksanah', '', 'sri-bhagavan uvach (The Lord says) kamyanam (with desire) karmanam (activities) nyasam (renunciation) sannyasam (renounced order of life) kavayah (the learned) viduh (know) sarva (of all) karma (activities) phala (of results) tyagam (renunciation) prahuh (call) tyagam (renunciation) vicaksanah (the experienced).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (625, 18.003, 'Moksa-Sanyasa-Yoga', 625, 'tyajyam dosavad ity eke ', 'karma prahur manisinah', 'yajnadanatapahkarma na ', 'tyajyam iti capare ', '', '', 'tyajyam (must be given up) dosa-vat (as an evil) iti (thus) eke (one group) karma (work) prahuh (says) manisinah (of great thinkers) yajna (sacrifice) dana (charity) tapah (penance) karma (work) na (never) tyajyam (is to be given up) iti (thus) ch (certainly) apare (others).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (626, 18.004, 'Moksa-Sanyasa-Yoga', 626, 'niscayam srnu me ', 'tatra tyage bharatasattama', 'tyago hi purusavyaghra ', 'trividhah samprakirtitah ', '', '', 'niscayam (certainty) srnu (hear) me (from Me) tatra (there) tyage (in the matter of renunciation) bharata-sat-tama (O best of the Bharatas) tyagah (renunciation) hi (certainly) purusa-vyaghra (O tiger among human beings) tri-vidhah (three kinds) samprakirtitah (is declared).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (627, 18.005, 'Moksa-Sanyasa-Yoga', 627, 'yajnadanatapahkarma na ', 'tyajyam karyam eva tat', 'yajno danam tapas caiva ', 'pavanani manisinam ', '', '', 'yajna (sacrifice) dana (charity) tapah (penance) karma (activities) na (never) tyajyam (to be given up) karyam (must be done) eva (certainly) tat (that) yajnah (sacrifice) danam (charity) tapah (penance) ch (also) eva (certainly) pavanani (purifying) manisinam (even of the great souls).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (628, 18.006, 'Moksa-Sanyasa-Yoga', 628, 'etany api tu karmani ', 'sangam tyaktva phalani ch', 'kartavyaniti me partha ', 'niscitam matam uttamam ', '', '', 'etani (all this) api (certainly) tu (must) karmani (activities) sangam (association) tyaktva (renouncing) phalani (results) ch (also) kartavyani (as duty) iti (thus) me (my) partha (O son of Prtha) niscitam (definite) matam (opinion) uttamam (the best).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (629, 18.007, 'Moksa-Sanyasa-Yoga', 629, 'niyatasya tu samnyasah ', 'karmano nopapadyate', 'mohat tasya parityagas ', 'tamasah parikirtitah ', '', '', 'niyatasya (prescribed duties) tu (but) sannyasah (renunciation) karmanah (activities) na (never) upapadyate (is deserved) mohat (by illusion) tasya (of which) parityagah (renunciation) tamasah (in the mode of ignorance) parikirtitah (is declared).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (630, 18.008, 'Moksa-Sanyasa-Yoga', 630, 'duhkham ity eva yat ', 'karma kayaklesabhayat tyajet', 'sa krtva rajasam ', 'tyagam naiva tyagaphalam labhet', '', '', 'duhkham (unhappy) iti (thus) eva (certainly) yat (that which) karma (work) kaya (body) klesa (troublesome) bhayat (out of fear) tyajet (gives up) sah (that) krtva (after doing) rajasam (in the mode of passion) tyagam (renunciation) na eva (certainly not) tyaga (renounced) phalam (results) labhet (gain).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (631, 18.009, 'Moksa-Sanyasa-Yoga', 631, 'karyam ity eva yat ', 'karma niyatam kriyaterjuna', 'sangam tyaktva phalam ', 'caiva sa tyagah sattviko matah ', '', '', 'karyam (must be done) iti (thus) eva (thus) yat (that which) karma (work) niyatam (prescribed) kriyate (performed) arjuna (O Arjuna) sangam (association) tyaktva (giving up) phalam (result) ch (also) eva (certainly) sah (that) tyagah (renunciation) sattvikah (in the mode of goodness) matah (in my opinion).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (632, 18.010, 'Moksa-Sanyasa-Yoga', 632, 'na dvesty akusalam ', 'karma kusale nanusajjate', 'tyagi sattvasamavisto ', 'medhavi chinnasamsayah ', '', '', 'na (never) dvesti (hates) akusalam (inauspicious) karma (work) kusale (in the auspicious) na (nor) anusajjate (becomes attached) tyagi (the renouncer) sattva (goodness) samavistah (absorbed in) medhavi (intelligent) chinna (cut up) samsayah (all doubts).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (633, 18.011, 'Moksa-Sanyasa-Yoga', 633, 'na hi dehabhrta sakyam ', 'tyaktum karmany asesatah', 'yas tu karmaphalatyagi ', 'sa tyagity abhidhiyate ', '', '', 'na (never) hi (certainly) deha-bhrta (of the embodied) sakyam (possible) tyaktum (to renounce) karmani (activities of) asesatah (altogether) yah tu (anyone who) karma (work) phala (results) tyagi (renouncer) sah (he) tyagi (the renouncer) iti (thus) abhidhiyate (it is says).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (634, 18.012, 'Moksa-Sanyasa-Yoga', 634, 'anistam istam misram ', 'ch trividham karmanah phalam', 'bhavaty atyaginam pretya ', 'na tu samnyasinam kvacit', '', '', 'anistam (leading to hell) istam (leading to heaven) misram ch (or mixture) tri-vidham (three kinds) karmanah (work) phalam (result) bhavati (becomes) atyaginam (of the renouncer) pretya (after death) na tu (but not) sannyasinam (of the renounced order) kvacit (at any time).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (635, 18.013, 'Moksa-Sanyasa-Yoga', 635, 'pancaitani mahabaho ', 'karanani nibodha me', 'samkhye krtante proktani ', 'siddhaye sarvakarmanam', '', '', 'panca (five) etani (all these) maha-baho (O mighty-armed one) karanani (cause) nibodha (just understand) me (from Me) sankhye (in the Vedas) krta-ante (after performance) proktani (says) siddhaye (perfection) sarva (all) karmanam (actuated).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (636, 18.014, 'Moksa-Sanyasa-Yoga', 636, 'adhisthanam tatha karta ', 'karanam ch prthagvidham', 'vividhas ch prthakcesta ', 'daivam caivatra pancamam', '', '', 'adhisthanam (place) tatha (also) karta (worker) karanam ch (and instruments) prthak-vidham (different kinds) vividhah ch (varieties) prthak (separately) cestah (endeavor) daivam (the Supreme) ch (also) eva (certainly) atra (here) pancamam (five).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (637, 18.015, 'Moksa-Sanyasa-Yoga', 637, 'sariravanmanobhir yat ', 'karma prarabhate narah', 'nyayyam va viparitam va ', 'pancaite tasya hetavah', '', '', 'sarira (body) vak (speech) manobhih (by the mind) yat (anything) karma (work) prarabhate (begins) narah (a person) nyayyam (right) va (or) viparitam (the opposite) va (or) panca (five) ete (all these) tasya (its) hetavah (causes).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (638, 18.016, 'Moksa-Sanyasa-Yoga', 638, 'tatraivam sati kartaram ', 'atmanam kevalam tu yah', 'pasyaty akrtabuddhitvan na ', 'sa pasyati durmatih ', '', '', 'tatra (there) evam (certainly) sati (being) kartaram (of the worker) atmanam (the self) kevalam (only) tu (but) yah (anyone) pasyati (sees) akrta-buddhitvat (due to unintelligence) na (never) sah (he) pasyati (sees) durmatih (foolish).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (639, 18.017, 'Moksa-Sanyasa-Yoga', 639, 'yasya nahamkrto bhavo ', 'buddhir yasya na lipyate', 'hatva.api sa imaml lokan ', 'na hanti na nibadhyate ', '', '', 'yasya (of one who) na (never) ahankrtah (false ego) bhavah (nature) buddhih (intelligence) yasya (one who) na (never) lipyate (is attached) hatva api (even killing) sah (he) iman (this) lokan (world) na (never) hanti (kills) na (never) nibadhyate (becomes entangled).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (640, 18.018, 'Moksa-Sanyasa-Yoga', 640, 'jnanam jneyam parijnata ', 'trividha karmacodana', 'karanam karma karteti ', 'trividhah karmasamgrahah', '', '', 'jnanam (knowledge) jneyam (objective) parijnata (the knower) tri-vidha (three kinds) karma (work) codana (impetus) karanam (the senses) karma (work) karta (the doer) iti (thus) tri-vidhah (three kinds) karma (work) sangrahah (accumulation).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (641, 18.019, 'Moksa-Sanyasa-Yoga', 641, 'jnanam karma ch karta ', 'ch tridhaiva gunabhedatah', 'procyate gunasamkhyane ', 'yathavac chrnu tany api', '', '', 'jnanam (knowledge) karma (work) ch (also) karta (worker) ch (also) tridha (three kinds) eva (certainly) guna-bhedatah (in terms of different modes of material nature) procyate (is says) guna-sankhyane (in terms of different modes) yatha-vat (as they act) srnu (hear) tani (all of them) api (also).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (642, 18.020, 'Moksa-Sanyasa-Yoga', 642, 'sarvabhutesu yenaikam ', 'bhavam avyayam iksate', 'avibhaktam vibhaktesu ', 'taj jnanam viddhi sattvikam', '', '', 'sarva-bhutesu (in all living entities) yena (by whom) ekam (one) bhavam (situation) avyayam (imperishable) iksate (does see) avibhaktam (undivided) vibhaktesu (in the numberless divided) tat (that) jnanam (knowledge) viddhi (know) sattvikam (in the mode of goodness).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (643, 18.021, 'Moksa-Sanyasa-Yoga', 643, 'prthaktvena tu yaj jnanam ', 'nanabhavan prthagvidhan', 'vetti sarvesu bhutesu taj ', 'jnanam viddhi rajasam ', '', '', 'prthaktvena (because of division) tu (but) yat jnanam (which knowledge) nana-bhavan (multifarious situations) prthak-vidhan (differently) vetti (one who knows) sarvesu (in all) bhutesu (living entities) tat jnanam (that knowledge) viddhi (must be known) rajasam (in terms of passion).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (644, 18.022, 'Moksa-Sanyasa-Yoga', 644, 'yat tu krtsnavad ekasmin ', 'karye saktam ahetukam', 'atattvarthavad alpam ch ', 'tat tamasam udahrtam ', '', '', 'yat (that which) tu (but) krtsna-vat (all in all) ekasmin (in one) karye (work) saktam (attached) ahaitukam (without cause) atattva-artha-vat (without reality) alpam (very meager) ch (and) tat (that) tamasam (in the mode of darkness) udahrtam (is spoken).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (645, 18.023, 'Moksa-Sanyasa-Yoga', 645, 'niyatam sangarahitam ', 'aragadvesatah krtam', 'aphalaprepsuna karma ', 'yat tat sattvikam ucyate ', '', '', 'niyatam (regulative) sanga-rahitam (without attachment) araga-dvesatah (without love or hatred) krtam (done) aphala-prepsuna (without fruitive result) karma (acts) yat (which) tat (that) sattvikam (in the mode of goodness) ucyate (is called).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (646, 18.024, 'Moksa-Sanyasa-Yoga', 646, 'yat tu kamepsuna karma ', 'sahamkarena va punah', 'kriyate bahulayasam tad ', 'rajasam udahrtam ', '', '', 'yat (that which) tu (but) kama-ipsuna (with fruitive result) karma (work) sa-ahankarena (with ego) va (or) punah (again) kriyate (performed) bahula-ayasam (with great labor) tat (that) rajasam (in the mode of passion) udahrtam (is says to be).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (647, 18.025, 'Moksa-Sanyasa-Yoga', 647, 'anubandham ksayam himsam ', 'anapeksya ch paurusam', 'mohad arabhyate karma ', 'yat tat tamasam ucyate ', '', '', 'anubandham (future bondage) ksayam (destruction) himsam (violence) anapeksya (without consideration of consequences) ch (also) paurusam (distressing to others) mohat (by illusion) arabhyate (begun) karma (work) yat (which) tat (that) tamasam (in the mode of ignorance) ucyate (is says to be).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (648, 18.026, 'Moksa-Sanyasa-Yoga', 648, 'muktasangonahamvadi ', 'dhrtyutsahasamanvitah', 'siddhyasiddhyor nirvikarah ', 'karta sattvika ucyate', '', '', 'mukta-sangah (liberated from all material association) anaham-vadi (without false ego) dhrti-utsaha (with great enthusiasm) samanvitah (qualified in that way) siddhi (perfection) asiddhyoh (failure) nirvikarah (without change) karta (worker) sattvikah (in the mode of goodness) ucyate (is says to be).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (649, 18.027, 'Moksa-Sanyasa-Yoga', 649, 'ragi karmaphalaprepsur ', 'lubdho himsatmakosucih', 'harsasokanvitah karta ', 'rajasah parikirtitah ', '', '', 'ragi (very much attached) karma-phala (to the fruit of the work) prepsuh (desiring) lubdhah (greedy) himsa-atmakah (and always envious) asucih (unclean) harsa-soka-anvitah (complicated, with joy and sorrow) karta (such a worker) rajasah (in the mode of passion) parikirtitah (is declared).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (650, 18.028, 'Moksa-Sanyasa-Yoga', 650, 'ayuktah prakrtah ', 'stabdhah satho naiskrtikolasah', 'visadi dirghasutri ch ', 'karta tamasa ucyate ', '', '', 'ayuktah (without reference to scriptural injunctions) prakrtah (materialistic) stabdhah (obstinate) sathah (deceitful) naiskrtikah (expert in insulting others) alasah (lazy) visadi (morose) dirgha-sutri (procrastinating) ch (also) karta (worker) tamasah (in the mode of ignorance) ucyate (is says to be).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (651, 18.029, 'Moksa-Sanyasa-Yoga', 651, 'buddher bhedam dhrtes ', 'caiva gunatas trividham srnu', 'procyamanam asesena ', 'prthaktvena dhanamjaya ', '', '', 'buddheh (of intelligence) bhedam (differences) dhrteh (of steadiness) ch (also) eva (certainly) gunatah (by the modes of material nature) tri-vidham (the three kinds of) srnu (just hear) procyamanam (as described by Me) asesena (in detail) prthaktvena (differently) dhananjaya (O winner of wealth).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (652, 18.030, 'Moksa-Sanyasa-Yoga', 652, 'pravrttim ch nivrttim ch ', 'karyakarye bhayabhaye', 'bandham moksam ch ya ', 'vetti buddhih sa partha sattviki ', '', '', 'pravrttim (deserving) ch (also) nivrttim (not deserving) ch (and) karya (work) akarye (reaction) bhaya (fearful) abhaye (fearlessness) bandham (obligation) moksam ch (and liberation) ya (that which) vetti (knows) buddhih (understanding) sa (that) partha (O son of Prtha) sattviki (in the mode of goodness).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (653, 18.031, 'Moksa-Sanyasa-Yoga', 653, 'yaya dharmam adharmam ch ', 'karyam cakaryam eva ch', 'ayathavat prajanati buddhih sa ', 'partha rajasi ', '', '', 'yaya (by which) dharmam (principles of religion) adharmam (and irreligion) ch (and) karyam (work) ch (also) akaryam (what ought not to be done) eva (certainly) ch (also) ayatha-vat (not perfectly) prajanati (knows) buddhih (intelligence) sa (that) partha (O son of Prtha) rajasi (in the mode of passion).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (654, 18.032, 'Moksa-Sanyasa-Yoga', 654, 'adharmam dharmam iti ya ', 'manyate tamasavrta', 'sarvarthan viparitans ch ', 'buddhih sa partha tamasi', '', '', 'adharmam (irreligion) dharmam (religion) iti (thus) ya (which) manyate (thinks) tamasa (by illusion) avrta (covered) sarva-arthan (all things) viparitan (the wrong direction) ch (also) buddhih (intelligence) sa (that) partha (O son of Prtha) tamasi (the mode of ignorance).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (655, 18.033, 'Moksa-Sanyasa-Yoga', 655, 'dhrtya yaya dharayate ', 'manahpranendriyakriyah', 'yogenavyabhicarinya ', 'dhrtih sa partha sattviki ', '', '', 'dhrtya (determination) yaya (by which) dharayate (is sustained) manah (mind) prana (life) indriya (senses) kriyah (activities) yogena (by yoga practice) avyabhicarinya (without any break) dhrtih (such determination) sa (that) partha (O son of Prtha) sattviki (in the mode of goodness).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (656, 18.034, 'Moksa-Sanyasa-Yoga', 656, 'yaya tu dharmakamarthan ', 'dhrtya dharayaterjuna', 'prasangena phalakanksi ', 'dhrtih sa partha rajasi ', '', '', 'yaya (by which) tu (but) dharma-kama-arthan (for religiosity and economic development) dhrtya (by determination) dharayate (in such terms) arjuna (O Arjuna) prasangena (for that) phala-akanksi (desiring fruitive results) dhrtih (determination) sa (that) partha (O son of Prtha) rajasi (in the mode of passion).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (657, 18.035, 'Moksa-Sanyasa-Yoga', 657, 'yaya svapnam bhayam sokam ', 'visadam madam eva ch', 'na vimuncati durmedha ', 'dhrtih sa partha tamasi ', '', '', 'yaya (by which) svapnam (dream) bhayam (fearfulness) sokam (lamentation) visadam (moroseness) madam (illusion) eva (certainly) ch (also) na (never) vimuncati (is liberated) durmedha (unintelligent) dhrtih (determination) sa (that) partha (O son of Prtha) tamasi (in the mode of ignorance).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (658, 18.036, 'Moksa-Sanyasa-Yoga', 658, 'sukham tv idanim ', 'trividham srnu me bharatarsabha', 'abhyasad ramate yatra ', 'duhkhantam ch nigacchhati', '', '', 'sukham (happiness) tu (but) idanim (now) tri-vidham (three kinds) srnu (hear) me (from Me) bharata-rsabha (O best amongst the Bharatas) abhyasat (by practice) ramate (enjoyer) yatra (where) duhkha (distress) antam (end) ch (also) nigacchati (gains).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (659, 18.037, 'Moksa-Sanyasa-Yoga', 659, 'yat tadagre visam iva ', 'parinamemrtopamam', 'tat sukham sattvikam proktam', 'atmabuddhiprasadajam ', '', '', 'yat (that which) tat (that) agre (in the beginning) visam iva (like poison) pariname (at the end) amrta (nectar) upamam (compared to) tat (that) sukham (happiness) sattvikam (in the mode of goodness) proktam (is says) atma (self) buddhi (intelligence) prasada-jam (satisfactory).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (660, 18.038, 'Moksa-Sanyasa-Yoga', 660, 'visayendriyasanyogad ', 'yat tad agremrtopamam', 'pariname visam iva tat ', 'sukham rajasam smrtam ', '', '', 'visaya (objects of the senses) indriya (senses) samyogat (combination) yat (which) tat (that) agre (in the beginning) amrta-upamam (just like nectar) pariname (at the end) visam iva (like poison) tat (that) sukham (happiness) rajasam (in the mode of passion) smrtam (is considered).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (661, 18.039, 'Moksa-Sanyasa-Yoga', 661, 'yad agre canubandhe ch ', 'sukham mohanam atmanah', 'nidralasyapramadottham ', 'tat tamasam udahrtam ', '', '', 'yat (that which) agre (in the beginning) ch (also) anubandhe (by binding) ch (also) sukham (happiness) mohanam (illusion) atmanah (of the self) nidra (sleeping) alasya (laziness) pramada (illusion) uttham (produced of) tat (that) tamasam (in the mode of ignorance) udahrtam (is says to be).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (662, 18.040, 'Moksa-Sanyasa-Yoga', 662, 'na tad asti prthivyam va ', 'divi devesu va punah', 'sattvam prakrtijair muktam ', 'yad ebhih syat tribhir gunaih ', '', '', 'na (not) tat (that) asti (there is) prthivyam (within the universe) va (or) divi (in the higher planetary system) devesu (amongst the demigods) va (or) punah (again) sattvam (existence) prakrti-jaih (under the influence of material nature) muktam (liberated) yat (that) ebhih (by this) syat (so becomes) tribhih (by three) gunaih (modes of material nature).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (663, 18.041, 'Moksa-Sanyasa-Yoga', 663, 'brahmanaksatriyavisam ', 'sudranam ch paramtapa', 'karmani pravibhaktani ', 'svabhavaprabhavair gunaih', '', '', 'brahmana (the brahmanas) ksatriya (the ksatriyas) visam (the vaisyas) sudranam (the sudras) ch (and) parantapa (O subduer of the enemies) karmani (activities) pravibhaktani (are divided) svabhava (own nature) prabhavaih (born of) gunaih (by the modes of material nature).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (664, 18.042, 'Moksa-Sanyasa-Yoga', 664, 'samo damas tapah saucam ', 'ksantir arjavam eva ch', 'jnanam vijnanam astikyam ', 'brahmakarma svabhavajam', '', '', 'samah (peacefulness) damah (self-control) tapah (austerity) saucam (purity) ksantih (tolerance) arjavam (honesty) eva (certainly) ch (and) jnanam (knowledge) vijnanam (wisdom) astikyam (religiousness) brahma (of a brahmana) karma (duty) svabhava-jam (born of his own nature).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (665, 18.043, 'Moksa-Sanyasa-Yoga', 665, 'sauryam tejo dhrtir ', 'daksyam yuddhe capy apalayanam', 'danam isvarabhavas ch ', 'ksatram karma svabhavajam', '', '', 'sauryam (heroism) tejah (power) dhrtih (determination) daksyam (resourcefulness) yuddhe (in battle) ch (and) api (also) apalayanam (not fleeing) danam (generosity) isvara (leadership) bhavah (nature) ch (and) ksatram (ksatriya) karma (duty) svabhava-jam (born of his own nature).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (666, 18.044, 'Moksa-Sanyasa-Yoga', 666, 'krsigauraksyavanijyam ', 'vaisyakarma svabhavajam', 'paricaryatmakam karma ', 'sudrasyapi svabhavajam', '', '', 'krsi (plowing) go (cows) raksya (protection) vanijyam (trade) vaisya (vaisya) karma (duty) svabhava-jam (born of his own nature) paricarya (service) atmakam (nature) karma (duty) sudrasya (of the sudra) api (also) svabhava-jam (born of his own nature).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (667, 18.045, 'Moksa-Sanyasa-Yoga', 667, 'sve sve karmany abhiratah ', 'samsiddhim labhate narah', 'svakarmaniratah siddhim ', 'yatha vindati tac chrnu', '', '', 'sve (own) sve (own) karmani (in work) abhiratah (following) samsiddhim (perfection) labhate (achieves) narah (a man) sva-karma (by his own duty) niratah (engaged) siddhim (perfection) yatha (as) vindati (attains) tat (that) srnu (listen).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (668, 18.046, 'Moksa-Sanyasa-Yoga', 668, 'yatah pravrttir bhutanam ', 'yena sarvam idam tatam', 'svakarmana tam abhyarcya ', 'siddhim vindati manavah', '', '', 'yatah (from whom) pravrttih (the emanation) bhutanam (of all living entities) yena (by whom) sarvam (all) idam (this) tatam (is pervaded) sva-karmana (in his own duties) tam (Him) abhyarcya (by worshiping) siddhim (perfection) vindati (achieves) manavah (a man).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (669, 18.047, 'Moksa-Sanyasa-Yoga', 669, 'sreyan svadharmo vigunah ', 'paradharmot svanusthitat', 'svabhavaniyatam karma ', 'kurvan napnoti kilbisam', '', '', 'sreyan (better) sva-dharmah (ones own occupation) vigunah (imperfectly performed) para-dharmat (anothers occupation) suanusthitat (perfectly done) svabhava-niyatam (prescribed duties according to ones nature) karma (work) kurvan (performing) na (never) apnoti (achieve) kilbisam (sinful reactions).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (670, 18.048, 'Moksa-Sanyasa-Yoga', 670, 'sahajam karma kaunteya ', 'sadosam api na tyajet', 'sarvarambha hi dosena ', 'dhumenagnir ivavrtah ', '', '', 'saha-jam (born simultaneously) karma (work) kaunteya (O son of Kunti) sa-dosam (with fault) api (although) na (never) tyajet (to be given up) sarva-arambhah (any venture) hi (certainly) dosena (with fault) dhumena (with smoke) agnih (fire) iva (as) avrtah (covered).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (671, 18.049, 'Moksa-Sanyasa-Yoga', 671, 'asaktabuddhih sarvatra ', 'jitatma vigatasprhah', 'naiskarmyasiddhim paramam ', 'samnyasenadhigacchati', '', '', 'asakta-buddhih (unattached intelligence) sarvatra (everywhere) jita-atma (control of the mind) vigata-sprhah (without material desires) naiskarmya-siddhim (perfection of non-reaction) paramam (supreme) sannyasena (by the renounced order of life) adhigacchati (attains).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (672, 18.050, 'Moksa-Sanyasa-Yoga', 672, 'siddhim prapto yatha ', 'brahma tathapnoti nibodha me', 'samasenaiva kaunteya ', 'nistha jnanasya ya para ', '', '', 'siddhim (perfection) praptah (achieving) yatha (as) brahma (the Supreme) tatha (so) apnoti (achieves) nibodha (try to understand) me (from Me) samasena (summarily) eva (certainly) kaunteya (O son of Kunti) nistha (stage) jnanasya (of knowledge) ya (which) para (transcendental).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (673, 18.051, 'Moksa-Sanyasa-Yoga', 673, 'buddhya visuddhaya yukto ', 'dhrtyatmanam niyamya ch', 'sabdadin visayams tyaktva ', 'ragadvesau vyudasya ch', '', '', 'buddhya (by the intelligence) visuddhaya (fully purified) yuktah (such engagement) dhrtya (determination) atmanam (self) niyamya (regulated) ch (also) sabda-adin (the sense objects, such as sound, etc.) visayan (sense objects) tyaktva (giving up) raga (attachment) dvesau (hatred) vyudasya (having laid aside) ch (also)', NULL, NULL, 'admin');
INSERT INTO gita VALUES (674, 18.052, 'Moksa-Sanyasa-Yoga', 674, 'viviktasevi laghvasi ', 'yatavakkayamanasah', 'dhyanayogaparo nityam ', 'vairagyam samupasritah', '', '', 'vivikta-sevi (living in a secluded place) laghu-asi (eating a small quantity) yata-vak (control of speech) kaya (body) manasah (control of the mind) dhyana-yoga-parah (always absorbed in trance) nityam (twenty-four hours a day) vairagyam (detachment) samupasritah (taken shelter of)', NULL, NULL, 'admin');
INSERT INTO gita VALUES (675, 18.053, 'Moksa-Sanyasa-Yoga', 675, 'ahamkaram balam darpam ', 'kamam krodham', 'parigraham vimucya nirmamah ', 'santo brahmabhuyaya kalpate', '', '', 'ahankaram (false ego) balam (false strength) darpam (false pride) kamam (lust) krodham (anger) parigraham (acceptance of material things) vimucya (being delivered) nirmamah (without proprietorship) santah (peaceful) brahma-bhuyaya (to become self-realized) kalpate (is understood).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (676, 18.054, 'Moksa-Sanyasa-Yoga', 676, 'brahmabhutah prasannatma ', 'na socati na kanksati', 'samah sarvesu bhutesu ', 'madbhaktim labhate param', '', '', 'brahma-bhutah (being one with the Absolute) prasanna-atma (fully joyful) na (never) socati (laments) na (never) kanksati (desires) samah (equally disposed) sarvesu (all) bhutesu (living entities) mat-bhaktim (my devotional service) labhate (gains) param (transcendental).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (677, 18.055, 'Moksa-Sanyasa-Yoga', 677, 'bhaktya mam abhijanati ', 'yavan yas casmi tattvatah', 'tato mam tattvato ', 'jnatva visate tadanantaram ', '', '', 'bhaktya (by pure devotional service) mam (Me) abhijanati (one can know) yavan (as much as) yah ch asmi (as I am) tattvatah (in truth) tatah (thereafter) mam (Me) tattvatah (by truth) jnatva (knowing) visate (enters) tat-anantaram (thereafter).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (678, 18.056, 'Moksa-Sanyasa-Yoga', 678, 'sarvakarmany api sada ', 'kurvano madvyapasrayah', 'matprasadad avapnoti ', 'sasvatam padam avyayam', '', '', 'sarva (all) karmani (activities) api (although) sada (always) kurvanah (performing) mat (under my) vyapasrayah (protection) mat (my) prasadat (mercy) avapnoti (achieves) sasvatam (eternal) padam (abode) avyayam (imperishable).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (679, 18.057, 'Moksa-Sanyasa-Yoga', 679, 'cetasa sarvakarmani ', 'mayi samnyasya matparah', 'buddhiyogam upasritya ', 'maccittah satatam bhava', '', '', 'cetasa (by intelligence) sarva-karmani (all kinds of activities) mayi (unto Me) sannyasya (giving up) mat-parah (my protection) buddhi-yogam (devotional activities) upasritya (taking shelter of) mat-cittah (consciousness) satatam (twenty-four hours a day) bhava (just become).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (680, 18.058, 'Moksa-Sanyasa-Yoga', 680, 'maccittah sarvadurgani ', 'matprasadat tarisyasi', 'atha cet tvam ahamkaran ', 'na srosyasi vinanksyasi', '', '', 'mat (my) cittah (consciousness) sarva (all) durgani (impediments) mat (my) prasadat (my mercy) tarisyasi (you will overcome) atha (therefore) cet (if) tvam (you) ahankarat (by false ego) na (not) srosyasi (do not hear) vinanksyasi (then lose yourself).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (681, 18.059, 'Moksa-Sanyasa-Yoga', 681, 'yad ahamkaram asritya na ', 'yotsya iti manyase', 'mithyaisa vyavasayas te ', 'prakrtis tvam niyoksyati', '', '', 'yat (therefore) ahankaram (false ego) asritya (taking shelter) na (not) yotsye (shall fight) iti (thus) manyase (think) mithya esah (this is all false) vyavasayah te (your determination) prakrtih (material nature) tvam (you) niyoksyati (will engage you).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (682, 18.060, 'Moksa-Sanyasa-Yoga', 682, 'svabhavajena kaunteya ', 'nibaddhah svena karmana', 'kartum necchasi yan mohat ', 'karisyasy avasopi tat', '', '', 'svabhava-jena (by ones own nature) kaunteya (O son of Kunti) nibaddhah (conditioned) svena (by ones own) karmana (activities) kartum (to do) na (not) icchasi (like) yat (that) mohat (by illusion) karisyasi (you will act) avasah (imperceptibly) api (even) tat (that).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (683, 18.061, 'Moksa-Sanyasa-Yoga', 683, 'isvarah sarvabhutanam ', 'hrddeserjuna tisthati', 'bhramayan sarvabhutani ', 'yantrarudhani mayaya ', '', '', 'isvarah (the Supreme Lord) sarva-bhutanam (of all living entities) hrt-dese (in the location of the heart) arjuna (O Arjuna) tisthati (resides) bhramayan (causing to travel) sarva-bhutani (all living entities) yantra (machine) arudhani (being so placed) mayaya (under the spell of material energy).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (684, 18.062, 'Moksa-Sanyasa-Yoga', 684, 'tam eva saranam gaccha ', 'sarvabhavena bharata', 'tatprasadat param santim ', 'sthanam prapsyasisasvatam ', '', '', 'tam (unto Him) eva (certainly) saranam (surrender) gaccha (go) sarva-bhavena (in all respects) bharata (O son of Bharata) tat-prasadat (by His grace) param (transcendental) santim (in peace) sthanam (abode) prapsyasi (you will get) sasvatam (eternal).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (685, 18.063, 'Moksa-Sanyasa-Yoga', 685, 'iti te jnanam akhyatam ', 'guhyad guhyataram maya', 'vimrsyaitad asesena ', 'yathecchasi tatha kuru ', '', '', 'iti (thus) te (unto you) jnanam (knowledge) akhyatam (described) guhyat (confidential) guhya-taram (still more confidential) maya (by Me) vimrsya (by deliberation) etat (that) asesena (fully) yatha (as you) icchasi (you like) tatha (that) kuru (perform).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (686, 18.064, 'Moksa-Sanyasa-Yoga', 686, 'sarvaguhyatamam bhuyah srnu me ', 'paramam vacah', 'istosi me drdham iti tato ', 'vaksyami te hitam ', '', '', 'sarva-guhya-tamam (the most confidential of all) bhuyah (again) srnu (just hear) me (from Me) paramam (the supreme) vacah (instruction) istah asi (you are very dear to Me) me (of Me) drdham (very) iti (thus) tatah (therefore) vaksyami (I am speaking) te (for your) hitam (benefit).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (687, 18.065, 'Moksa-Sanyasa-Yoga', 687, 'manmana bhava madbhakto', 'madyaji mam namaskuru', 'mam evaisyasi satyam te ', 'pratijane priyosi me ', NULL, NULL, 'mat-manah (thinking of Me) bhava (just become) mat-bhaktah (my devotee) mat-yaji (my worshiper) mam (unto Me) namaskuru (offer your obeisances) mam (unto Me) eva (certainly) esyasi (come) satyam (truly) te (to you) pratijane (I promise) priyah (dear) asi (you are) me (mine).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (688, 18.066, 'Moksa-Sanyasa-Yoga', 688, 'sarvadharman parityajya mam ekam ', 'saranam vraja', 'aham tva sarvapapebhyo ', 'moksyayisyami ma sucah', '', '', 'sarva-dharman (all varieties of religion) parityajya (abandoning) mam (unto Me) ekam (only) saranam (surrender) vraja (go) aham (I) tvam (you) sarva (all) papebhyah (from sinful reactions) moksayisyami (deliver) ma (not) sucah (worry).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (689, 18.067, 'Moksa-Sanyasa-Yoga', 689, 'idam te natapaskaya ', 'nabhaktaya kadacana', 'na casusrusave vacyam na ch ', 'mam yobhyasuyati ', '', '', 'idam (this) te (you) na (never) atapaskaya (one who is not austere) na (never) abhaktaya (one who is not a devotee) kadacana (at any time) na (never) ch (also) asusrusave (one who is not engaged in devotional service) vacyam (to be spoken) na (never) ch (also) mam (unto Me) yah (anyone) abhyasuyati (envious).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (690, 18.068, 'Moksa-Sanyasa-Yoga', 690, 'ya idam paramam guhyam ', 'madbhaktesv abhidhasyati', 'bhaktim mayi param krtva ', 'mam evaisyaty asamsayah', '', '', 'yah (anyone) idam (this) paramam (most) guhyam (confidential) mat (mine) bhaktesu (amongst devotees of) abhidhasyati (explains) bhaktim (devotional service) mayi (unto Me) param (transcendental) krtva (having done) mam (unto Me) eva (certainly) esyati (comes) asamsayah (without doubt).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (691, 18.069, 'Moksa-Sanyasa-Yoga', 691, 'na ch tasman manusyesu ', 'kascin me priyakrttamah', 'bhavita na ch me tasmad ', 'anyah priyataro bhuvi ', '', '', 'na (never) ch (and) tasmat (therefore) manusyesu (among mankind) kascit (anyone) me (my) priya-krt-tamah (more dear) bhavita (will become) na (nor) ch (and) me (my) tasmat (than him) anyah (other) priya-tarah (dearer) bhuvi (in this world).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (692, 18.070, 'Moksa-Sanyasa-Yoga', 692, 'adhyesyate ch ya imam ', 'dharmyam samvadam avayoh', 'jnanayajnena tenaham istah ', 'syam iti me matih ', '', '', 'adhyesyate (will study) ch (also) yah (he) imam (this) dharmyam (sacred) samvadam (conversation) avayoh (of ours) jnana (knowledge) yajnena (by sacrifice) tena (by him) aham (I) istah (worshiped) syam (shall be) iti (thus) me (my) matih (opinion).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (693, 18.071, 'Moksa-Sanyasa-Yoga', 693, 'sraddhavan anasuyas ch ', 'srnuyad api yo narah', 'sopi muktah subhaml ', 'lokan prapnuyat punyakarmanam ', '', '', 'sraddha-van (faithful) anasuyah ch (and not envious) srnuyat (does hear) api (certainly) yah (who) narah (a man) sah (he) api (also) muktah (being liberated) subhan (auspicious) lokan (planets) prapnuyat (attains) punya-karmanam (of the pious).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (694, 18.072, 'Moksa-Sanyasa-Yoga', 694, 'kaccid etac chrutam partha ', 'tvayaikagrena cetasa', 'kaccid ajnanasammohah ', 'pranastas te dhanamjaya', '', '', 'kaccit (whether) etat (this) srutam (heard) partha (O son of Prtha) tvaya (by you) eka-agrena (with full attention) cetasa (by the mind) kaccit (whether) ajnana (ignorant) sammohah (illusion) pranastah (dispelled) te (of you) dhananjaya (O conqueror of wealth (Arjuna)).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (695, 18.073, 'Moksa-Sanyasa-Yoga', 695, 'arjuna uvach; अर्जुन उवाच', 'nasto mohah; नष्टॊ मॊहः ', 'smrtir labdha-tvatprasadan mayacyuta; समृतिर्लब्धा त्वत्प्रसादान्मयाच्युत', 'sthitosmi gatasamdehah; स्थितॊ ऽसमि गतसंदेहः ', 'karisye vacanam tava; करिष्ये वचनं तव ', '', 'arjunah uvach (Arjuna says) nastah (dispelled) mohah (illusion) smrtih (memory) labdha (regained) tvat-prasadat (by your mercy) maya (by me) acyuta (O infallible Krishna) sthitah (situated) asmi (I am) gata (removed) sandehah (all doubts) karisye (I shall execute) vacanam (order) tava (your).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (696, 18.074, 'Moksa-Sanyasa-Yoga', 696, 'sanjaya uvach; संजय उवाच', 'ity aham vasudevasya; इत्यहं वासुदेवस्य ', 'parthasya ch mahatmanah; पार्थस्य च महात्मनः', 'samvadam imam asrausam adbhutam; संवादमिममश्रौषमद्धुतम् ', ' romaharsanam; रॊमहर्षणम', '', 'sanjaya uvach (says) iti (thus) aham (I) vasudevasya (of Krishna) parthasya (of Arjuna) ch (also) maha-atmanah (two great souls) samvadam (discussing) imam (this) asrausam (heard) adbhutam (wonder) roma-harsanam (hair standing on end).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (697, 18.075, 'Moksa-Sanyasa-Yoga', 697, 'vyasaprasada-cchrutavan-etad-guhyam-aham; वयासप्रसादाच्छ्रुतवानेतदुह्यमहं ', 'param; परम', 'yogam yoge; यॊगं यॊगे ', 'svarat-krishnat-saksat-kathayatah svayam; श्वरात्कृष्णात्साक्षात्कदयतः सवयम', '', '', 'vyasa-prasadat (offerings of Vyasa) srutavan (heard) etat (this) guhyam (confidential) aham (I) param (the supreme) yogam (mysticism) yoga-isvarat (from the master of all mysticism) Krishnat (from Krishna) saksat (directly) kathayatah (speaking) svayam (personally).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (698, 18.076, 'Moksa-Sanyasa-Yoga', 698, 'rajan-samsmrtya samsmrtya; राजन्संस्मृत्य संस्मृत्य ', 'samvadam-imam-adbhutam; संवादमिममद्धुतम्', 'kesavarjunayoh punyam; केशवार्जुनयॊः पुण्यं ', 'hrsyami ch muhur muhuh; हृष्यामि च मुहुर मुहुः', '', '', 'rajan (O King) samsmrtya (remembering) samsmrtya (remembering) samvadam (message) imam (this) adbhutam (wonderful) kesava (Lord Krishna) arjunayoh (and Arjuna) punyam (pious) hrsyami (taking pleasure) ch (also) muhuh muhuh (always, repeatedly).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (699, 18.077, 'Moksa-Sanyasa-Yoga', 699, 'tacch samsmrtya samsmrtya; तच्च संस्मृत्य संस्मृत्य ', 'rupam-atyadbhutam hareh; रूपमत्यद्भुतं हरेः', 'vismayo me; विस्मयॊ मे ', ' mahan-arjan-harsyami ch punah punah; महान्राजन्ह्रष्यामि च पुनः पुनः', '', '', 'tat (that) ch (also) samsmrtya (remembering) samsmrtya (remembering) rupam (form) ati (great) adbhutam (wonderful) hareh (of Lord Krishna) vismayah (wonder) me (my) mahan (great) arjan (O Aryan King) harsyami (enjoying) ch (also) punah punah (will repeat).', NULL, NULL, 'admin');
INSERT INTO gita VALUES (700, 18.078, 'Moksa-Sanyasa-Yoga', 700, 'yatra yogesvarah krsno; यत्र यॊगेश्वरः कृष्णॊ ', 'yatra partho dhanurdharah; यत्र पार्थो धनुर्धरः', 'tatra srir vijayo; तत्र श्रीव्रिज्यो ', 'bhutir-dhruva nitir matir mama; भुतिर्ध्रुवा नीतिर्मतिर्मम ', '', '', 'yatra (where) yoga-isvarah (the master of mysticism) Krishna yatra (where) Partha dhanuh-dharah (the carrier of the bow and arrow) tatra (there) srih (opulence) vijayah (victory) bhutih (exceptional power) dhruva (certain) nitih (morality) matih mama (is my opinion).', NULL, NULL, 'admin');
