PRAGMA encoding="UTF-8";
DROP TABLE IF EXISTS gita;
CREATE TABLE IF NOT EXISTS gita (
  id INTEGER PRIMARY KEY,
  origchapternum decimal(7,3) default NULL,
  origchapter varchar(120) default NULL,
  origid INTEGER default NULL,
  sanskrit1 varchar(254) NULL,
  sanskrit2 varchar(254) NULL,
  sanskrit3 varchar(254) NULL,
  sanskrit4 varchar(254) NULL,
  sanskrit5 varchar(254) NULL,
  sanskrit6 varchar(254) NULL,
  sanskrit_main text,
  trasliter_my text,
  purpose_my text,
  user varchar(6) NOT NULL
);

