-- 
-- Table structure for table upanishad
-- 

DROP TABLE IF EXISTS upanishad;
CREATE TABLE IF NOT EXISTS upanishad (
  id INTEGER PRIMARY KEY,
  upanishad varchar(100) NOT NULL,
  upanishad_h varchar(127) default NULL,
  aranyaka varchar(4) default NULL,
  adhyaya varchar(4) default NULL,
  khanda varchar(4) default NULL,
  origid float(5,3) NOT NULL,
  oracle text NOT NULL,
  oracle_h text
);

